css:
@-moz-document url('http://forum.football365.com') {
	
	.fixedfluidfixed-right, #site-description { display: none}
	#page-body .fixedfluidfixed-content { width: 100%}
	#wrap { 
	background: #000;
	border-color: #000;
	}
	.panel { background-color: #232323}
	.bg1, .bg2,
	.online, .navbar { 
	background-color: #171717;
	background: #171717;
	}
	.content, label { color: #b1a8a8}
	a, .content h2, 
	.panel h2 { color: #bd8417}
	.postlink {
		border-bottom-color: #bd8417;
		color: #bd8417;
	}
	.postprofile strong { color: #aeb132}
	.postbody { color: #39ce6f}
	blockquote,
	blockquote blockquote blockquote,
	blockquote blockquote blockquote blockquote blockquote {
		background-color: #31312f;
		border-color: #151515;
	}
	blockquote blockquote,
	blockquote blockquote blockquote blockquote,
	blockquote blockquote blockquote blockquote blockquote blockquote
	{ background-color: #1d1d1d}
	fieldset.polls dl {
		border-top-color: #020202;
		color: #cecece;
	}

	.forumbg {
		background-color: #4c4c4c;
		background-image: none;
	}
	li.row {
		border-top-color: #90461c;
		border-bottom-color: #90461c;
	}
	li.row:hover { background-color: #ffffff}
}
