          ▐█▌▐▌▐█ ▪▐▌▐█▄▪▐█ ▐█▌·   ██▌.██ ██▌▐█▌
           .▀▀▀  ▀  ▀  ▀▀▀▀  ▀▀▀  ▀ ▀▀▀ ▀▀  █▪▀▀▀
                 by vortex !NOiDx.cCl2

>> scroll to the bottom for sidebar/footer hiding options
>> RSBK by Rasbok was used to find some class names
>> modify to your needs by modifying the below variables
*/

@-moz-document
domain("last.fm")
{

/* VARIABLES */
:root {
  --header-bg:#060512;
  --body-bg:#03020a;
  --color-main:#c0dbb6;
  --color-1:#24949b;
  --color-2:#256f8a;
  --color-3:#e94d5a;
  --color-4:#55b9b0;
  --color-5:#236f8b;
  --color-6:#52baae;
  --color-low:#627694;
  --color-medium:#4b658c;
  --color-high:#1a7fc8;
  --color-veryhigh:#269387;
  --color-super:#2aaeac;
  --border-on:1px solid;
  --border-off:none;
  --font-family:'MonteCarlo';
  --font-size:15px;
  }
 
/* FIX FONT SIZES */
.grid-items-item-main-text, 
.grid-items-item-details, 
.header .header-metadata-title, 
.header--overview.header--user .header-title-secondary, 
.kerve .nowslider_artist, 
.kerve .nowslider_track, 
.kerve .nowslider_scrobbles, 
.chartlist .chartlist-countbar, 
.chartlist .chartlist-timestamp, 
.shout-timestamp, 
.shout-user, 
.shout-action,
.header .header-metadata-display,
.header-title,
.featured-item-heading,
.featured-item-name,
.featured-item-artist,
.header .header-metadata-listeners-you-know-list,
.label,
.recs-feed,
.content-form .form-row-help-text,
.h4, h4 {
  font-size:var(--font-size) !important;
  font-weight:normal !important;
}
  
/* FIX INPUT FORMS */
.content-form .form-control, 
.content-form input[type="date"], 
.content-form input[type="email"], 
.content-form input[type="number"], 
.content-form input[type="password"], 
.content-form input[type="text"], 
.content-form input[type="url"], 
.content-form select, 
.content-form textarea {
  background:var(--header-bg) !important;
  color:var(--color-main) !important;
  font-size:var(--font-size) !important;
  border:var(--border-on) var(--color-4) !important;
  border-radius:0px !important;
}

/* REMOVE BOX SHADOW/BORDERS */
.article,
.attendance-btn,
.comments .shout-btn,
.content-top-lower:after,
.content-top:after,
.header-metadata-user-stats,
.overlay-control-bar,
.overlay-control-bar::after,
div.overlay-content,
.suggest-correction-success-message,
.gallery-image-vote-down-off,
.gallery-image-vote-up-off,
.gallery-upload-rules,
.masthead-wrapper .masthead,
.search-field,
.alert,
.alert-info,
.alert-warning,
.dropdown-menu-clickable,
.modal-content,
.alert,
.alert-info,
.alert-warning,
.gallery-upload-rules,
.tag-cta,
.tag-cta:hover,
.upload-artwork-cta,
.upload-artwork-cta:hover,
.upload-image-cta,
.upload-image-cta:hover,
.upload-video-cta,
.upload-video-cta:hover,
.wiki-cta,
.wiki-cta:hover,
.scrobble-cta,
#manual-updates,
.section-settings,
.selectbox .selectboxit-options,
.share-chart .share-block,
.user-dashboard-module,
.user-dashboard-comparison,
.more-disclose-mobile .more-block{
  border:var(--border-off) !important;
}
  
.stationlink-list li, 
.chartlist tr, 
.btn.btn--header,
.chartlist td,
.events-filters,
.events-list td,
.globalchart .globalchart-item,
.inbox td,
.recs-feed .recs-feed-item,
.site-links,
.context,
.date-range-picker-content,
.dropdown-menu.dropdown--open,
.dropdown-buylinks--light .dropdown-featured-buylink,
.dropdown-buylinks--light .dropdown-more-buylinks,
.lfmRed .site-search .search-box,
.masthead-wrapper .masthead,
.secondary-nav--scrollable.secondary-nav--more ul.secondary-nav-hidden-list,
.secondary-nav .navlist-hidden-list,
.modal-content,
.user-dashboard-module-share,
.header-popularity-rank-tooltip {
  box-shadow:var(--border-off) !important;
  webkit-box-shadow:var(--border-off) !important;
}
  
.pagination-page[aria-current] {
  font-weight: 700;
  -webkit-box-shadow: inset 0 -3px var(--color-2);
  box-shadow: inset 0 -3px var(--color-2);   
}

/* HEADER */
.header .header-metadata-display {
  color:var(--color-3) !important;
}
  
.top-bar, 
.player-bar,
.header--overview,
.content-top {
  background:var(--header-bg) !important;
}
  
.header--overview .header-info-secondary {
  color:var(--color-main) !important;  
}
  
.masthead-logo {
  background:url(https://i.imgur.com/tC4GAyk.png) !important;
  height:28px !important;
}
  
.tasteometer-compat-low .tasteometer-compat-colour {
  color: var(--color-low) !important;
  fill: var(--color-low) !important;
  font-weight: 700;
}
  
.tasteometer-compat-medium .tasteometer-compat-colour {
  color: var(--color-medium) !important;
  fill: var(--color-medium) !important;
  font-weight: 700;
}

.tasteometer-compat-high .tasteometer-compat-colour {
  color: var(--color-high) !important;
  fill: var(--color-high) !important;
  font-weight: 700;
}

.tasteometer-compat-very_high .tasteometer-compat-colour {
  color: var(--color-veryhigh) !important;
  fill: var(--color-veryhigh) !important;
  font-weight: 700;
}

.tasteometer-compat-super .tasteometer-compat-colour {
  color: var(--color-super) !important;
  fill: var(--color-super) !important;
  font-weight: 700;
}
/* BODY */
body {
  color:var(--color-main) !important;
  font-family: MonteCarlo !important;
  font-size:var(--font-size) !important;
  font-weight:normal !important;
}
  
.container.page-content,
.content-top,
.content-top-lower,
.date-range-picker-content,
.date-picker-content,
.dropdown-menu.dropdown--open ,
.dropdown-menu.dropdown--open,
.events-filter-location-dropdown,
.footer-wrapper,
.gallery-image-container,
.header .header-info-secondary:before,
.img-thumbnail,
.large-header-layout .header.header--overview,
.library-controls-datepicker,
.overlay-content,
.player-bar,
.secondary-nav--scrollable.secondary-nav--more ul.secondary-nav-hidden-list,
.selectboxit-option-anchor,
.top-bar,
.widget_listeningnow,
.widget_trends,
body,
.main-content,
.header--overview,
textarea.comment-input,
tr.js-link-block,
.recs-feed .recs-feed-item,
.date-range-picker-content,
.lfmRed .site-search .search-box,
.fiflufi .leftCol,
.stationbuttonLarge,
.stationbuttonLarge span.stationButtonWrapper,
#page .sic #content,
.fiflufi #content .fullWidth,
.fiflufi #content,
article.content,
.fiflufi #content .clearit,
.fiflufi .LeftCol,
span.quote,
.site-footer,
ul.forumComments li.odd,
table.forumtable tr.alt td,
.actions-bar,
#forumsearch,
body:not(.dashbody) .kerve.widget,
.header--sub-page .container, 
.header--overview .container,
table.table tr:nth-child(2n) td {
  background:var(--body-bg) !important;
}
  
.chartlist tr.chartlist-row--even,
.recs-feed .context {
  background:var(--header-bg) !important;
}
  
.shout {
  border-bottom:var(--border-off) !important;
}

.shout--deleted, 
.shout--reported, 
.wiki-cta, 
.kerve.widget, 
.upload-video-cta,
.tag-cta {
  background:var(--header-bg) !important;
  border:1px solid var(--color-1) !important;
  color:var(--color-1) !important;
}
  
.chartlist .chartlist-artists a {
  color:var(--color-5) !important;
}

.chartlist-name a {
  color:var(--color-6) !important;
}

.chartlist .chartlist-album a, 
.shout-user a,
.content-top .secondary-nav-item-link--active, 
.content-top .secondary-nav-item-link--active:focus, 
.content-top .secondary-nav-item-link--active:hover,
.js .date-range-picker-button,
.date-range-picker-button {
  color:var(--color-2) !important;
}
  
.chartlist .chartlist-row--highlight, 
.chartlist .now-scrobbling {
  background:var(--header-bg) !important;
  border-left:2px dotted var(--color-3) !important;
}
  
.label, 
.recommended-next-button {
   background:var(--color-5) !important;
}
  
.chartlist .countbar-bar-slug {
   border:solid 1px var(--color-medium) !important;
   background:transparent !important;
}
 
/* MISC */
.love-button{
  -webkit-filter:saturate(100%) brightness(60%)!important;
  filter:saturate(100%) brightness(60%)!important;
}
.love-button.love-button--loved{
  -webkit-filter:saturate(30%) brightness(300%) hue-rotate(230deg) !important;
  filter:saturate(30%) brightness(300%) hue-rotate(230deg) !important;
}
.love-button:hover{
  -webkit-filter:saturate(0%) brightness(600%)!important;
  filter:saturate(0%) brightness(600%)!important;
}

.btn-primary {
  background:var(--color-1) !important;
}
  
.correction-vote-yes-off {
  background:var(--color-2) !important;
  font-size:var(--font-size) !important;
}
  
.correction-vote-no-off {
  background:var(--color-3) !important;
  font-size:var(--font-size) !important;
}
  
.add-event-btn,
.btn-add-sm,
.btn-primary,
.content-top .secondary-nav .secondary-nav-item-link--active::after,
.content-top .secondary-nav a.secondary-nav-item-link:focus::after,
.content-top .secondary-nav a.secondary-nav-item-link:hover::after,
.header .secondary-nav-item-link--active::after,
.header a.secondary-nav-item-link:focus::after,
.header a.secondary-nav-item-link:hover::after,
.homefeed .secondary-nav-item-link--active::after,
.homefeed a.secondary-nav-item-link:focus::after,
.homefeed a.secondary-nav-item-link:hover::after,
.whats-new .secondary-nav .secondary-nav-item-link--active::after,
.whats-new .secondary-nav a.secondary-nav-item-link:focus::after,
.whats-new .secondary-nav a.secondary-nav-item-link:hover::after,
.whats-new .secondary-nav-item-link--active::after,
.whats-new a.secondary-nav-item-link:focus::after,
.whats-new a.secondary-nav-item-link:hover::after,
.btn-add,
.correction-vote-yes-on,
.inbox .inbox-buttons .inbox-compose-button,
.clipboard-button{
  background:var(--color-5) !important;
}
  
/* HIDE ELEMENTS */
.mpu-subscription-upsell--mpu{
  display:none !important;
}
  
.chartlist tr.chartlist-row--even .chartlist-more-button {
  background:transparent !important;
}
  
/* COMMENT OUT BELOW PROPERTIES TO BRING BACK SIDEBAR */  
.col-sidebar {
  display:none !important;
}

.container, 
.header .header-background,
.col-main {
  width:90% !important;
}
  
/* COMMENT OUT BELOW TO BRING BACK FOOTER */
.features-footer,
.footer,
.lazy-features-footer {
  display:none !important;
}
}
