css:
@-moz-document domain("www.chessable.com") {
body, .body {
    background-color: #414141 !important;
    background-image: none !important;
    color: #efefef !important;
}

a {
    color: #00BFFF !important;
}

header {
    position: relative;
    margin: 0 auto;
}

header {
    background-color: inherit !important;
    box-shadow: none;
}

.nav-i__button, .nav-i.open .nav-i__button, .nav-i__button--active, .nav-i__button:focus, .nav-i__button:hover, .header__redbutton, .header__text, .header__text:not(.header__text--no-link):hover {
    color: #efefef !important;
}

.header__redbutton {
    background-color: inherit !important;
}

.dropdown-menu {
    background-color: #5b5b5b !important;
}

.dropdown-menu>li>a {
    color: #efefef !important;
}

.book-menu__icon {
    color: #efefef !important;
}

.dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover {
    background-color: #6f6f6f !important;
}

#streak_current, #ruby_current {
    background-color: inherit !important;
}

.bubbleUser, .notificationsBox {
    background-color: #5b5b5b !important;
    color: inherit !important;
}

.bubbleUser:after, .notificationsBox:after {
    border-color: #5b5b5b transparent !important;
}

.classCurrent.badgeCherryText {
    color: #e61a38 !important;
}

.classCurrent.badgeVeniceBlueText {
    color: #74c4f0 !important;
}

.notificationsBox a, .bubbleUser a {
    color: #00BFFF !important;
}

.bubbleUser a:hover {
    background-color: #6f6f6f !important;
}

div.top-footer {
    background-color: #1a1a1a;
}

div.myCanvas {
	background-image: none;
    background-color: inherit !important;
    margin-top: 0;
}

.swal2-modal, .floating-dialog {
    background-color: #5b5b5b !important;
    color: #efefef !important;
    box-shadow: 0 0 6px rgba(171,171,171,.8), inset 0 0 0 1px rgba(0,0,0,.0470588);
}

.swal2-title, .swal2-modal .swal2-content {
    color: #efefef !important;
}

.swal2-modal button.swal2-confirm--box-shadow-green {
    background-color: #476694 !important;
    color: #fff !important;
    border-color: #476694 !important;
    text-shadow: none;
    box-shadow: 0 0 2px rgba(134,140,148,.8), inset 0 0 0 1px rgba(0,0,0,.0470588);
}

.swal2-icon.swal2-success:after, .swal2-icon.swal2-success:before {
    background: #5b5b5b !important;
}

.swal2-icon.swal2-success .fix {
    background-color: #5b5b5b !important;
}

.floating-dialog__h2--green {
    color: inherit !important;
}

.floating-dialog {
    transition-duration: 0s;
}

.book-cover__header-main {
    color: #efefef !important;
}

.variation-header__title {
    color: #efefef !important;
}

.homeCol2-container {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

/* home tab */
.breadcrumbsBox {
    box-shadow: 0 0 0 1px #8a8a8a, 0 2px 2px #9a9a9a;
}

.breadcrumbs>.item, .breadcrumbs>.item:after, .breadcrumbs>.item:hover, .breadcrumbs>.item:hover+.item:after {
    background-color: #383838 !important;
}

.breadcrumbs>.item .points {
    color: #efefef !important;
}

.breadcrumbs>.item.active {
    background-color: #07b8ff !important;
}

.breadcrumbs>.item.active:hover, .breadcrumbs>.item.active:hover+.item:after {
    background: #07b8ff !important;
}

.breadcrumbs>.item.active+.item:after, .breadcrumbs>.item.active:after {
    border: none;
}

.off:hover {
    background-color: #e0e0e0 !important;
}

.myButton, .page-controls__btn.engineToggle, .reading-button, a.myButton.off.floating-dialog__close-trigger-2 { /* cf .learn */
    background-color: #476694 !important;
    color: #fff !important;
    border-color: #476694 !important;
    text-shadow: none !important;
}

.myButton:hover, .page-controls__btn.engineToggle:hover, .reading-button:hover, .myButton .off .floating-dialog__close-trigger-2:hover, a.myButton.off.floating-dialog__close-trigger-2:hover {
    background-color: #5c7dad !important;
}

.myButtonOff, .off {
    background-color: #5b5b5b !important;
    color: inherit !important;
    border-color: #000000;
}

.off {
    font-weight: 400;
    text-shadow: none;
    box-shadow: none;
}

.myButtonOff:hover, .off:hover {
    background-color: #6f6f6f !important;
}

.myButtonGlow {
    box-shadow: none;
}

.isa_info {
    background-color: inherit !important;
    color: inherit !important;
}

.isa_info a {
    color: inherit !important;
}

#orderHomeBooksSelect select {
    background: inherit !important;
    color: inherit !important;
}

#orderHomeBooksSelect select:focus {
    outline: none;
}

#orderHomeBooksSelect option {
    background: gray !important;
    color: white !important;
}

.bookHome {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

.bookMenu {
    color: inherit !important;
}

.bookActions {
    border-top: 1px solid #6a6a6a;
}

.meter {
    background: #7d7d7d !important;
}

.mini-profile__name {
    color: inherit ! important;
    font-weight: bold;
}

.mini-profile__rank {
    color: #bcbcbc !important;
}

.mini-profile__stats {
    background-color: inherit !important;
}

.mini-profile__value {
    color: inherit !important;
}

.mini-profile__cap {
    -webkit-text-stroke-width: 1px;
    -webkit-text-stroke-color: #efefef;
}

.mini-profile__vars {
    border: none;
}

.mini-profile__link {
    color: #00BFFF !important;
}

.mini-profile__link:active {
    background-color: inherit !important;
    box-shadow: none;
}

.mini-profile__label {
    color: #bcbcbc !important;
}

#miniProfileDiv + div { /* streak is being dispayed in topnav, this just clutters the ui */
    display:none !important;
}

#miniProfileDiv + div + div { /* i don't know what this is good for, also clutters the ui */
    display:none !important;
}

.activity {
    color: inherit ! important;
    font-weight: bold;
}

.timePlanner {
    color: inherit ! important;
    font-weight: bold;
}

a[href$="pro/?h=planner-box"] {
    display:none !important;
}

.smallColStandardContainer>.ableTip {
    display:none !important;
}

.leaderBoardTitle {
    color: inherit ! important;
    font-weight: bold;
}

.leaderHighlight {
    background-color: inherit !important;
    font-weight: bold;
}

.leaderBoardRow {
    background-color: inherit !important;
    border-bottom: 1px solid #6a6a6a;
}

.leaderBoardRow>a {
	color: inherit !important;
}

#homeMoreButtons>a[href$="/repertoires/"] {
    display:none !important;
}

/* board */
.white-1e1d7 {
    background-color: #DDDDDD !important;
    color: #000000 !important;
}

.black-3c85d {
    background-color: #447099 !important;
    color: #000000 !important;
}

.board-info, .board-info__timer-container, .board-info--curved {
    background-color: #383838 !important;
    box-shadow: none;
}

#board-info {
    border: 1px solid #6a6a6a !important;
    box-shadow: 0 1px 3px #6a6a6a !important;
}

#board-info__skip-move {
    top: 23px;
    left: 25px;
}

.board-info__title--error {
    color: inherit !important;
}

/* info boxes next to board */
.board-card {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a !important;
    box-shadow: 0 1px 3px #6a6a6a !important;
}

.board-info__skip-move {
    color: inherit !important;
}

.practice-header__title>a {
    color: #6297fe !important;
}

.paper-checkbox {
    background-color: inherit !important;
    color: inherit !important;
    border-color: inherit !important;
}

.muteButton {
    color: inherit !important;
}

/* book view */

.bookMainDesc {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

.tabbed-nav__tab {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

.tabbed-nav__tab--blue, .tabbed-nav__tab--red {
    color: inherit !important;
}

.tabbed-nav__tab--blue.selected, .tabbed-nav__tab--red.selected {
    box-shadow: inset 0 3px 0 0 #85abe5;
}

.tabbed-nav__tab.selected:before {
    background-color: inherit !important;
    height: 0px;
}

#addnewcomment, .new_comment input[type=text] {
    background-color: #cdcdcd !important;
    color: #414141 !important;
}

.whiteBodyContainer {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

.book-ruby__rubies {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

.details-container {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

.related-item--white-container {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

.related-item {
    color: inherit !important;
}

.badgeGrayoutText, .badgeGrayoutTextContrast {
    color:  #414141 !important;
}

.banner-bottom {
    background-color: #383838 !important;
    color: inherit !important;
}

/* browse books view */
.bookstore-sidebar__main {
    background-color: #383838 !important;
    color: inherit !important;
}

#bookstore-sidebar__inner {
    border: 1px solid #6a6a6a !important;
    box-shadow: 0 1px 3px #6a6a6a !important;
}

.bookstore-sidebar__option-name {
    color: inherit !important;
}

.bookstore-sidebar__option>a {
    color: #00BFFF !important;
}

.bookstore-sidebar__option:hover>a {
    color: #00BFFF !important;
    text-decoration: underline;
}

.bookstore-sidebar__btn {
    background-color: #476694 !important;
    border-color: #476694 !important;
    text-shadow: none;
    color: #fff !important;
}

.bookstore-sidebar__btn:hover {
	background-color: #5c7dad !important;
}

.bookstore-header__title {
    color: inherit !important;
}

input[type=text].search-field__input, input[type=text] {
    background-color: inherit !important;
    color: inherit !important;
    border: 1px solid #6a6a6a;
}

input[type=text].search-field__input:focus {
    border: 1px solid #00BFFF;
}

.search-field__icon {
    color: inherit !important;
}

.book-card__inner {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a !important;
    box-shadow: 0 1px 3px #6a6a6a !important;
}

.book-card__title>a {
    color: #00BFFF !important;
}

.book-card__title>a:hover {
    text-decoration: underline;
}

.book-card__price {
    color: inherit !important;
}

.book-card__btn {
    background-color: #476694 !important;
    color: #fff !important;
    border-color: #476694 !important;
    text-shadow: none;
}

/* book chapters view */
.openingsHeader>h1 {
    color: #fff !important;
}

.levelBox__inner {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

.levelBox__num {
    color: #efefef !important;
}

.levelBox__moves-ready {
    color: #efefef !important;
}

.levelBox__title {
    background-color: #5b5b5b !important;
    color: #fff !important;
}

.smallColTitle {
    color: inherit !important;
    font-weight: bold;
    border-bottom: 1px solid #6a6a6a;
}

#user_default_study, #user_default_review, #user_default_reps {
    background-color: inherit !important;
    color: inherit !important;
    border: 1px solid #ffffff;
}

#user_default_study option, #user_default_review option, #user_default_reps option {
    background: gray !important;
    color: white !important;
}

.toolbar-buttons__btn.selected, .toolbar-buttons__btn.selected:hover {
    background: linear-gradient(#6f6f6f,#5b5b5b);
}

.toolbar-buttons__btn {
    background: linear-gradient(#5b5b5b,#383838);
}

.toolbar-buttons__btn:hover {
    background-color: #6f6f6f !important;
}

.toolbar-buttons {
    border-bottom: 1px solid #6a6a6a;;
}

/* book single chapter view */
.variation-card {
    background-color: #383838 !important;
    min-height: 0px;
}

.variation-card__top {
    background-color: #383838 !important;
}

.variation-card__moves {
    border-top: none;
}

.variation-card__controls {
    border-bottom: none;
}

.opening, .openingTitle {
    background-color: inherit !important;
    color: inherit !important;
}

.opening .openingTitle .title {
    font-weight: normal !important;
}

.i2ybutton {
    background-color: #5b5b5b !important;
    color: inherit !important;
    text-shadow: none;
}

.i2ybutton:hover {
    background-color: #6f6f6f !important;
}

.i2ybutton.disabled, .i2ybutton.disabled:hover {
    background-color: #484848 !important;
    color: inherit !important;
}

/* chapter line view */
#theOpeningMoves, #stockFishInfo, .colTitle, .colContent {
    background-color: #383838 !important;
    color: inherit !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

.colTitle {
    border: 1px solid gray;
}

.board-controls__btn:not(.myButtonOff)+.myButtonOff {
    border-left-color: #000 !important;
}

.turnToMove {
    background-color: #383838 !important;
    color: inherit !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

#turnToMove__color--white {
    background-color: #e0e0e0 !important;
    color: #000 !important;
    border: 1px solid #9a9a9a;
}

#turnToMove__color--black {
    background-color: #303030 !important;
    color: #fff !important;
    border: 1px solid #9a9a9a;
}

.variation-header__title {
    border-bottom: none;
}

.openingDetails {
    border-top: none;
}

/* profile */
.profile>.profileRows>.rows {
    background-color: #5b5b5b !important;
}

.profile>.profileRows>.rowsAlt {
    background-color: #6f6f6f !important;
}

.book {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a !important;
    box-shadow: 0 1px 3px #6a6a6a;
    color: inherit !important;
}

.book .title {
    font-weight: 700 !important;
}

/* badges */
.badgeCherryTextContrast {
    color: #000 !important;
}

/* practice */
.practice-status__playing-white {
    background-color: #e0e0e0 !important;
    color: #000 !important;
}

/*.reading-button {
    background-color: #29364a;
    color: #e0e0e0;
}

.reading-button:hover {
    background: #21252a;
}*/

/* logged out */
a.nav-bar__link, .new-frontpage-menu li a {
    background-color: inherit !important;
    color: inherit !important;
}

a.nav-bar__link:hover {
    text-decoration: none;
}

.nav-bar__item:hover>.nav-bar__link--color-green, .nav-bar__item:hover>.nav-bar__link--color-green {
	background-image: linear-gradient(-180deg,hsla(0,0%,100%,.2),hsla(0,0%,100%,.14));
    background-color: transparent;
    box-shadow: 0 3px 8px 0 rgba(0,0,0,.14)
}

.nav-bar__link--color-green, .new-frontpage-menu li .sign-up-link {
    background-image: none;
    background-color: inherit !important;
    text-shadow: none;
    border: none;
    box-shadow: none;
}

.join-now {
    background-color: #476694 !important;
    color: #fff !important;
    border-color: #476694 !important;
    text-shadow: none !important;
    background: linear-gradient(#466593 54%,#486795 60%) !important;
}

.new-frontpage-menu li .repertoires-link:hover, .new-frontpage-menu li .sign-up-link:hover{
    background-image: -moz-linear-gradient(-180deg, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0.14) 100%);
    background-image: -ms-linear-gradient(-180deg, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0.14) 100%);
    background-image: -webkit-linear-gradient(-180deg, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0.14) 100%);
    background-image: linear-gradient(-180deg, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0.14) 100%);
    -webkit-box-shadow: 0px 3px 8px 0px rgba(0,0,0,0.14);
    box-shadow: 0px 3px 8px 0px rgba(0,0,0,0.14);
}

/* login page */
.login-box {
    background: #5b5b5b !important;
}

.lb-header { /* no need, this is already in topnav */
    display: none;
}

.i2ybutton.green {
    border-color: grey !important;
}

/* Tools > Explore line */
.nextMovesTable {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a !important;
    box-shadow: 0 1px 3px #6a6a6a !important;     
}

.nextMovesBit>tr:not(.tr--noHover):hover {
    background-color: #6f6f6f !important;
}

.nextMovesSource select {
    background: #383838 !important;
    color: inherit !important;
    border: 1px solid #6a6a6a !important;
}

.nextMovesSource select:focus {
    outline: none;
}

.nextMovesSource option {
    background: gray !important;
    color: white !important;
}

#interactivePGN-container {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a !important;
    box-shadow: 0 1px 3px #6a6a6a !important; 
}

.moveActive {
    background-color: #6f6f6f;
}

#inputFEN {
    background-color: #383838 !important;
    color: #b4b4b4 !important;
    border: 1px solid #6a6a6a !important;
}

/* Tools > Analysis */
.analysis-container {
    background-color: #383838 !important;
    border: 1px solid #6a6a6a;
    box-shadow: 0 1px 3px #6a6a6a;
}

.a-move {
    color: inherit !important;
}

.active {
    background-color: #6f6f6f !important;
}

/* Tools > Import */
.inputfile+label, #cutAndPastePGN {
    background-color: #383838 !important;
}

/* Tools > Board editor */
.valid {
    box-shadow: none !important;
}

#turnToPlay {
    background: #484848 !important;
    color: inherit !important;
    border: 1px solid #6a6a6a !important;
}

/* Tools > New Repertoire */
.form-style-1>li>select {
    background: #484848 !important;
    color: inherit !important;
    border: 1px solid #6a6a6a !important;
}

.form-style-1>li>textarea {
    background-color: #383838 !important;
    color: inherit !important;
}

.tutorialBubble {
    background-color: #505050 !important;
    border: 1px solid #6a6a6a !important;
    color: #efefef !important;
}

.tutorialBubble::before {
    left: -42px;
    border-color: transparent #6a6a6a;
}

.tutorialBubble::after {
    border-color: transparent #505050;
}

/* Forum */
.thread_stats {
    background-color: #505050 !important;
}

/* Go pro */
.pro_sell {
    background-color: #383838 !important;
    color: #efefef !important;
    border: 1px solid #6a6a6a !important;
    box-shadow: 0 1px 3px #6a6a6a !important;
}

.pro_price {
    color: #efefef !important;
}

#currencies {
    background: #484848 !important;
    color: #efefef !important;
    border: 1px solid #6a6a6a !important
}

.pro_features, .pro_whynot {
    background-color: #414141 !important;
    color: #efefef !important;
}
}
