css:
@-moz-document domain("jandan.net") {
body, html {
	background-color: rgb(33, 33, 33);
	color: rgb(186, 186, 186);
}
a, a:link, a:visited {
	color: rgb(186, 186, 186);
	transition: color .1s;
}
a:hover {
	color: orangered;
}
.righttext {
	color: rgb(150, 149, 153);
}
.righttext a {
	color: rgb(150, 149, 153);
}
hr {
	border-color: rgb(67, 67, 67);
}
#header .logo h1 a {
	background-image: url(data:image/gif;base64,);
	filter: drop-shadow(0px 1px 3px #993);
}
#body {
	border-color: rgb(50, 50, 50);
	background-image: linear-gradient(to top, rgb(50, 50, 50), rgb(50, 50, 50));
	background-size: 1px 1px;
}
#nav_next a, #nav_prev a {
	color: rgb(179, 179, 179);
}
#nav_next a:hover, #nav_prev a:hover {
	color: rgb(135, 135, 135);
}
#nav_top {
	background-color: rgb(50, 50, 50);
	color: rgb(191, 191, 191);
}
#nav_top:hover {
	background-color: rgb(135, 135, 135);
}
#content .photos h3 {
	color: rgb(186, 186, 186);
}
#content h3 {
	color: rgb(186, 186, 186);
}
#content h3 a {
	color: rgb(186, 186, 186);
}
#content .title {
	background-color: rgb(39, 39, 39);
	border-bottom-color: rgb(50, 50, 50);
	color: rgb(175, 175, 175);
}
#content .title a {
	color: rgb(175, 175, 175);
}
#content .f {
	border-bottom-color: rgb(50, 50, 50);
}
#content .s {
	border-bottom-color: rgb(50, 50, 50);
}
#content .post p a, #content .post p a:link, #content .post p a:visited {
	border-bottom-color: rgb(33, 135, 135);
	color: rgb(186, 186, 186);
}
#content .post p a:hover {
	color: #DD3333;
}
#content .post li a, #content .post li a:link, #content .post li a:visited {
	border-bottom-color: rgb(33, 135, 135);
	color: rgb(186, 186, 186);
}
#content .post li a:hover {
	color: #DD3333;
}
em {
	color: rgb(114, 114, 114);
}
#content .post em a, #content .post em a:link, #content .post em a:visited {
	border-bottom-color: rgb(50, 50, 50);
	color: rgb(106, 106, 110);
}
#content .post em a:hover {
	color: #DD3333;
}
#content .post del {
	background-color: rgb(67, 67, 67);
	color: rgb(106, 106, 110);
}
#content .post blockquote {
	background-color: rgb(40, 40, 40);
	border-left-color: rgb(50, 50, 50);
	color: rgb(186, 186, 186);
}
#content .post blockquote.block {
	background-color: rgb(49, 49, 44);
}
#content .post strong.highlight {
	background-color: rgb(33, 50, 101);
}
#content .post h4 {
	border-left-color: #DD3333;
}
#content .post h4.pullquote {
	background-color: rgb(45, 45, 33);
	border-left-color: rgb(68, 68, 33);
}
#content .post .time_s {
	color: rgb(114, 114, 114);
}
#content .post .time_s a {
	color: rgb(114, 114, 114);
}
#content .comment-link {
	color: rgb(153, 153, 153);
}
#content .comment-link a {
	color: rgb(140, 140, 140);
}
#content .comment-big a {
	color: rgb(178, 178, 178);
}
#content .post h1 {
	border-bottom-color: rgb(50, 50, 50);
}
#content .post h1 a, .post h2 a {
	color: #ec5747;
}
#content .post h1 a:hover, .post h2 a:hover {
	color: #4285F4;
}
#content .other h3 {
	color: rgb(127, 127, 127);
}
.commentlist .time a {
	color: rgb(150, 149, 153);
}
.commentlist .row {
	border-bottom-color: rgb(50, 50, 50);
}
.commentlist .author small a {
	color: rgb(153, 153, 153);
}
#commentform input#author, #commentform input#email, #commentform input#url {
	border-color: rgb(50, 50, 50);
	background: #333;
	color: #ccc;
}
#commentform input#author:hover, input#email:hover, input#url:hover {
	background-color: rgb(33, 33, 33);
	background: #333;
	color: #ccc;
}
#commentform textarea {
	border-color: rgb(50, 50, 50);
	background: #333;
	color: #ccc;
}
#commentform #submit {
	background-color: rgb(39, 39, 39);
	border-color: rgb(50, 50, 50);
	color: #ccc;
}
.commentlist .tip .row {
	background-color: rgb(40, 40, 40);
}
#sidebar h3 {
	color: rgb(127, 127, 127);
}
#sidebar ol {
	border-bottom-color: rgb(50, 50, 50);
	color: rgb(127, 127, 127);
}
#sidebar ul {
	border-bottom-color: rgb(50, 50, 50);
}
#sidebar form {
	border-bottom-color: rgb(50, 50, 50);
	background-color: rgb(39, 39, 39);
}
#sidebar form input {
	background-color: rgb(39, 39, 39);
	color: rgb(114, 114, 114);
}
#sidebar .tags a {
	border-bottom-color: rgb(50, 50, 50);
	border-right-color: rgb(50, 50, 50);
	color: #444444;
}
#sidebar .tags a:hover {
	background-color: #444444;
	color: rgb(191, 191, 191);
}
#sidebar .links a {
	color: #444444;
}
#sidebar .links a:hover {
	color: #444444;
}
#sidebar #float {
	background-color: rgb(33, 33, 33);
}
#sidebar .s_title {
	color: rgb(175, 50, 39);
}
#sidebar .s_title:hover {
	color: #4285F4;
}
#sidebar .s_related li {
	border-top-color: rgb(50, 50, 50);
}
#footer {
	border-color: rgb(50, 50, 50);
	color: rgb(127, 127, 127);
}
#footer a {
	color: rgb(127, 127, 127);
}
.postnav a {
	background-color: rgb(39, 39, 39);
	border-color: rgb(50, 50, 50);
	color: rgb(114, 114, 114);
}
.postnav a:hover {
	border-color: #DD3333;
	color: #444444;
}
.wp-pagenavi {
	color: rgb(127, 127, 127);
}
.wp-pagenavi a {
	background-color: rgb(84, 84, 84);
	color: rgb(191, 191, 191);
}
.wp-pagenavi a:hover {
	color: rgb(33, 33, 33);
}
.wp-pagenavi .pages {
	background-color: rgb(46, 46, 46);
	border-color: rgb(50, 50, 50);
}
.wp-pagenavi .current {
	border-color: rgb(50, 50, 50);
}
.cp-pagenavi {
	border-bottom-color: rgb(50, 50, 50);
}
.cp-pagenavi a {
	background-color: rgb(84, 84, 84);
	color: rgb(191, 191, 191);
}
.cp-pagenavi a:hover {
	background-color: #444444;
}
.cp-pagenavi .current-comment-page {
	border-color: rgb(50, 50, 50);
	color: rgb(127, 127, 127);
}
.acv_author {
	background-color: rgb(43, 43, 43);
}
.vote {
	color: rgb(150, 149, 153);
}
.vote a {
	color: rgb(150, 149, 153);
}
.acv_comment {
	background: none repeat scroll 0% 0% rgb(33, 33, 33);
}
.acv_comment .show_more {
	background-color: #000000;
	color: rgb(191, 191, 191);
}
.acv_comment .vote {
	background-color: rgb(33, 33, 33);
}
.acv_author {
	color: rgb(127, 127, 127);
}
.acv_author a {
	color: #555555;
}
.acv4:link {
	color: rgb(33, 118, 118);
}
.acva:link {
	color: rgb(118, 118, 33);
}
.acv4:link:hover {
	color: #FF0000;
}
.acva:link:hover {
	color: #0000FF;
}
#authorbar {
	background-color: rgb(47, 47, 47);
	border-top-color: rgb(50, 50, 50);
}
#authorbar a {
	color: #555555;
}
.gif-mask {
	background-color: #000000;
	color: rgb(191, 191, 191);
}
.gif-mask {
	background-color: #000000;
	color: rgb(191, 191, 191);
}
.video-mask {
	background-color: #000000;
	color: rgb(191, 191, 191);
}
.hot-tabs li {
	border-color: rgb(50, 50, 50);
}
.hot-tabs li.current {
	background-color: rgb(50, 50, 50);
	font-weight: bold;
}
.host-list-split {
	background-color: rgb(50, 50, 50);
}
a.share-link-weibo {
	color: rgb(191, 191, 191);
}
a.share-link-weixin {
	color: rgb(191, 191, 191);
}
.share-link-weibo {
	background-color: #FF5555;
}
.share-link-weixin {
	background-color: #34A853;
}
.post a.jandan-zan {
	background-color: #295377;
	color: rgb(191, 191, 191);
}
.post a.jandan-zaned:hover {
	color: rgb(118, 118, 118);
}
.list-post a.jandan-zan {
	color: rgb(212, 212, 212);
}
.list-post a.jandan-zan:visited {
	color: rgb(33, 33, 33);
}
.list-post a.jandan-zan:hover {
	color: #4285F4;
}
.list-post a.jandan-zaned {
	color: rgb(191, 95, 29);
}
.headline .cat-link {
	color: rgb(114, 114, 114);
}
.block-panel span:hover {
	color: red;
}
a.view_img_link {
	color: rgb(127, 127, 127);
}
.hot-tabs {
	border-bottom-color: rgb(50, 50, 50);
}
.hot-tabs .hot-tab {
	border-right-color: rgb(50, 50, 50);
}
.hot-tabs .current-tab {
	color: rgb(203, 203, 203);
	background-color: rgb(49, 49, 49);
}
#shang-button {
	background-color: #FF7F27;
	color: rgb(191, 191, 191);
}
.shang .shang-peoples {
	color: rgb(152, 152, 152);
}
#shang-content {
	color: rgb(152, 152, 152);
}
#shang-content .shang-submit {
	background-color: #666666;
	color: rgb(191, 191, 191);
}
#shang-iframe {
	background-color: rgb(33, 33, 33);
	border-color: rgb(50, 50, 50);
}
#shang-iframe .shang-iframe-title {
	border-bottom-color: rgb(50, 50, 50);
}
.jandan-vote {
	color: rgb(150, 149, 153);
}
.jandan-vote a.like, .tucao-vote a.like {
	color: #ce5a47;
}
.jandan-vote a.like:hover, .tucao-vote a.like:hover, .jandan-vote a.like:visited, .tucao-vote a.like:visited {
	color: #ff0000;
}
.jandan-vote a.unlike, .tucao-vote a.unlike {
	color: #3991b9;
}
.jandan-vote a.tucao-btn:link, tucao-vote a.tucao-btn:link {
	color: rgb(88, 89, 84);
}
.jandan-vote a.unlike:hover, .tucao-vote a.unlike:hover, .jandan-vote a.unlike:visited, .tucao-vote a.unlike:visited {
	color: #0000ff;
}
.jandan-tucao {
	border-color: rgb(50, 50, 50);
}
.jandan-tucao-more:hover {
	background-color: #333333;
	color: rgb(191, 191, 191);
}
.jandan-tucao-close {
	color: rgb(114, 114, 114);
}
.jandan-tucao-close a {
	color: rgb(150, 149, 153);
}
.tucao-hot {
	border-color: rgb(50, 50, 50);
	background-color: rgb(33, 33, 50);
}
.tucao-hot-title {
	border-bottom-color: rgb(50, 50, 50);
	color: #444444;
	background-color: rgb(50, 50, 50);
}
.tucao-row {
	border-bottom-color: rgb(50, 50, 50);
}
.tucao-author, .tucao-author a {
	color: rgb(150, 149, 153);
}
.tucao-tip-author {
	color: rgb(165, 38, 25);
}
.tucao-vote {
	color: rgb(150, 149, 153);
}
.tucao-vote span, .tucao-vote a {
	color: rgb(150, 149, 153);
}
.tucao-vote span.tucao-oo, span.tucao-id {
	color: rgb(191, 190, 178);
}
.tucao-at:hover {
	color: #333333;
}
a.tucao-link, a.tucao-link:link {
	color: rgb(135, 135, 135);
}
.tucao-form button {
	background-color: rgb(84, 84, 84);
	border-color: rgb(84, 84, 84);
	color: rgb(191, 191, 191);
}
.tucao-form input.tucao-nickname, .tucao-form input.tucao-email {
	border-color: rgb(50, 50, 50);
	background: #333;
	color: #ccc;
}
.tucao-form textarea {
	border-color: rgb(50, 50, 50);
	background: #333;
	color: #ccc;
}
#tucao-popup {
	border-color: rgb(88, 89, 84);
	background-color: rgb(33, 33, 33);
}
.tucao-popup-author {
	border-bottom-color: rgb(88, 89, 84);
}
#jandan-msg {
	background-color: #383838;
	color: rgb(180, 180, 180);
}
}
