css:
@-moz-document domain("app.mysms.com") {
@font-face {
	font-family: 'Roboto';
	font-style: normal;
	font-weight: 400;
	src: local('Roboto Regular'), local('Roboto-Regular'), url(https://themes.googleusercontent.com/static/fonts/roboto/v11/CrYjSnGjrRCn0pd9VQsnFOvvDin1pK8aKteLpeZ5c0A.woff) format('woff');
}
@font-face {
	font-family: 'Roboto';
	font-style: normal;
	font-weight: 700;
	src: local('Roboto Bold'), local('Roboto-Bold'), url(https://themes.googleusercontent.com/static/fonts/roboto/v11/d-6IYplOFocCacKzxwXSOLO3LdcAZYWl9Si6vvxL-qU.woff) format('woff');
}
body {
	font-family: Roboto, Helvetica!important;
}
.mainPanel {
	position: static!important
}
.goPro,
.friendList,
.logo,
.feedbackButton {
	display: none!important
}
.dataPanel.conversation .avatar {
	left: 0!important;
	top: 0!important;
	width: 6.4em!important;
	height: 6.4em!important;
	border-radius: 0em!important;
}
.dataPanel.conversation .divider {
	display: none;
}
.dataPanel.conversation .name,
.dataPanel.conversation .snippet {
	left: 8.2em!important;
}
.dataPanel.conversation.single .container>div:hover {
	background: #fbfbfb!important;
}
.touchScrollPanel .dataPanel.conversation.single .container>div.selected,
.callsPopupPanel .headerCalls {
	background-color: #777777!important;
	background: #777777!important;
	background-image: #777777!important;
}
.callsPopupPanel .headerCalls div {
	color: #fff!important;
	text-shadow: none;
}
textarea {
	-webkit-box-shadow: none!important;
}
textarea:focus {
	border: 1px solid #777!important;
}
.messageListView {
	background-color: #ededed!important
}
.container>div>span.bubble {
	/*border-radius: 0!important;*/
	/*box-shadow: 1px 1px 2px rgba(150, 150, 150, 0.2);*/
}
.container>div.incoming>span.bubble {
	color: #fff!important;
	background: #0091ea!important;
	/*border: 0.1em solid #003656!important;*/
}
.container>div.outgoing>span.bubble {
	color: #fff!important;
	background: #333!important;
	/*border: 0.1em solid #292929!important;*/
}
.message .name {
	display: none;
}
.recipientEditorPanel.focus {
	border: 1px solid #777!important;
}
div.outgoing .icon {}
.dataPanel.message .outgoing .arrow {
	border-color: rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) #ffebac!important;
}
.dataPanel.message .incoming .arrow {
	border-color: rgba(255, 255, 255, 0) #fcfcfc rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) !important;
}
.dataPanel.message .outgoing .arrow-border {
	border-color: rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) #e9d2ac!important;
}
.dataPanel.message .incoming .arrow-border {
	border-color: rgba(255, 255, 255, 0) #dfdfdf rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) !important;
}


.mainPanel .GO2BGF-CEI {
	position: fixed!important;
	left: auto!important;
	top: auto!important;
	right: 25px!important;
	bottom: 20px!important;
	height: 170px;
	width: 56px;
	z-index: 1;
	background: transparent;
}
.mainPanel .GO2BGF-CEI .calls,
.mainPanel .GO2BGF-CEI .composeMessage,
.mainPanel .GO2BGF-CEI .account {
	position: absolute;
	border-left: 0;
	border-right: 0;
	border-radius: 50%;
	box-shadow: 0 0 4px rgba(0, 0, 0, .14), 0 4px 8px rgba(0, 0, 0, .28);
	box-sizing: border-box;
}
.mainPanel .GO2BGF-CEI .composeMessage {
	background-color: #0288d1;
	padding: 0;
	height: 56px;
	width: 56px;
	bottom: 0;
}
.mainPanel .GO2BGF-CEI .composeMessage:before {
	width: 56px;
	height: 56px;
	position: absolute;
	content: " ";
	background-repeat: no-repeat;
	background-position: 50%;
	background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/2x/bt_speed_dial_2x.png);
	background-size: 50%;
	transform: rotate(180deg);
	transition: transform .3s;
}
.mainPanel .GO2BGF-CEI .composeMessage:hover:before {
	background-image: url(https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/2x/bt_compose2_2x.png);
	transform: rotate(360deg);
}
.GO2BGF-CEI .composeMessage>img,
.GO2BGF-CEI .subscription,
.GO2BGF-CEI .leftBorder {
	display: none;
}
.mainPanel .GO2BGF-CEI .calls:before,
.mainPanel .GO2BGF-CEI .composeMessage>div {
	background: rgba(0, 0, 0, 0.5);
	padding: 0.6em;
	border-radius: 3px;
	width: 100px;
	right: 130%;
	top: 25%;
	position: absolute;
	opacity: 0;
	transition: opacity .3s;
	font-size: 1.2em;
	line-height: 1em;
}
.mainPanel .GO2BGF-CEI .calls:before {
	content: "Journal d'appels";
	top: 15%;
	right: 170%;
	width: 90px;
}
.mainPanel .GO2BGF-CEI .composeMessage:hover > div,
.mainPanel .GO2BGF-CEI .calls:hover:before {
	opacity: 1;
}
.mainPanel .GO2BGF-CEI .calls,
.mainPanel .GO2BGF-CEI .account {
	width: 40px;
	height: 40px;
	transition: bottom .3s;
	background: #45ade5;
	border-radius: 50%;
	left: 8px;
	bottom: 10px;
}
.mainPanel .GO2BGF-CEI .calls {
	padding: 0.7em 0.8em 0.5em;
}
.GO2BGF-CEI:hover .calls {
	bottom: 70px;
}
.GO2BGF-CEI:hover .account {
	bottom: 120px;
}


.callsPopupPanel .arrow,
.callsPopupPanel .arrow-border,
.accountOptionDialog .arrow,
.accountOptionDialog .arrow-border {
	display: none
}
div.mainPanel>div:last-child {
	top: 0!important
}
.callsPopupPanel,
.accountOptionDialog {
	position: fixed!important;
	right: 100px;
	bottom: 20px;
	left: auto!important;
	top: auto!important;
}
.dataPanel.conversation .container div.item {
	border: none!important
}

.touchScrollPanel {
	    background: #282828;
}

.name>span {
	    color: white;
}

.arrow-border, .arrow {
	display: none !important;
}

span.bubble {
	height: 50px;	
	border-radius: 3px !important;
	border: none !important;
}

span.bubble .message {
	top: 25px;	
	font-size: 18px !important;
}

.date {
    float: right;	
	color: #dedede !important;
}

.header {
	color: #fff;
}

.charCounter, .messageComposePanel.bottom {
background-color: #424242 !important;	
	color: #fff;
}

.textarea {
	background-color: #313131 !important;	
	color: #fff;
	border: 1px solid #212121;
}


.mainPanel .GO2BGF-CEI {
    position: fixed!important;
    left: 25px!important;
    top: auto!important;
    right: auto!important;
    bottom: 20px!important;
    height: 170px;
    width: 56px;
    z-index: 1;
    background: transparent;
}

.messageListView.bottom .charCounter {
    border-top: 0.1em solid #212121;
    border-right: 0.1em solid #212121;
}

.messageListView.bottom .touchScrollPanel {
    border-bottom: 0.1em solid #212121;
}

button.sendButton.sim, button.primary {
    border-color: #3f3f3f;
    background-color: #525252;
	background-image: none;
}
}
