css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
:root {
    --color-kiiro:#f90;
    --color-shiro:#fff;
    --color-hai:#333;
  }
/*   背景色 */
  .style-scope.ytd-page-manager{
    background-color: #222 !important;
  }
  a{
   
  }
/*h1は動画タイトルだけ    */
  h1{
    color:#fff !important;
  }
/*   チャット欄↑ */
  .style-scope.yt-live-chat-renderer.x-scope.yt-live-chat-header-renderer-0{
    background-color: #333
  }
  .style-scope.yt-live-chat-header-renderer{
    color: var(--color-shiro) !important;
  }
/*   チャット欄青ボタン */
  .style-scope.yt-live-chat-item-list-renderer.x-scope.paper-icon-button-0{
    background-color: var(--color-kiiro)
  }
/*   チャット欄コメント */
  .style-scope.yt-live-chat-text-message-renderer{
    color: var(--color-shiro) !important;
  }
/*   チャット欄背景 */
  .hide-on-collapse.style-scope.yt-live-chat-renderer{
    background-color: #222
  }
/*   n人が視聴中 */
  .view-count.style-scope.yt-view-count-renderer{
    color: rgba(238,238,238,0.6)
  }
/*   評価ボタン類 */
  .style-scope.ytd-menu-renderer.style-text.force-icon-button{
    color: rgba(238,238,238,0.4)
  }
  .style-scope.ytd-menu-renderer.force-icon-button.style-default{
    color: rgba(238,238,238,0.4)
  }
  .style-scope.ytd-menu-renderer.style-default{
    color: rgba(238,238,238,0.4)
  }
  /*評価ボタンおした時*/
  .style-scope.paper-icon-button-light.circle{
    color: #f90 !important;
  }
/*   評価ボタン↓色 */
  .style-scope.ytd-sentiment-bar-renderer{
    background-color: var(--color-kiiro) !important;
  }
/*   チャンネル名 */
  .yt-simple-endpoint.style-scope.yt-formatted-string{
    color: var(--color-shiro);
  }
/*   n時間前にライブ配信 */
  .date.style-scope.ytd-video-secondary-info-renderer{
    color: var(--color-shiro)!important;
  }
/*   ライセンス */
  .content.style-scope.ytd-metadata-row-renderer{
    color: var(--color-shiro);
  }
/*   説明文 */
  .content.style-scope.ytd-video-secondary-info-renderer{
    color: #fff !important;
  }
/*   show more */
  .more-button.style-scope.ytd-video-secondary-info-renderer{
    color: var(--color-kiiro) !important;
  }
/*   show less */
  .less-button.style-scope.ytd-video-secondary-info-renderer{
    color: var(--color-kiiro) !important;
  }
/*   横動画一覧タイトル */
  #video-title{
    color: var(--color-shiro)
  }
/*   次の動画 */
  .style-scope.ytd-compact-autoplay-renderer{
    color: var(--color-shiro) !important;
  }
/*   自動再生青色 うまく行かなかったので*/
/*   .paper-toggle-button[checked]:not([disabled]){
    background-color: #f90;
  }
  .paper-toggle-button[checked]{
    color:#f90 !important;
  }
  .toggle-button.style-scope.paper-toggle-button{
    background-color: #f90 !important;
  } */
/*   n件のコメント */
  .count-text.style-scope.ytd-comments-header-renderer{
    color: #fff
  }
/*   並び替え */
  .style-scope.yt-dropdown-menu{
    color: #fff !important;
    fill: rgba(255,255,255,0.9) !important;
  }
/*   公開コメントを追加 */
  .style-scope.ytd-comment-simplebox-renderer{
    color : #fff !important;
  }
/*   コメント投稿者 */
  .style-scope.ytd-comment-renderer{
    color : #fff
  }
  .style-scope.ytd-comment-renderer{
    color: #fff !important;
  }
/*   view all n replies */
  .align-by-text.style-scope.ytd-comment-replies-renderer{
    color: var(--color-kiiro) !important;
  }
  .style-scope.ytd-comment-replies-renderer{
    color: var(--color-kiiro) !important; 
  }
/*   when author */
  .style-scope.ytd-author-comment-badge-renderer{
    color: var(--color-kiiro) !important;
    fill: red !important;
  }
  .style-scope.ytd-author-comment-badge-renderer{}
/*   コメント評価 */
  .style-scope.ytd-comment-action-buttons-renderer.style-text{
    color: var(--color-shiro)
  }
  .style-scope.ytd-button-renderer.style-text{
    color: var(--color-shiro)
  }
/*   投稿者 */
  #byline{
    color: rgba(238,238,238,0.6) !important;
  }
/*   再生数 */
  .style-scope.ytd-video-meta-block{
    color: var(--color-shiro)
  }
/*   ヘッダー */
  #masthead{
    --yt-swatch-primary: rgb(35,35,35)!important;
    --yt-swatch-primary-darker: rgb(32,32,32)!important;
    --yt-swatch-text: rgb(255,255,255)!important;
    --yt-swatch-important-text: rgb(255,255,255)!important;
    --yt-swatch-input-text: rgba(255,255,255,1)!important;
    --yt-swatch-textbox-bg: rgba(19,19,19,1)!important;
    --yt-swatch-logo-override: rgb(255,255,255)!important;
    --yt-swatch-icon-color: rgba(136,136,136,1)!important;
  }
/*   黄色にはしなくていいかも */
/*   チャンネル登録ボタン */
  .style-scope.ytd-video-secondary-info-renderer.style-destructive{
/*     background-color: #f90 !important; */
  }
  .style-scope.ytd-button-renderer.style-destructive{
    color: #000 !important; 
  }
  .deemphasize.style-scope.yt-formatted-string{
    color: #000 !important;
  }
/*  ライブ配信中  */
  .badge.badge-style-type-live-now.style-scope.ytd-badge-supported-renderer{
/*     color: #f90 !important; */
/*     border-color: #f90 !important; */
    background-color: var(--color-hai);
  }
/*   -------------------------- */
/*   検索画面結果 */
  .style-scope.ytd-search-sub-menu-renderer{
    color: #fff !important;
  }
  
/*   検索画面タイトル */
  .yt-simple-endpoint.style-scope.ytd-video-renderer{
    color: #fff !important;
  }
/*   チャンネル */
  .style-scope.ytd-channel-renderer{
    color: var(--color-shiro) !important;
  }
  /*   -------------------------- */
/*   home */
/*   reccomend */
  .style-scope.ytd-shelf-renderer{
    color: var(--color-shiro) !important;
  }
/*   title */
  .yt-simple-endpoint.style-scope.ytd-grid-video-renderer{
    color: var(--color-shiro) !important;    
  }
/*   watch counter */
  .style-scope.ytd-grid-video-renderer{
    color: #aaa
  }
/*   official */
  .style-scope.ytd-badge-supported-renderer{
    fill: var(--color-kiiro) !important;
  } 
/*   recomend */
  .style-scope.ytd-badge-supported-renderer{
    color: var(--color-shiro)
  }
/*   browse channerls */
  .style-scope.ytd-grid-channel-renderer{
    color: var(--color-shiro) !important;
  }
  /*   channel about */
  .subheadline.style-scope.ytd-channel-about-metadata-renderer{
    color: var(--color-shiro)
  }
  .style-scope.ytd-channel-about-metadata-renderer{
    color: var(--color-shiro)
  }
  .style-scope.ytd-button-renderer{
    color: var(--color-shiro)
  }
/*   subscriptions ここの背景を黒にするならこのままでいいけど白なら買える */
  ytd-guide-section-renderer.style-scope:nth-child(3) > h3:nth-child(2) > yt-formatted-string:nth-child(1) > a:nth-child(1){
    color:rgba(17, 17, 17, 0.6) !important;
  }
  /*   -------------------------- */
/*   history */
  .style-scope.ytd-message-renderer{
    color: var(--color-shiro) !important;
  }
  .style-scope.ytd-sub-feed-selector-renderer {
    color: var(--color-shiro) !important;
  }
  .style-scope.ytd-sub-feed-option-renderer{
    color: var(--color-shiro) !important;
  }
  .style-scope.ytd-toggle-button-renderer.style-text{
    color: var(--color-shiro) !important;
  }

}
