/* Color Source */
 :root {
    --color-text1: #38daff;
    --color-text2: #00bfea;
    --background1: #2C2C2C;
    --background2: #181818;
    --color-blue1: #00aaee;
    --color-blue2: #181818;
    /* background-color: var(--color-text1) !important; */
}
/* Subscriptions Section Compact and Color*/

#guide-section-title.ytd-guide-subscriptions-section-renderer {
    padding: 1px 25px;
    font-size: 97%;
}
#guide-section-title.ytd-guide-section-renderer {
    padding: 1px 25px;
    font-size: 97%;
}
yt-img-shadow.ytd-guide-entry-renderer {
    border-radius: 1%;
}
.guide-entry-count.ytd-guide-entry-renderer {
    font-size: 1.3rem !important;
    color: var(--color-text2) !important;
    opacity: .6;
}
.title.ytd-guide-entry-renderer {
    color: var(--color-text1) !important;
    opacity: .7;
    font-size: 1.2rem;
}
#sections.ytd-guide-renderer > *.ytd-guide-renderer:first-child {
    padding: 1px 0 !important;
}
/*Titel*/
 #byline.ytd-video-meta-block, #author-text.yt-simple-endpoint.ytd-comment-renderer, #author-text.yt-simple-endpoint.ytd-comment-renderer, #title.ytd-shelf-renderer, yt-formatted-string.style-brand, #link-list-container.ytd-channel-about-metadata-renderer yt-formatted-string.ytd-channel-about-metadata-renderer, .yt-simple-endpoint.style-scope.yt-formatted-string {
    color: var(--color-text1) !important;
    opacity: 0.9 !important;
    text-decoration: none !important;
    text-shadow: none !important;
}
ytd-author-comment-badge-renderer:not(.creator) #icon.ytd-author-comment-badge-renderer {
    fill: #0091b2 !important;
}
#video-title:hover, #byline.ytd-video-meta-block:hover, #author-text.yt-simple-endpoint.ytd-comment-renderer:hover, ytd-button-renderer #button.ytd-button-renderer:hover, .yt-simple-endpoint.style-scope.yt-formatted-string:hover, #title.ytd-shelf-renderer:hover, #footer.ytd-guide-renderer #guide-links-primary a:hover:not(:last-child), #footer.ytd-guide-renderer #guide-links-secondary a:hover:not(:last-child), yt-formatted-string.style-brand:hover, #channel-title.ytd-channel-renderer:hover, paper-tab.ytd-c4-tabbed-header-renderer:hover, #link-list-container.ytd-channel-about-metadata-renderer yt-formatted-string.ytd-channel-about-metadata-renderer:hover, #title.ytd-grid-channel-renderer:hover, .content-text.style-scope.ytd-metadata-with-image-row-renderer:hover, .yt-simple-endpoint.style-scope.yt-formatted-string:hover, ytd-search-filter-renderer yt-formatted-string.ytd-search-filter-renderer:hover, #footer.ytd-guide-renderer #guide-links-primary a:hover, #footer.ytd-guide-renderer #guide-links-secondary a:hover, #name.ytd-author-comment-badge-renderer:hover, .title.ytd-mini-channel-renderer:hover {
    color: var(--color-text1) !important;
    opacity: 1 !important;
}
/* Progress bar */
 .html5-play-progress, .ytp-play-progress {
    background: var(--color-text1) !important;
}
.ytp-volume-slider-track, .ytp-volume-slider-handle:before {
    background: var(--color-text1) !important;
    z-index: -117;
}
.html5-scrubber-button:hover, .ytp-chrome-controls .ytp-button[aria-pressed]::after, .ytp-scrubber-button:hover, .ytp-swatch-background-color, .ytp-swatch-background-color-secondary {
    background: var(--color-text2) !important;
}
.ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after, .ytp-settings-button.ytp-3d-badge::after {
    background: var(--color-text2) !important;
}
.ytp-swatch-color {
    color: var(--color-text2) !important;
    height: 11px;
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
    background: var(--color-text2) !important;
}
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
    fill: var(--color-text1) !important;
}
.ytp-cued-thumbnail-overlay:hover .ytp-large-play-button-bg, .ytp-large-play-button.ytp-touch-device .ytp-large-play-button-bg {
    fill: var(--color-text2) !important;
}
paper-button[subscribed].ytd-subscribe-button-renderer {
    background-color: hsla(182, 100%, 62%, 0.43);
    color: hsla(183, 100%, 86%, 0.6);
}
.paper-button.ytd-subscribe-button-renderer {
    background-color: hsla(185, 75%, 61%, 0.26)!important;
    color: hsla(0, 0%, 68%, 0.6)!important;
}
.ytd-subscribe-button-renderer {
    padding: 2px 3px!important;
    margin: auto 2px!important;
}
.style-scope ytd-subscribe-button-renderer {
    color: hsla(171, 99%, 29%, 0.3) !important;
}
/*Logo hover*/
 #logo-icon:hover {
    --yt-swatch-logo-override: var(--color-text1) !important;
}
/* Like Green */
 #top-level-buttons ytd-toggle-button-renderer.style-default-active[is-icon-button]:first-of-type, ytd-toggle-button-renderer.ytd-comment-action-buttons-renderer.style-default-active:first-of-type {
    color: #07b21a !important;
}
/* UnLike Red  */
 #top-level-buttons ytd-toggle-button-renderer.style-default-active[is-icon-button]:last-of-type, ytd-toggle-button-renderer.ytd-comment-action-buttons-renderer.style-default-active:last-of-type {
    color: #D6271C !important;
}
/* Like Bar Green*/
 #like-bar {
    background-color: #07b21a !important;
}
/* Dislike Bar  Red*/
 ytd-sentiment-bar-renderer {
    background-color: #f12b24 !important;
    background-clip:content-box;
}
#items.ytd-grid-renderer > ytd-grid-video-renderer.ytd-grid-renderer:hover #video-title.yt-simple-endpoint.ytd-grid-video-renderer, #dismissable.ytd-compact-video-renderer:hover h3.ytd-compact-video-renderer, #content.ytd-playlist-video-renderer:hover #video-title.ytd-playlist-video-renderer, ytd-playlist-panel-video-renderer:hover #video-title.ytd-playlist-panel-video-renderer, ytd-playlist-panel-video-renderer:hover #index.ytd-playlist-panel-video-renderer, #items.yt-horizontal-list-renderer > .yt-horizontal-list-renderer:hover #video-title.yt-simple-endpoint.ytd-grid-video-renderer, ytd-notification-renderer:hover .text.ytd-notification-renderer, ytd-grid-playlist-renderer:hover #video-title.ytd-grid-playlist-renderer, #dismissable.ytd-video-renderer:hover #video-title.ytd-video-renderer, ytd-channel-renderer:hover #channel-title.ytd-channel-renderer, ytd-grid-channel-renderer:hover #title.ytd-grid-channel-renderer {
    color: var(--color-text1) !important;
}
ytd-guide-entry-renderer:hover .title.ytd-guide-entry-renderer, ytd-guide-entry-renderer:hover .guide-icon.ytd-guide-entry-renderer, ytd-menu-service-item-renderer:hover yt-formatted-string.ytd-menu-service-item-renderer, ytd-compact-link-renderer:hover #label.ytd-compact-link-renderer, ytd-compact-link-renderer:hover yt-icon.ytd-compact-link-renderer, paper-item:hover .item.yt-dropdown-menu, #label.yt-dropdown-menu:hover #icon-label.yt-dropdown-menu, yt-dropdown-menu:hover #label-text.yt-dropdown-menu, ytd-menu-navigation-item-renderer:hover yt-formatted-string.ytd-menu-navigation-item-renderer, ytd-playlist-add-to-option-renderer:hover #label.ytd-playlist-add-to-option-renderer, ytd-playlist-add-to-option-renderer:hover yt-icon.ytd-playlist-add-to-option-renderer, paper-item.ytd-account-settings:hover .label.ytd-account-settings, paper-item.ytd-account-settings:hover .style-scope.ytd-account-settings, paper-item.style-scope:nth-child(7):hover, paper-item.ytd-account-settings:hover, #more.ytd-vertical-list-renderer yt-formatted-string.ytd-vertical-list-renderer:hover, #items.yt-multi-page-menu-section-renderer > .yt-multi-page-menu-section-renderer:hover, #items.yt-multi-page-menu-section-renderer > .yt-multi-page-menu-section-renderer:hover yt-icon.ytd-toggle-theme-compact-link-renderer {
    color: var(--color-text1) !important;
}
/*Titel Color */
 .title.ytd-video-primary-info-renderer {
    color: var(--color-text1) !important;
}
#upnext.ytd-compact-autoplay-renderer {
    color: var(--color-text1) !important;
}
#autoplay.ytd-compact-autoplay-renderer {
    color: hsl(0, 0%, 61%) !important;
}
/*Verified badge*/
 .badge-style-type-simple.ytd-badge-supported-renderer, .badge-style-type-disabled.ytd-badge-supported-renderer, yt-icon.ytd-badge-supported-renderer {
    color: var(--color-text1) !important;
}
/*Info text*/
 #metadata-line.ytd-grid-video-renderer span.ytd-grid-video-renderer {
    /* display: inline-block; */
    color: var(--color-text2) !important;
}
/*Search Text colors*/
 .gsfs {
    color: var(--color-text1) !important;
}
.sbsb_c.gsfs:hover, .sbsb_d {
    background-color: #7a7a7a !important;
}
.sbsb_c:hover .sbqs_c, .sbsb_c.gsfs.sbsb_d .sbqs_c {
    color: #111111 !important;
}
.sbsb_a, .sbdd_b {
    background: #343434 !important;
    border: none !important;
}
/*Square Profile Pictures*/
 yt-img-shadow.ytd-mini-channel-renderer {
    border-radius: 7%;
}
yt-img-shadow.ytd-grid-channel-renderer {
    border-radius: 7%;
}
yt-img-shadow.ytd-topbar-menu-button-renderer {
    border-radius: 7%;
}
#avatar.ytd-active-account-header-renderer {
    border-radius: 7%;
}
yt-img-shadow.ytd-guide-entry-renderer {
    border-radius: 7%;
}
#avatar.ytd-video-owner-renderer {
    border-radius: 7%;
}
#author-thumbnail.ytd-comment-simplebox-renderer {
    border-radius: 7%;
}
#author-thumbnail.ytd-comment-renderer yt-img-shadow.ytd-comment-renderer {
    border-radius: 7%;
}
#avatar.ytd-c4-tabbed-header-renderer {
    border-radius: 7%;
}
#avatar.ytd-shelf-renderer {
    border-radius: 7%;
}
.sbpqs_a {
    color: #61D616;
}
/* Done */
}

@-moz-document domain("youtube.com") {
body::-webkit-scrollbar {
    width: 1em;
}
 
body::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
} 
body::-webkit-scrollbar-thumb {
  background-color: #000;
  outline: 1px solid #5e5e5e;

}
#yt-masthead-container {
    background: black !important;
    border-bottom: 1px solid #333 !important;
}
#player.watch-large {
    margin: 0 !important;
    width: 100% !important;
}
#player.watch-large #player-api {
    margin: 0 !important;
    width: 100% !important;
    height: 942px !important;
}
#player.watch-large video {
    width: 190% !important;
    height: 907px !important;
}
#player.watch-large .html5-video-content {
    display: none;
    visibility: collapse;
}
#alerts {
    display: none;
    visibility: collapse;
}
@media(min-width: 882px) {
    ytd-watch[theater] #player.ytd-watch {
        height: 100vw;
        max-height:950px !important
    }
}
#masthead-container {
    opacity: 1;
    transition: 0s;
   /*==transition-delay: 0s; ==*/

}
#page-manager.ytd-app {
    margin-top: 0;
}

[is-watch-page] ytd-page-manager {
  margin-top: 0!important;
}

[is-watch-page] ytd-masthead {
  background: transparent!important;
}
[is-watch-page] ytd-masthead::before {
  content: '';
  position: absolute;
  width: 100%!important;
  height: 100%!important;
  background: var(--yt-swatch-primary);
  opacity: .65;
  z-index: -1;
}

[is-watch-page] ytd-masthead,
[is-watch-page] #masthead-container.ytd-app::after {
  transform: translateY(-56px);
  transition: transform 0s 0s ease-out;
}
[is-watch-page] #masthead-container:hover ytd-masthead,
[is-watch-page] #masthead-container:hover.ytd-app::after {
  transform: translateY(0px);
}

[is-watch-page] [theater] #player.ytd-watch {
  height: 100vh!important;
  max-height: 100vh!important;
}

ytd-watch-flexy[theater] #player-theater-container.ytd-watch-flexy, 
ytd-watch-flexy[fullscreen] #player-theater-container.ytd-watch-flexy,
[is-watch-page] [theater] #player video {
  width: 100%!important;
  height: 100vh!important;
  max-height: 100vh!important;
  max-width: 100%!important;
  left: 0!important;
}

[is-watch-page] .ytp-chrome-top.ytp-share-button-visible,
[is-watch-page] #iv-drawer .iv-drawer-header,
[is-watch-page] #iv-drawer .iv-drawer-content {
  margin-top: 30px;
}

[is-watch-page] [theater] .ytp-chrome-top.ytp-share-button-visible,
[is-watch-page] [theater] #iv-drawer .iv-drawer-header,
[is-watch-page] [theater] #iv-drawer .iv-drawer-content {
  margin-top: 56px;
}

[is-watch-page] .ytp-tooltip.ytp-tooltip-opaque.ytp-bottom {
  right: 60px!important;
  top: 35px!important;
}

[is-watch-page] [theater] .ytp-tooltip.ytp-tooltip-opaque.ytp-bottom {
  right: 60px!important;
  top: 60px!important;
}
}
