css:
@-moz-document domain("syncedonline.com") {
.standardPage,
.frameHeaderStatus {
    background-color: #000000 !important;
}
.noclient img {
    display: none !important;
}
.statusPanel .TopBoxLinks a, .frameHeaderNavi .NaviButton .actionLabel, .frameHeaderStatus .TopBoxLinks .actionLabel {
    color: #d9d9d9;
}
.statusPanel .TopBoxLinks a:hover,
.pageTitle .SubNavi a:hover {
    text-decoration: underline !important;
}
.statusPanel .Levels {
    color: #d9d9d9;
}
.frameHeaderNavi .NaviButton .actionLabel {
    height: 22px
}
.frameHeaderNavi .NaviButton .actionLabel {
    font-size: 0px
}
.frameHeaderNavi .NaviButton.me {
    background: url(http://i.imgur.com/CxvHebL.png) no-repeat !important;
    color: #489BCC !important;
}
.frameHeaderNavi .NaviButton.stuff {
    background: url(http://i.imgur.com/RujxrTb.png) no-repeat !important;
    color: #489BCC !important;
}
.frameHeaderNavi .NaviButton.games {
    background: url(http://i.imgur.com/nMaqtLj.png) no-repeat !important;
    color: #489BCC !important;
}
.frameHeaderNavi .NaviButton.rooms {
    background: url(http://i.imgur.com/ilR2Vq8.png) no-repeat !important;
    color: #489BCC !important;
}
.frameHeaderNavi .NaviButton.groups {
    background: url(http://i.imgur.com/kHIVuAR.png) no-repeat !important;
    color: #489BCC !important;
}
.frameHeaderNavi .NaviButton.shop {
    background: url(http://i.imgur.com/GnvKqaZ.png) no-repeat !important;
    color: #489BCC !important;
}
.frameHeaderNavi .NaviButtonSelected {
    background: url("http://i.imgur.com/nNlWWX0.png") no-repeat !important;
