css:
@-moz-document url-prefix(about:downloads)
{
  #contentAreaDownloadsView
  {
    background-color: #111111;
  }
  #downloadsRichListBox
  {
    background-color: #222222;
    border: 1px solid black;
  }
  .download
  {
    color: #C7C7C7;
  }
  richlistitem.download[selected]
  {
    background-image: none;
    outline: 1px solid rgb(100,100,100, 0.4);
    background-color: rgb(10,10,10,0.5);
    color: #F5F5F5;
  }
  .downloadTypeIcon
  {
    opacity: 0.5;
  }
}

@-moz-document url-prefix(about:home) 
{
  body 
  {
    background-color:#111111;
  }
  #searchIconAndTextContainer
  {
    color: #C5C5C5;
  }
  #searchText
  {
    background-color: #222222;
  }
  #contentSearchSettingsButton
  {
    background: #222222;
    color: #C5C5C5;
  }
  #contentSearchDefaultEngineHeader
  {
    background: #222222;
    color: #C5C5C5;
  }
  #searchSubmit
  {
    background: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, #222 padding-box;
    box-shadow: 0 0 1px rgb(68,68,68, 1) inset, 0 1px 0 rgb(68,68,68, 0.2);
  }
  .launchButton
  {
    color: #C5C5C5;
  }
  .contentSearchSuggestionRow
  {
    background: #222222;
    color: #C5C5C5;
  }
  .contentSearchSuggestionsList
  {
    border-bottom: 1px solid #444444;
  }
  .contentSearchSuggestionTable
  {
    background-color: #222222;
  }
  .contentSearchHeader
  {
    border-bottom: 1px solid #444444;
  }
  
}

@-moz-document url-prefix(about:blank) 
{
  body { background-color: #111111 }
}

@-moz-document url-prefix(about:newtab) 
{
  #newtab-vertical-margin 
  { 
    background-color: #111111;
  }
  #newtab-search-text
  {
    background: #222222;
    color: #C5C5C5;
  }
  #newtab-search-submit
  {
    background: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, #222 padding-box;
    box-shadow: 0 0 1px rgb(68,68,68, 1) inset, 0 1px 0 rgb(68,68,68, 0.2);
  }
  #contentSearchSettingsButton
  {
    background: #222222;
    color: #C5C5C5;
  }
  #contentSearchDefaultEngineHeader
  {
    background: #222222;
    color: #C5C5C5;
  }
  #newtab-undo-container
  {
    background: #444444;
    color: #C5C5C5;
  }
  #newtab-undo-close-button
  {
    background-color: #777777;
  }
  #newtab-undo-button newtab-undo-restore-button
  {
    color: #389bff;
  }
  #newtab-customize-overlay
  {
    background: #000000;
  }
  #newtab-customize-button
  {
    background-color: #222222;
    border: solid 1px #333333;
  }
  #newtab-customize-button:-moz-any(:hover, :active, [active])
  {
    background-color: #222222;
    border: solid 1px #333333;
  }
  #newtab-customize-panel-anchor, #newtab-customize-title,.newtab-customize-complex-option, .newtab-customize-panel-item, #newtab-customize-panel-inner-wrapper
  {
   background-color: #222222;
  }
  .newtab-customize-panel-item[selected], .newtab-customize-panel-subitem[selected]
  {
    color: #999999;
  }
  .newtab-customize-panel-subitem > .checkbox
  {
    background-color: #222222;
    border: solid 1px #666666;
  }
  .newtab-customize-panel-subitem[selected] > .checkbox
  {
    background: url("chrome://global/skin/menu/shared-menu-check-black.svg") no-repeat #777777;
    background-size: 9px 9px;
    background-position: center;
  }
  .newtab-customize-complex-option:hover > .selectable:not([selected]), .selectable:not([selected]):hover, .newtab-customize-complex-option:hover > .selectable:not([selected]) + .newtab-customize-panel-subitem
  {
    background-color: #333333;
    color: #999999;
  }
  .newtab-title
  {
    background: #222222;
    color: #C5C5C5;
    border: 1px solid #222222;
  }
  .newtab-site
  {
    box-shadow: 0 1px 3px #555555 !important;
    border-top-color: black !important;
  }
  .thumbnail
  {
    opacity: 0.3;
  }
  .newtab-link:hover
  {
    border-color: #666666 !important;
    box-shadow: 0 1px 3px #777777 !important;
    border-top-color: #666666 !important;
  }
  .contentSearchSuggestionRow
  {
    background: #222222;
    color: #C5C5C5;
  }
  .contentSearchSuggestionsList
  {
    border-bottom: 1px solid #444444;
  }
  .contentSearchSuggestionTable
  {
    background-color: #222222;
  }
  .contentSearchHeader
  {
    border-bottom: 1px solid #444444;
  }
}
