css:
@-moz-document url("http://hostc.local:39549/"), url("http://192.168.1.9:39549/") {
html,
body {
  background: #222;
  color: #fff;
}

div.wrapper {
  background: #2196F3;
  position: fixed;
  right: 0;
  bottom: 0;
  max-width: 368px;
  margin: 24px;
  border-radius: 3px;
  transition: all ease-in-out .3s;
}

div.wrapper:hover {
  box-shadow: 0px 10px 15px rgba(0, 0, 0, .08);
  box-shadow: 0px 6px 12px rgba(0, 0, 0, .10);
}

div#trackimage {
  position: absolute;
  bottom: 100px;
  z-index: -1;
  margin: 0 auto;
  width: 100%;
  height: auto;
}

img.albumart,
img.noalbumart {
  margin: 0 auto;
  width: 100%;
  height: auto;
  border: none;
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  transition: all ease-in-out .3s;
}

img.noalbumart {
  content: url('http://i.imgur.com/0SZoIf8.png');
}

#trackbits {
  font-weight: 700;
  z-index: 0;
  padding: 12px 16px;
  max-width: 100%;
  background: rgba(0, 0, 0, .4);
  overflow-x: hidden;
  overflow-y: hidden;
  bottom: 0;
  left: 0;
  color: rgba(255, 255, 255, .2);
  text-align: left;
  transition: all ease-in-out .3s;
}

#tracktitle {
  color: rgba(255, 255, 255, 1);
  max-width: 352px;
  white-space: nowrap;
  text-overflow: ellipsis;
  font-family: "Roboto Mono";
  font-weight: 400;
  padding: 8px 0px;
  font-size: 22px;
  line-height: 20px;
}

#trackalbum,
#trackartist {
  font-weight: 400;
  color: rgba(255, 255, 255, .7);
}

#trackposition,
#trackduration {
  font-weight: 400;
  color: rgba(255, 255, 255, .5);
  line-height: 24px;
}

#seekbar {
  z-index: 24;
  margin: 0;
}

.pure-u-1-4 {
  display: none;
}

.pure-u-1-2 {
  width: 100%;
}

input[type=range] {
  -webkit-appearance: none;
  background: transparent;
  outline: none;
}

input[type=range]::-webkit-slider-runnable-track {
  -webkit-appearance: none;
  width: 100%;
  height: 16px;
  background: #1976D2;
}

input[type=range]::-webkit-slider-thumb {
  opacity: .7;
  border: none;
  height: 16px;
  width: 4px;
  background: #E3F2FD;
  cursor: pointer;
  -webkit-appearance: none;
  transition: all ease-in-out .3s;
}

input[type=range]::-webkit-slider-thumb:hover {
  opacity: 1;
}

div#playerbuttons {
  height: 84px;
  border: none;
  background: #2196F3;
  border-bottom-left-radius: 3px;
  border-bottom-right-radius: 3px;
}

.pure-g {
  text-align: center;
}

.pure-u-1-3 {}

img[src="/icon/media-skip-backward-symbolic/48"] {
  content: url('http://192.168.1.9/images/ic_previous.svg');
  height: 36px;
}

img[src="/icon/media-playback-pause-symbolic/48"] {
  content: url("http://192.168.1.9/images/ic_pause.svg");
  height: 36px;
}

img[src="/icon/media-playback-start-symbolic/48"] {
  content: url("http://192.168.1.9/images/ic_play.svg");
  height: 36px;
}

img[src="/icon/media-skip-forward-symbolic/48"] {
  content: url("http://192.168.1.9/images/ic_next.svg");
  height: 36px;
}

#previous {}

#playpause {}

#next {}

.pure-button-disabled {
  background: #444;
}

.pure-button {
  outline: none;
  opacity: 0.7;
  padding: 0;
  border: none;
  background: none;
  text-decoration: none;
  border-radius: 50%;
  transition: all ease-in-out .3s;
}

.pure-button:hover {
  opacity: 1;
  background: none;
  text-decoration: none;
}

div.playerbutton {
  text-align: center;
  margin: 24px 0;
  display: block;
}

#connectform {
  background: rgba(0, 0, 0, .4);
  margin-bottom: 16px;
}

.pure-form#connectform {
  background: rgba(0, 0, 0, 0);
  display: inline-flex;
  margin-bottom: 0;
}

.pure-form input[type="password"] {
  font-family: "Roboto Mono";
  font-size: 24px!important;
  line-height: 24px;
  color: #fff;
  -webkit-appearance: none;
  background: none;
  border-radius: 0;
  box-shadow: none;
  border: none;
  border-bottom: 2px solid rgba(255, 255, 255, .5);
  width: calc(100% - 40px);
}

.pure-form input[type="password"]::placeholder {
  color: rgba(255, 255, 255, .7);
}

.pure-form input[type="password"]:focus {
  border: none;
  border-bottom: 2px solid rgba(255, 255, 255, 1);
}

#connectbutton {
  opacity: 1;
  display: inline-flex;
}

img[src="/icon/go-next-symbolic/24"] {
  padding: 4px;
  width: 32px;
  height: 32px;
  border-radius: 50%;
  content: url("http://192.168.1.9/images/ic_done.svg");
  transition: all ease-in-out .3s;
}

img[src="/icon/go-next-symbolic/24"]:hover {
  background: rgba(255, 255, 255, .16)
}
}
