css:
@-moz-document domain("mycourses.rit.edu") {
html,
body,
.d2l-navigation-s,
.d2l-navigation-s-button-highlight,
.d2l-dropdown-opener,
.dco_c {
    background: #101010 !important;
    color: #c5c5c5;
}
.daylight .d2l-msg-container {
    background: #101010 !important;
    color: #c5c5c5;
    border-radius: 8px;
    border: 1px solid #202020;
}
.d2l-collapsepane-header,
.d2l-collapsepane-content {
    background: #101010 !important;
    color: #c5c5c5;
    border-radius: 8px;
    border: 1px solid #202020;
}
.d2l-calendar-mini {
    background: #101010 !important;
    color: #c5c5c5;
    border-radius: 8px;
    border: 1px solid #202020;
}
.d2l-calendar-mini tr {
    background: #101010 !important;
    color: #c5c5c5;
}
.d2l-calendar-mini-day,
.d2l-calendar-mini-weekend {
    background: #101010 !important;
    color: #c5c5c5;
}
.daylight .d2l-widget,
.d2l-navigation-s-main,
.d2l-navigation-s-header {
    border-bottom: 1px solid #303030;
}
.d2l-course-tile-0 .course-image.d2l-course-tile {
    background: #101010;
}
hr[style] {
    border: 1px solid #404040 !important;
}
.daylight .homepage-container div:last-of-type .d2l-widget:last-child {
    border-bottom: 1px solid #404040;
}
.d2l-dropdown-content {
    background-color: #222 !important;
}
.d2l-dropdown-content-1 .d2l-dropdown-content-width.d2l-dropdown-content,
.d2l-dropdown-content-1 .d2l-dropdown-content-pointer.d2l-dropdown-content>div.d2l-dropdown-content {
    border: 1px solid #444;
}
.d2l-dropdown-content-pointer {
    background: transparent;
    background-color: transparent !important;
}
li.d2l-datalist-item.d2l-datalist-item-actionable.d2l-datalist-simpleitem.vui-selected {
    background: #222;
}
.d2l-link,
.d2l-text-imagelink>.d2l-text-imagelink-text {
    color: #A3A3A3 !important;
}
.vui-list>.vui-selected,
.vui-list>.vui-selected+li {
    border-top-color: #444;
}
.d2l-navigation-s-band-color {
    background: #101010 !important;
}
.d2l-datalist-container {
    background: #222 !important;
}
.daylight .d2l-messagebucket-button-container {
    border-bottom: 1px solid #404040;
}
.d2l-msg-container-text {
    background: #222 !important;
}
.vui-active {
    background-color: #777 !important;
}
.d2l-datalist-container.d2l-datalist-style1.d2l-datalist-outdent {
    background: transparent !important;
}
.d2l-htmlblock a,
.d2l-htmlblock a:active,
.d2l-htmlblock a:link,
.d2l-htmlblock a:visited {
    color: #A3A3A3 !important;
}
.vui-dropdown-menu-item-link:focus,
.vui-dropdown-menu-item-link:hover {
    color: #A3A3A3 !important;
}
.course-text {
    color: #A3A3A3 !important;
}
.d2l-heading.vui-heading-2 {
    color: #A3A3A3 !important;
    font-size: 1.5em;
}
.d2l-floating-container {
    background: #222 !important;
    border: 1px solid #444;
    border-radius: .4rem;
    box-shadow: 0 2px 12px 0 rgba(86, 90, 92, .2);
}
.vui-dropdown-menu>ul>li {
    border-top: 1px solid #444;
    border-left: 1px solid #444;
    border-right: 1px solid #444;
    border-bottom: 1px solid #444;
    line-height: 0;
}
.vui-dropdown-menu>ul>li:last-child {
    border-bottom: 1px solid #444;
}
.vui-dropdown-menu>ul>li.d2l-last-visible-item {
    border-bottom-left-radius: .4rem;
    border-bottom-right-radius: .4rem;
    border-bottom: 1px solid #444;
}
.vui-dropdown-menu>ul>li.d2l-last-visible-item.vui-dropdown-menu-item-focus,
.vui-dropdown-menu>ul>li.vui-dropdown-menu-item-focus,
.vui-dropdown-menu>ul>li.vui-dropdown-menu-item-focus:last-child {
    border: 1px solid #222;
}
.vui-changed,
.vui-dropdown-menu>ul>li.d2l-last-visible-item.vui-dropdown-menu-item-focus,
.vui-dropdown-menu>ul>li.vui-dropdown-menu-item-focus {
    background-color: #777;
    color: #222;
}
tbody {
    background: #101010 !important;
}
.d2l-table-0 >*>tr>td,
.d2l-table-0 >*>tr>th {
    border-top: 1px solid #444;
    border-right: 1px solid #444;
}
.d2l-table-0 >*>tr[header]>th,
.d2l-table-0 >thead>tr>th {
    color: #A3A3A3;
    font-family: inherit;
    font-size: .7rem;
    line-height: 1rem;
    background-color: #070707;
    margin: 1rem 0;
}
.vui-input[type=checkbox],
.vui-input[type=checkbox]:hover:disabled {
    background-color: #333;
    border-color: #555;
    border-width: 1px;
    box-shadow: none;
    padding: 0;
}
.d2l-table-0 >tbody>tr[selected] {
    background-color: #777;
}
[dir=rtl] .d2l-table-0 >tbody>tr[selected]>td:last-child,
.d2l-table-0[dir=rtl] >tbody>tr[selected]>td:last-child,
[dir=rtl] .d2l-table-0 >tbody>tr[selected]>th:last-child,
.d2l-table-0[dir=rtl] >tbody>tr[selected]>th:last-child,
.d2l-table-0 >tbody>tr[selected]>td:first-child,
.d2l-table-0 >tbody>tr[selected]>th:first-child {
    border-left-color: #333;
}
.d2l-table-0 >tbody>tr[selected]+tr>td,
.d2l-table-0 >tbody>tr[selected]+tr>th,
.d2l-table-0 >tbody>tr[selected]>td,
.d2l-table-0 >tbody>tr[selected]>th {
    border-top-color: #555;
}
[dir=rtl] .d2l-table-0 >tbody>tr[selected]>td:first-child,
.d2l-table-0[dir=rtl] >tbody>tr[selected]>td:first-child,
[dir=rtl] .d2l-table-0 >tbody>tr[selected]>th:first-child,
.d2l-table-0[dir=rtl] >tbody>tr[selected]>th:first-child,
.d2l-table-0 >tbody>tr[selected]>td:last-child,
.d2l-table-0 >tbody>tr[selected]>th:last-child {
    border-right-color: #555;
}
.d2l-table-0[no-column-border] >tbody>tr>td:first-child,
.d2l-table-0[no-column-border] >tbody>tr>th:first-child,
[dir=rtl] .d2l-table-0 >*>tr>td:last-child,
.d2l-table-0[dir=rtl] >*>tr>td:last-child,
[dir=rtl] .d2l-table-0 >*>tr>th:last-child,
.d2l-table-0[dir=rtl] >*>tr>th:last-child,
.d2l-table-0 >*>tr>td:first-child,
.d2l-table-0 >*>tr>th:first-child {
    border-color: #555;
}
.d2l-table-0 >tbody:first-of-type>tr:first-child>th,
.d2l-table-0 >thead>tr:first-child>th {
    border-color: #555;
}
.d2l-table-0[no-column-border] >tbody>tr>td:last-child,
.d2l-table-0[no-column-border] >tbody>tr>th:last-child,
[dir=rtl] .d2l-table-0 >*>tr>td:first-child,
.d2l-table-0[dir=rtl] >*>tr>td:first-child,
[dir=rtl] .d2l-table-0 >*>tr>th:first-child,
.d2l-table-0[dir=rtl] >*>tr>th:first-child,
.d2l-table-0 >*>tr>td:last-child,
.d2l-table-0 >*>tr>th:last-child {
    border-color: #555;
}
.d2l-table-0 >*>tr[last-row]>td,
.d2l-table-0 >*>tr[last-row]>th,
.d2l-table-0 >tbody:last-child>tr:last-of-type>td,
.d2l-table-0 >tbody:last-child>tr:last-of-type>th,
.d2l-table-0 >tfoot>tr:last-of-type>td,
.d2l-table-0 >tfoot>tr:last-of-type>th,
.d2l-table-0 >thead:only-child>tr:last-of-type>td,
.d2l-table-0 >thead:only-child>tr:last-of-type>th {
    border-color: #555;
}
.daylight div.d_tabs_container,
.daylight .d_tabs_header td {
    border-color: #555;
}
.daylight .d_tabs_header .d_tabs_c,
.daylight .d_tabs_header td.d_tabs_c_o {
    background: #222;
    border-color: #555;
}
.daylight .d_tabs_header td.d_tabs_c_s,
.daylight .d_tabs_header td.d_tabs_c_s_o {
    border-color: #555;
}
.d2l-button,
.d2l-button[disabled]:focus,
.d2l-button[disabled]:hover {
    background-color: #222;
    border-color: #555;
    color: #A3A3A3;
}
.d2l-twopanelselector-side-bg.d2l-twopanelselector-side-sep,
.d2l-twopanelselector-side.d2l-twopanelselector-side-sep {
    border-right: 1px solid #555;
    background: transparent;
}
.daylight #ContentModuleTree .d2l-le-TreeAccordionItem.d2l-le-TreeAccordionItem-Selected > a.d2l-le-TreeAccordionItem-anchor::before,
.daylight #ContentModuleTree .d2l-le-TreeAccordionItem.d2l-le-TreeAccordionItem-Selected > a.d2l-le-TreeAccordionItem-anchor:link::before,
.daylight #ContentModuleTree .d2l-le-TreeAccordionItem.d2l-le-TreeAccordionItem-Selected > a.d2l-le-TreeAccordionItem-anchor:visited::before,
.daylight #ContentModuleTree .d2l-le-TreeAccordionItem.d2l-le-TreeAccordionItem-Selected > a.d2l-le-TreeAccordionItem-anchor:hover::before,
.daylight #ContentModuleTree .d2l-le-TreeAccordionItem.d2l-le-TreeAccordionItem-Selected > a.d2l-le-TreeAccordionItem-anchor:focus::before {
    background: transparent;
}
.d2l_1_34_681 {
    border-bottom: 1px solid #555;
}
.daylight .d2l-grid[type=data] >:only-child > tr.d_ggl2 > td,
.daylight .d2l-grid[type=data] >:only-child > tr.d_ggl2 > th,
.daylight .d2l-grid[type=list] >:only-child > tr.d_ggl2 > td,
.daylight .d2l-grid[type=list] >:only-child > tr.d_ggl2 > th {
    background-color: #111;
}

.d2l-button[primary], .d2l-button[primary][disabled]:focus, .d2l-button[primary][disabled]:hover {
    background-color: #222;
    border-color: #555;
    color: #A3A3A3;
}

.daylight .d2l-tool-areas-list {
    border-color: #555;
}
}
