css:
@-moz-document domain("frystcraft.com"), domain("papocraft.com") {
/*Fuera TODOS los borders*/
	* {
		border-radius: 0 !important;
	}

.panel {
    background-color: #fff;
}

h3.panel-title.pull-left, div.page-header, a {
  	color: #207ce5;
}

div#wrap {
    background-image: url("http://i.imgur.com/lC80MFy.png");
    background-repeat: repeat;
}

	/*Agrandar logo y letras color blanco*/
	span.imageset.site_logo {
		margin-top: 22px;
		background-size: 600px;
		padding-top: 183px;
		padding-left: 600px;
        background-image: url("http://i.imgur.com/burXZZC.png");
	}
	/*POSTS*/
	div.col-md-3.col-xs-12.post-info.post-info-left {
		background-image: linear-gradient(to top, #cfd9df 0%, #e2ebf0 100%);
	}
	/*quitar imagen de la firma*/
	div.signature {
		background-image: none;
		background-color: #fff;
	}
	/*NAV*/
	nav.navbar.navbar-inverse.navbar-fixed-top.navbar-fix, .navbar-inverse {
		background-color: rgba(0, 0, 0, 0);
		border-color: rgba(0, 0, 0, 0);
	}
	nav.navbar.navbar-inverse.navbar-fixed-bottom.hidden-sm.hidden-xs {
		background-color: #4498DF;
		border-color: #4498DF;
	}
	a[title="Preguntas Frecuentes"] {
		background-image: linear-gradient(to top, #cfd9df 0%, #e2ebf0 100%);
	}

	.navbar-inverse .navbar-nav > li > a[href="/app.php/help/faq"] {
		color: black !important;
	}

	.navbar-inverse .navbar-nav > li > a:focus, .navbar-inverse .navbar-nav > li > a:hover {
        background-color: transparent;
    }

	li#quick-links.dropdown>a.dropdown-toggle {
		background: linear-gradient(to right, rgba(73, 155, 234, 1) 0%, rgba(32, 124, 229, 1) 100%);
	}
	a.btn.btn-primary, button.btn.btn-primary.btn-labeled.navbar-btn.dropdown-toggle, button.btn.btn-primary.btn-block {
		background: linear-gradient(to right, rgba(73, 155, 234, 1) 0%, rgba(32, 124, 229, 1) 100%);
	}
	div.panel.panel-primary.post-body {
		border-color: rgba(0, 0, 0, 0);
	}
	body#phpbb.nojs.notouch.section-viewtopic.ltr.pace-done>div#wrap>div#page-body.container>div.panel.panel-default>div.panel-body {
		background-image: linear-gradient(to top, #cfd9df 0%, #e2ebf0 100%);
	}
	.post-info {
		color: #333;
	}
	div.col-md-3.col-xs-12.post-info.post-info-left>div.row.no-margin-xs>dl.col-sm-12.hidden-xs>dt>hr {
		border-color: rgba(0, 0, 0, 0);
		color: rgba(0, 0, 0, 0);
		background-color: rgba(0, 0, 0, 0);
	}
	/*Quotes*/
	blockquote {
		background-color: #ddecfb;
		border-left: 5px solid #499BEA;
	}
}
