css:
@-moz-document domain('trello.com')
{
  .body-board-view
  {
    font: 12px tahoma;
  }

  .checklist-item
  {
      direction:rtl;
  }

  .checklist-item-checkbox
  {
    left:inherit;
    right:5px;
  }

  .checklist-item-details
  {
    padding-right:15px;
  }

  .comment-box-input
  {
    direction:rtl;
    font:12px tahoma;
  }

  .checklist-new-item.focus .checklist-new-item-text,
  .checklist-new-item.focus .checklist-new-item-text:focus,
  .checklist-new-item.focus .checklist-new-item-text:hover
  {
    font:12px tahoma;
  }

  .window-module
  {
      direction:rtl;
  }
  
  .board-tile-details-name
  {
  	font-size: 0.7pc;
    font-weight: normal;
    font-family: tahoma;
    text-align: right;
  }

  .boards-page-board-section-header-name
  {
    font-size: 0.8pc;
    font-family: tahoma;
    font-weight: normal;
  }

  .editable .current
  {
    padding-right:15px;
  }

.tab__tabLink__3C9rw, .tab__tabButton__37WIj
{
    font-weight:normal;
    font:13px tahoma;
}

.checklist-item-details .checklist-item-menu-wrapper
{
 left:0;
right:inherit;
}
}
