css:
@-moz-document domain("www.youtube.com") {
#guide-container {
    background-color: #b3b3b3;
}
a.guide-item.yt-uix-sessionlink.yt-valign.spf-link.guide-item-selected {
    background-color: #6182d6;
}
a.yt-ui-ellipsis.yt-ui-ellipsis-2.yt-uix-sessionlink.spf-link {
    background-color: #67718a;
    color: #ffffff;
}
a.yt-uix-sessionlink {
    color: #243a70;
}
div.appbar-content-hidable {
    background-color: #8c94a8;
}
div.clearfix.yt-base-gutter {
    background-color: #495573;
    color: #ffffff;
}
div.feed-item-container.browse-list-item-container.yt-section-hover-container.compact-shelf.shelf-item.branded-page-box.clearfix {
    background-color: #67718a;
    border-bottom-width: 1px;
    border-color: #000000;
    border-left-width: 1px;
    border-right-width: 1px;
    border-style: double;
    border-top-width: 1px;
}
div.home.clearfix.masthead-ad-expanded {
    background-color: #67718a;
}
div.yt-lockup-byline.yt-ui-ellipsis.yt-ui-ellipsis-2 {
    background-color: #67718a;
}
div.yt-lockup-dismissable {
    border-bottom-width: .1px;
    border-left-width: .1px;
    border-right-width: .1px;
    border-style: solid;
    border-top-width: .1px;
}
h2.epic-nav-item-heading {
    border-bottom-width: 2px;
    border-color: #ffffff;
    border-left-width: 0;
    border-right-width: 0;
    border-style: solid;
    border-top-width: 0;
    color: #ffffff;
}
span.thumb.guide-video-youtube-red-icon.yt-sprite {
    color: #6182d6;
}
span.yt-thumb-simple img {
    border-bottom-width: .1px;
    border-left-width: .1px;
    border-right-width: .1px;
    border-style: solid;
    border-top-width: .1px;
}
span.yt-uix-button-content {
    color: #c7c7c7;
}
span.yt-uix-button-icon.yt-uix-button-icon-appbar-guide.yt-sprite {
    color: #ffffff;
}
ytd-browse.style-scope.ytd-page-manager {
    background-color: #a3a3a3;
}
div.style-scope.ytd-masthead {
    background-color: #4c5f75;
}
#vat-notice {
    background-color: #8a8f9e;
}
#guide-links-secondary {
    background-color: #8a8f9e;
    color: #000000;
}
#guide-links-secondary a {
    color: #000000;
}
#guide-links-primary {
    background-color: #8a8f9e;
    display: none;
}
#guide-links-primary a {
    color: #ffffff;
}
ytd-guide-section-renderer.style-scope.ytd-guide-renderer {
    background-color: #8a8f9e;
}
yt-icon.guide-icon.style-scope.ytd-guide-entry-renderer {
    color: #000000;
}
ytd-guide-subscriptions-section-renderer.style-scope.ytd-guide-renderer {
    background-color: #8a8f9e;
}
a.yt-simple-endpoint.style-scope.yt-formatted-string {
    color: #1d1d70;
}
span.guide-entry-count.style-scope.ytd-guide-entry-renderer {
    color: #ffffff;
}
yt-icon.style-scope.ytd-button-renderer {
    color: #bd0000;
}
yt-icon.style-scope.ytd-topbar-menu-button-renderer {
    color: #000000;
}
yt-icon.style-scope.ytd-notification-topbar-button-renderer {
    color: #000000;
}
input.ytd-searchbox {
    background-color: #bababa;
    border-color: #bababa;
}
button.style-scope.ytd-searchbox {
    background-color: #bababa;
}
div.style-scope.ytd-searchbox {
    background-color: #bababa;
}
#copyright {
    background-color: #8a8f9e;
}
#copyright div {
    color: #000000;
}
}
