css:
@-moz-document domain(youtube.com) {
  .title.ytd-guide-entry-renderer {
      font-size: 1.2rem;
  }

  #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer {
      padding: 0px 0px 0px 10px;
      min-height: 10px;
  }

  ytd-guide-entry-renderer {
      --yt-guide-icon-spacing: 18px;
      min-height: 10px;
  }

  .guide-icon.ytd-guide-entry-renderer {
      margin-right: 10px;
  }

  ytd-guide-renderer.ytd-app {
      width: 170px;
  }

  app-drawer#guide {
      width: 180px;
      z-index: 2000;
  }

  #guide-section-title.ytd-guide-section-renderer, #guide-section-title.ytd-guide-subscriptions-section-renderer {
      padding: 2px 12px;
  }

  yt-img-shadow.ytd-guide-entry-renderer {
      margin-right: 4px;
  }

  .guide-entry-count.ytd-guide-entry-renderer {
      font-size: 1rem;
  }

  yt-img-shadow.ytd-guide-entry-renderer {
      margin: 2px 4px 2px 0px;
  }

  #page-manager.ytd-app {
      margin-left: 20px;
      margin-top: 40px;
  }

  ytd-thumbnail.ytd-video-renderer {
      width: 200px;
      height: 112px;
  }

  #video-title.ytd-video-renderer {
      font-size: 1.2rem;
  }

  #byline-container.ytd-video-meta-block {
      font-size: 1.1rem;
  }

  #metadata-line.ytd-video-meta-block {
      font-size: 1.1rem;
  }

  #description-text.ytd-video-renderer {
      font-size: 1.1rem;
  }

  #contents.ytd-shelf-renderer {
      margin-top: 10px;
  }

  #title.ytd-shelf-renderer {
      font-size: 1.4rem;
  }

  #image-container img {
      height: 20px;
      width: 20px;
  }

  .grid-subheader.ytd-shelf-renderer {
      margin-top: 10px;
  }

  ytd-expanded-shelf-contents-renderer {
      margin-bottom: 10px;
  }

  #container.ytd-masthead {
      height: 40px;
  }

  app-drawer {
      top: -136px;
  }

  ytd-video-primary-info-renderer {
      padding-top: 10px;
  }

  ytd-video-primary-info-renderer {
      --yt-button-icon-size: 30px;
  }

  #info.ytd-video-primary-info-renderer {
      height: 14px;
  }

  span.yt-view-count-renderer {
      font-size: 12px;
  }

  #sentiment.ytd-video-primary-info-renderer {
      margin-top: -10px;
  }

  ytd-video-secondary-info-renderer {
      margin-bottom: -18px;
      padding-top: 0px;
  }

  .description.ytd-video-secondary-info-renderer {
      font-size: 1.2rem;
      line-height: 1.6rem;
  }

  #avatar.ytd-video-owner-renderer {
      margin-right: 16px;
      margin-left: 8px;
      margin-top: 4px;
      width: 40px;
      height: 40px;
  }

  .more-button.ytd-video-secondary-info-renderer, .less-button.ytd-video-secondary-info-renderer {
      color: #2693e6;
      font-size: 1rem;
  }

  paper-button.ytd-subscribe-button-renderer {
      padding: 2px 8px;
      font-size: 1rem;
      font-weight: 700;
  }

  button[is="paper-icon-button-light"].ytd-toggle-button-renderer {
      width: 32px;
      height: 32px;
  }

  .count-text.ytd-comments-header-renderer {
      font-size: 1.4rem;
  }

  #icon-label.yt-dropdown-menu {
      font-size: 1.2rem;
  }

  #header.ytd-comment-renderer {
      margin-bottom: 0px;
  }

  #content-text.ytd-comment-renderer {
      font-size: 1.2rem;
  }

  #reply-button.ytd-comment-action-buttons-renderer, #vote-count.ytd-comment-action-buttons-renderer {
      font-size: 1.2rem;
  }

  button[is="paper-icon-button-light"].ytd-toggle-button-renderer {
      width: 28px;
      height: 28px;
  }

  ytd-comment-thread-renderer {
      margin-bottom: 4px;
  }

  ytd-button-renderer {
      font-size: 1rem;
  }

  ytd-button-renderer #button.ytd-button-renderer {
      padding: 6px 10px;
  }

  .gsfs {
      font-size: 1.2rem;
  }

  ytd-searchbox  input {
      font-size: 14px;
  }

  .sbsb_a {
      padding: 0px;
  }

  .sbsb_c {
      padding: 2px 24px 2px 6px;
      line-height: 18px;
  }

  .sbdd_a {
      top: 41px;
  }

  h3.ytd-grid-video-renderer {
      margin: 2px 0 2px;
  }

  #byline-container.ytd-grid-video-renderer {
      font-size: 1.1rem;
  }

  #metadata-line.ytd-grid-video-renderer {
      font-size: 1.1rem;
      line-height: 1.4rem;
  }

  #items.ytd-grid-renderer > ytd-grid-video-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-channel-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-playlist-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-movie-playlist-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-show-renderer.ytd-grid-renderer {
      margin-bottom: 12px;
  }

  ytd-toggle-button-renderer.force-icon-button, ytd-button-renderer.force-icon-button {
      font-size: 1rem;
  }

  button[is="paper-icon-button-light"].ytd-toggle-button-renderer {
      line-height: 0px;
  }

  ytd-button-renderer #button.ytd-button-renderer {
      padding: 6px;
  }

  ytd-video-primary-info-renderer.ytd-watch {
      min-height: 40px;
  }
}
