css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.com"), domain("eldarya.com.br") {

/*Nevra en familier*/  
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://68.media.tumblr.com/93fb87d96be2e174c0f7c030bfe6ba06/tumblr_ovo6a0JWpf1stb5xvo1_500.jpg") -15px top no-repeat !important;
  background-size : auto 60px !important ;
  }

  #pet-portrait {
    background : url("https://68.media.tumblr.com/93fb87d96be2e174c0f7c030bfe6ba06/tumblr_ovo6a0JWpf1stb5xvo1_500.jpg") center no-repeat !important ;
    background-size : auto 350px !important ;
    height : 350px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Ezarel, Nevra, Valkyon" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Ainda não decidiu qual dos tres? pegue todos não espere mais!" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Elfo, Vampiro, Faelien " !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("https://68.media.tumblr.com/401339d80ae0381600ac35b23744df08/tumblr_oviha8aci51stb5xvo1_540.png") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Alimento" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Ezarel sur la page du profil*/

  #player-display-pet {
    background : url("https://68.media.tumblr.com/43f341f5ffb3f39f924ac2b91e868b06/tumblr_ovo5stRNTJ1stb5xvo1_1280.png") no-repeat ;
    background-size : auto 100% ;
    height : 540px !important ;
    width : 790px !important ;
  }

  #player-display-pet > img {display : none !important ;}
}
