css:
@-moz-document domain("gw2raidar.com") {
html {
	background-color: #222;
}
a, a:hover, a:visited {
	color: /*[[color_highlight]]*/;
	text-decoration: underline;
}
.uk-flex-1, h1, h2, h3, h4 {
	color: #FFF;
	font-family: "/*[[font]]*/", "Trebuchet MS", Helvetica, sans-serif;
}
h1, h2, h3, h4 {
	font-family: "/*[[font_heading]]*/", "Palatino Linotype", "Book Antiqua", Palatino, serif;
}
.uk-background-primary {
	background-color: #222;
	color: #FFF;
}
.uk-background-muted {
	background-color: #222;
}
.uk-navbar {
	border-radius: 4px;
}
.uk-navbar-container:not(.uk-navbar-transparent) {
	background-color: /*[[color]]*/;
}
.uk-navbar-nav>li>a {
	color: #FFF;
}
.uk-navbar-nav>li>a:hover, .uk-navbar-nav>li.uk-active>a:hover {
	color: /*[[color_highlight]]*/;
}
.uk-nav {
	background-color: #303030;
}
.uk-navbar-nav .uk-open {
	background-color: #303030;
	color: /*[[color_active]]*/;
}
.uk-navbar-nav>li.uk-active>a {
	background-color: /*[[color_active]]*/;
	color: #FFF;
}
.uk-navbar-nav>li>a.uk-open {
	background-color: /*[[color_active]]*/;
	color: /*[[color_highlight]]*/;
}
.uk-navbar-dropdown {
	background-color: #303030;
	padding: 25px 0 25px 0;
}
.uk-navbar-dropdown-nav>li>a {
	background-color: #303030;
	color: #FFF;
	padding: 5px 25px 5px 25px;
}
.uk-navbar-dropdown-nav>li>a:hover {
	background-color: /*[[color]]*/;
	color: #FFF;
}
.uk-navbar-dropdown-nav>li.uk-active>a {
	color: /*[[color_highlight]]*/;
}
.uk-text-success {
	color: /*[[color_highlight]]*/!important;
}
.uk-text-danger, .uk-flex-1>p>strong>font {
	color: #E74C3C;
}
table.row-header {
	background-color: #222;
}
.uk-table th {
	border-bottom: 1px solid #464545;
	color: #FFF;
	font-weight: bold;
}
.uk-table :not(:last-child)>td {
	border-bottom: 1px solid #464545;
}
.uk-table .uk-dropdown {
	background-color: #303030;
	color: #FFF;
}
.uk-table .uk-text-primary>div:not(.uk-dropdown) {
	color: #000;
	font-weight: bold;
}
.uk-tooltip {
	background-color: #000;
}
.uk-dropdown {
	background-color: #303030;
	color: #FFF;
}
.uk-grid-small.uk-grid,
.uk-flex.uk-flex-between.uk-padding-small>div {
	color: #000;	
}
.uk-table-striped tbody tr:nth-of-type(odd),
.uk-table-striped>tr:nth-of-type(odd) {
	background-color: #3D3D3D;
}
.uk-button-group>.uk-button {
	border: 1px solid #FFF;
}
.uk-button {
	color: #FFF;
	background-color: /*[[color]]*/;
}
.uk-button:hover {
	color: /*[[color_highlight]]*/;
	background-color: /*[[color_active]]*/;
}
.uk-button-primary:focus, .uk-button-primary:hover {
	background-color: /*[[color_active]]*/;
}
.uk-radio:checked {
	background-color: /*[[color_highlight]]*/;
}
.uk-button {
	color: #FFF;
	background-color: /*[[color]]*/;
	border: 1px solid transparent;
	border-radius: 4px;
}
.uk-button:focus, .uk-button:hover {
	background-color: /*[[color_active]]*/;
	border: 1px solid /*[[color]]*/;
	color: #FFF
}
.uk-button-group>.uk-button {
	border: 1px solid /*[[color]]*/;
	border-radius: 0;
	color: #FFF;
}
.uk-button-group>.uk-button:first-child {
	border-top-left-radius: 4px;
	border-bottom-left-radius: 4px;
}
.uk-button-group>.uk-button:last-child {
	border-top-right-radius: 4px;
	border-bottom-right-radius: 4px;
}
.uk-button-group>.uk-button-primary {
	background-color: /*[[color_active]]*/;
	border: 1px solid /*[[color]]*/;
}
.uk-button-group>.uk-button-primary:focus, .uk-button-group>.uk-button-primary:hover {
	background-color: /*[[color_active]]*/;
}
.uk-radio:checked {
	background-color: /*[[color_highlight]]*/;
}
.uk-pagination {
	position: relative;
	left: 10px;
}
.uk-pagination>li {
	background-color: /*[[color_page]]*/;
	color: #FFF;
}
.uk-pagination>li:first-child {
	border-radius: 4px 0 0 4px;
}
.uk-pagination>li:last-child {
	border-radius: 0 4px 4px 0;
}
.uk-pagination>li>a, .uk-pagination>li>a:hover {
	color: #FFF;
	text-decoration: none;
}
.uk-pagination>.uk-active, .uk-pagination>li:hover {
	background-color: /*[[color_page_active]]*/;
}
.uk-pagination>.uk-disabled {
	background-color: /*[[color_page_disabled]]*/;
	color: #FFF;
}
.uk-pagination>.uk-active>*, .uk-pagination>.uk-disabled>*, .uk-pagination>*>:focus {
	color: #FFF;
}
.uk-pagination>*>* {
	position: relative;
	left: -10px;
}
footer .uk-nav {
	background-color: #222;
}
}

@-moz-document regexp(".*gw2raidar.com/encounter/.*") {
.uk-table th {
	height: 26px;
}
.uk-table td {
	color: #FFF;
}
.uk-table.row-header td {
	color: #000;
}
.uk-table span {
	background-color: rgba(0, 0, 0, 0.3);
	border-radius: 4px;
	color: #FFF;
	font-family: "/*[[font_numbers]]*/", "Trebuchet MS", Helvetica, sans-serif;
	font-size: small;
	padding: 0 1px;
	text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000;
}
.uk-table span.expected {
	background-color: rgba(0, 0, 0, 0.2);
	font-size: x-small;
}
}

@-moz-document regexp(".*gw2raidar.com/global_stats.*") {
.uk-table th {
	background-color: #375A7F;
	color: #FFF;
}
.uk-table tr, tbody {
	background-color: #E3E8ED;
	color: #000;
	font-size: 98%;
}
.uk-table-striped tbody tr:nth-of-type(odd),
.uk-table-striped>tr:nth-of-type(odd), tbody {
	background-color: #C8CCD1;
}
}
