css:
 @-moz-document domain("ezcapechat.com") {
     body {
         position: fixed;
         height: 100%;
          width: 100%;
        background-image: none;
         background: #222222 !important;
     }
     #container {
         display: grid;
         grid-gap:unset !important;
         grid-template-columns: 100%;
         grid-template-rows: 95% 5%;
         width: 100%;
         height: 100%;
         border: none !important;
        margin: unset !important;
         padding: unset!important;
       background: #222222;
     }
     #hdr_cnt {
         
         position:sticky;
           grid-row: 2 !important;
         height:100% !important;
         width: 100% !important;
         margin: unset !important;
         padding: unset!important;
         border: none !important;
     }
     #hdr {
         background: #6db3f2 !important;
         ;
         /* Old browsers */
         
         background: -moz-linear-gradient(top, #6db3f2 0%, #54a3ee 50%, #3690f0 51%, #1e69de 100%) !important;
         ;
         /* FF3.6-15 */
         
         background: -webkit-linear-gradient(top, #6db3f2 0%, #54a3ee 50%, #3690f0 51%, #1e69de 100%)!important;
         ;
         /* Chrome10-25,Safari5.1-6 */
         
         background: linear-gradient(to bottom, #6db3f2 0%, #54a3ee 50%, #3690f0 51%, #1e69de 100%)!important;
         ;
         /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
         
         display: grid;
         grid-template-columns: 20%;
         grid-template-rows: 100%;
         height: 100% !important;
         width: 100% !important;
         margin: unset !important;
         padding: unset!important;
     }
     #rmsnav,#room_header,#ftr,#swf_version_link,#room_footer,#frnt_mascot {
         display: none !important;
     }
     #hdr a{
         background: transparent !important;
         align-self: center;
         grid-column: 1;
         vertical-align: middle;
         height: 100% !important;
         width: 40px !important;
         margin: unset !important;
         padding: unset!important;
         background-image: url('data:image/png;base64,') !important;
          background-size: 40px 40px !important;
    background-repeat: no-repeat !important;
      bottom:-10% !important;
    
         font-size: 1.5vh;
         color: grey;
     }
     #logo{
        display: none !important;
     }
   
     
     
     
     
     #btntop {
         grid-column: 5;
         background: transparent !important;
         padding: unset !important;
         margin: unset !important;
         width: 100% !important;
         z-index: 10 !important;
     }
     #lgnsts {
         background: transparent !important;
         z-index: 10 !important;
         height: 100% !important;
         padding: unset !important;
         margin: unset !important;
         width: 100% !important;
     }
     #lgnsts p {
         background: transparent !important;
         z-index: 10 !important;
         display: inline-grid;
         grid-template-columns: 50%;
         grid-template-rows: 100%;
         height: 100% !important;
         width: 100% !important;
     }
     #lgnsts p a {
         z-index: 10 !important;
         grid-column: 1;
         grid-row: 1;
         font-size: 5vh !important;
         text-align: center !important;
         width: 100% !important;
         line-height: 1em !important;
         height: 100% !important;
         text-shadow: 3px 3px 1px #000000;
         vertical-align: middle !important;
         display: block;
         background-image: none !important;
     }
     #lgnsts p a:after {
         display: none;
     }
     #lgnsts p a:hover {} #lgnsts p span {
         background: transparent !important;
         grid-column: 2;
         grid-row: 1;
      
         padding: unset !important;
         margin: unset !important;
         width: 100% !important;
         height: 100% !important;
     }
   
      #swf_chat_login_mask {
        height: 100% !important;
        width: 100% !important;
         padding: unset !important;
         margin: unset !important;
        background: linear-gradient(
  to bottom,
  #5d9634,
  #5d9634 50%,
  #538c2b 50%,
  #538c2b
);
        
        
/* The rectangle in which to repeat. 
   It can be fully wide in this case */
background-size: 100% 20px;
     }
   
  #login_box, #swf_chat_login,#swf_login_account ul,#swf_login_guest ul{
  background:rgba(255,255,255,0.5);
     border: none !important;
     color: black;
     
   }
     #lgnsts p span a {
         background: transparent !important;
         display: inline-block;
         text-align: center !important;
     }
    
     #content {
                  overflow-y: scroll;
    overflow-x: hidden;

         grid-row-end: 2;
         width: 100% !important;
         height: 100% !important;
         border: none !important;
       padding: unset !important;
         margin: unset !important;
         
     }
     #swf_chat_normal,#kchatswf{
       width: 100% !important;;
         height: 100% !important;;
         border: none !important;
       padding: unset !important;
         margin: unset !important;
       border:none !important;
        
        
     }
   div[class*="section"]{
     
     background-color: grey!important;
     
   }
 }
