css:
:root {
    --coloursync:#34495E;
    --blueVKcolor:#1D3853;
    --darkGrayColor:#252525;
    --greenFontColor:#9DFCD0;
}

.feed_row {
 	background-color: rgba(0,0,0,0) !important;   
}

body {
    background-color: var(--coloursync) !important;  
}

.chat_tab_typing_wrap {
 	background-color: var(--blueVKcolor) !important;   
}

/********** AUDIO PLAYER ***********/
.audio_page_player.audio_page_player_fixed {
 	background-color: var(--blueVKcolor) !important;
}

.audio_row .audio_row_inner,
.audio_row .audio_row_inner:hover {
    background-color: rgba(3, 3, 3, 3) !important;
}

.sf-tooltip {
    background-color: black !important;
 	color: white !important;   
}

.eltt .audio_page_player { 
	border-bottom: 0px !important; 
    height: 88px !important;
    background-color: var(--darkGrayColor) !important;
}

.audio_row.audio_row__current .audio_row_content {
	background-color: rgba(0, 0, 0, 0.5) !important;
}

.audio_row:hover:not(.audio_row__current) .audio_row_content {
	background-color: rgba(0, 0, 0, 0) !important;
}

div.audio_row.audio_row_with_cover {
 	background-color: transparent !important;   
}

._audio_row__actions {
    filter: brightness(0) invert(1) !Important;
}

.audio_page_player .audio_page_player_add .btn_icon, .audio_page_player .audio_player_btn_added .btn_icon,
.audio_page_player .audio_page_player_shuffle .btn_icon,
.audio_page_player .audio_page_player_repeat .btn_icon,
.audio_page_player .audio_page_player_recoms .btn_icon,
.audio_page_player .audio_page_player_status.audio_page_player_btn_enabled .btn_icon,
.audio_page_player .audio_page_player_share .btn_icon,
.top_nav_btn#top_audio .top_nav_btn_icon,
.top_nav_btn#top_notify_btn .top_nav_btn_icon {
	filter: brightness(0) invert(1) !Important;
}


/* TOP NOTIFICATIONS */
.tt_w.top_notify_tt {
    background-color: #1A1A1A !important;
    border-color: black !important;
}

.audio_page_player .audio_page_player__cover {
	width: 88px !important;
    height: 88px !important;
}

div.blog_about_company_stats,
div.blog_about_press {
 	background-color: rgba(0, 0, 0, 0.5) !important;   
}

ul.ui_tabs.clear_fix.blog_about_tabs.page_header_wrap::before {
 	display: none !important;   
}



/* CUSTOM BACGKROUND */
body[onresize],
#page_header_cont .back:after,
#page_header_cont .back:before,
#page_header_cont:before,
#page_layout:before {
    background: url(https://img-fotki.yandex.ru/get/4409/110173670.1/0_109426_a9f217c0_orig) top center fixed !important;
}

#page_body {
 	background-color: transparent !important;   
}

body[onresize]{
    background-size: cover !important;
    background-repeat: no-repeat !important
}

.page_block {
    position:relative;
    background-color: #FFF;
    border-radius:0px !important;
    box-shadow:0 1px 0 0 #FFF,0 0 0 1px #FFF;
    margin:0px 0 0;
    border:solid #ebedf0;
    border-width:1px 0 0
}

body{font-family:Segoe UI,Open Sans,Helvetica Neue,sans-serif; }

#top_profile_menu,
#top_notify_wrap,
.eltt,
.chat_onl_inner,
.ms_items_more,
.emoji_tt_wrap,
.tt_default,
.tt_default_right,
.ui_actions_menu,
.tt_w.like_tt,
.tt_w.poll_tt,
.page_status_editor .editor {
    border:0px solid #c5d0db;
    border-radius:0px;box-shadow:0 0 23px 12px rgba(0,0,0,0.1)
}

