css:
@-moz-document domain("discord.js.org") {

#home {
    background: #2A2C31
}

#home h2 {
    color: white
}

#home p {
    color: white;
}


#home code {
    background: #2F3136;
    color: #839496
}
#docs-viewer {
    background: #2A2C31;
    color: #A1A3A2;
    padding: 16px;
}
#file-viewer :not(pre)>code {
    background: #2F3136;
}
#docs-viewer pre {
    color: #839496;
    background: #2F3136;
}
.hljs {
    background: #2F3136;
}
.hljs, .hljs-subst {
    color: #839496;
}
#docs-sidebar ul li {
    color: #6B82D3;
}
#docs-sidebar ul ul li {
    color: #7C7C7C;
}
#docs-sidebar {
    border-right: 1px solid rgba(255, 255, 255, 0);
}
#docs-sidebar .router-link-active {
    background: #42464D;
}
#docs-sidebar ul ul li a:hover {
    border-color: #36393F;
    color: white;
    background: #36393F
}
#docs-navbar input, #docs-navbar select {
    background: #333;
    color: #fff;
    border: 1px solid #6B82D3;
}
header {
    background: #6B82D3;
}

#class-overview ul li a {
    padding-left: 6px;
    border-left: 2px solid #6B82D3;
}
#class-viewer h1 {
    color: #6B82D3;
}
#class-overview .title {
    color: #6B82D3;
}
#class-overview ul li a:hover {
    color: #2196f3;
    background: #42464D;
}
#class-viewer .class-item-details {
    margin-top: 8px;
    padding: 6px 0 6px 8px;
    border-left: 2px solid #6B82D3;
}
#class-viewer :not(pre)>code {
    background: #2F3136;
}
.param-table {
    border: 1px solid #2F3136;
}
.param-table tr {
    background: #2F3136;
}
.param-table tr:nth-child(odd) {
    background: #2F3136;
}
#docs-viewer div.warn {
    background: #2F3136;
    border-color: red;
    border-left-color: red;
}
.method-param {
    color: grey;
}
#typedef-viewer :not(pre)>code {
    background: #2F3136;
    border-radius: 2px;
}
#docs-viewer div.info {
    background: #2F3136;
    border-color: #6B82D3;
    border-left-color: #6B82D3;
}
#docs-body {
    background: #2F3136;
}
 #home #jumbotron {
    background-color: #6B82D3;
    background-image: linear-gradient(top,#6B82D3,#6B82D3);
}
 .hljs-type, .hljs-string, .hljs-number, .hljs-selector-id, .hljs-selector-class, .hljs-quote, .hljs-template-tag, .hljs-deletion {
    color: #007C97;
}
  .param-table th {
    background: #2F3136;
    color: #fff;
    border-bottom: solid 3px #2B2E32;
}
.badge {
    color: #fff;
    background: #6B82D3;
}
.small-badge {
    background: #6B82D3;
}
.badge.warn {
    background: #F04747;
}
.small-badge.warn {
    background: #F04747;
}
header .container>a:hover {
    background: #333333;
}
header .container>a {
    background: #333333;
}
}
