css:
@-moz-document domain("kissanime.com.ru") {
 #topHolderBox {
     color: #b31a1a;
     background: #2f2f2f;
 }
 html,
 body {
     background: url("https://i.imgur.com/h2oVENM.gif");
 }
 #navsubbar p {
     background-color: #b31a1a;
 }
 #navsubbar {
     background: url(https://i.imgur.com/obYK6kf.gif) repeat-x;
     color: #b31a1a;
 }
 #topHolderBox a {
     color: rgb(255, 0, 0);
 }
 #switch {
     color: #b31a1a;
 }
 #currentTab {
     display: none !important;
 }
 #navbar {
     color: black;
 }
 a {
     color: red;
 }
 .barContent div div div div {
     color: #b31a1a;
 }
 #navcontainer a:link {
     float: left;
     background: url(https://i.imgur.com/1QrfHNs.gif) no-repeat left top;
     background-position: 0% -27px;
 }
 #head h1 {
     background: transparent url(https://i.imgur.com/SA7P3rj.png) no-repeat !important;
 }
 bigChar {
     color: #ca0705;
 }
 .barTitle {
     background: url(https://i.imgur.com/ayaAii2.png) repeat-x;
 }

#search input.button  {
	height: 0 !important;
	width: 0 !important;
	padding-left: 90px !important;
	padding-top: 33px !important;
	background: url(https://i.imgur.com/aKxvK9K.png) no-repeat !important;
}
#btnNext {
	height: 0 !important;
	width: 0 !important;
	padding-left: 50px !important;
	padding-top: 25px !important;
	background: url(https://i.imgur.com/Dd40js5.png) no-repeat !important;
}
#btnPrevious { height: 0 !important;
	width: 0 !important;
	padding-left: 27px !important;
	padding-top: 25px !important;
	background: url(https://i.imgur.com/ZVHp3ic.png) no-repeat !important;
}
.specialButton {
background: none !important
}
.specialButton { border: none !important}
.specialButton { box-shadow: none !important}
.specialButton { text-shadow: 2px 2px 0px rgb(109,1,1) }
.specialButton {color: rgb(202,0,0) }
select {
    background-color: rgba(0,0,0,0.14);
    color: #b70404;
    border: 1px solid rgba(0,0,0,0);
}
}
