css:
 @-moz-document domain(www.youtube.com) {
html, body, ytd-app, div#guide-inner-content, ytd-page-manager > *.ytd-page-manager {
    background: #23232d!important;
}

.title.ytd-video-primary-info-renderer, a.yt-simple-endpoint.yt-formatted-string, .more-button.ytd-video-secondary-info-renderer, #content-text.ytd-comment-renderer, #text, #vote-count, ytd-toggle-button-renderer #button.ytd-toggle-button-renderer, ytd-button-renderer #button.ytd-button-renderer, #button.ytd-menu-renderer yt-icon.ytd-menu-renderer, ytd-topbar-menu-button-renderer #button.ytd-topbar-menu-button-renderer, ytd-notification-topbar-button-renderer #button.ytd-notification-topbar-button-renderer, #icon-label.yt-dropdown-menu, #simplebox-placeholder.ytd-comment-simplebox-renderer, #video-title, #content, #description, .ytd-comment-renderer, yt-formatted-string.less-button.style-scope.ytd-video-secondary-info-renderer, .ytd-video-secondary-info-renderer, span.yt-view-count-renderer, #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer, paper-item.ytd-compact-link-renderer, ytd-toggle-theme-compact-link-renderer, #title.ytd-shelf-renderer, #toggle.ytd-grid-renderer, paper-button, yt-formatted-string.ytd-comments-header-renderer, .input-content.paper-input-container  textarea, .deemphasize.yt-formatted-string, #scroller.yt-confirm-dialog-renderer, #title.ytd-vertical-channel-section-renderer, #title.yt-confirm-dialog-renderer, .index-message.ytd-playlist-panel-renderer, .publisher.ytd-playlist-panel-renderer:not(:empty) + .index-message.ytd-playlist-panel-renderer::before, #byline.ytd-playlist-panel-video-renderer {
    color: #FFF!important;
}
yt-icon {
    fill: #FFF!important;
}

ytd-searchbox[mode=legacy] #container.ytd-searchbox input.ytd-searchbox {
    color: #000;
}

#byline.ytd-video-meta-block, #metadata-line.ytd-video-meta-block, #metadata-line.ytd-grid-video-renderer {
    color: #aaaaae;
}
#description-text.ytd-video-renderer {
    color: hsl(0, 0%, 53.3%);
}


*:not([style-scope]):not(.style-scope) {
    --yt-primary-color: hsl(213, 94%, 41%);
    --yt-primary-text-color: #FFF;
    --yt-metadata-color: hsla(0, 0%, 100%, 0.6);
    
}

ytd-masthead#masthead, #yt-masthead-container {
    background: #2c2c36;
    border-bottom: 1px solid #2c2c36;
}
ytd-compact-radio-renderer.style-scope.ytd-watch-next-secondary-results-renderer, ytd-compact-video-renderer.style-scope.ytd-watch-next-secondary-results-renderer, ytd-compact-playlist-renderer.ytd-watch-next-secondary-results-renderer {
    background: #2c2c36;
    margin-bottom: 10px;
    padding: 5px!important;
    border-radius: 3px;
    border: 1px dashed #ceced8;
}
ytd-compact-video-renderer.ytd-compact-autoplay-renderer{
    border: 0px dashed #ceced8!important;
    padding-bottom: 7px;
}
ytd-compact-radio-renderer.style-scope.ytd-watch-next-secondary-results-renderer, ytd-compact-video-renderer.style-scope.ytd-watch-next-secondary-results-renderer, ytd-compact-autoplay-renderer{
    box-shadow: 2px 2px 5px rgba(0, 0, 0, 0.38);
}

div#container.ytd-video-primary-info-renderer {
    /* background: #000; */
    /* padding: 5px; */
    /* border: 1px dashed #fff; */
}

ytd-video-primary-info-renderer {
    border-bottom: 0px dotted #FFF;
}
ytd-video-secondary-info-renderer {
   padding: 10px;
    /* background: url(http://i.imgur.com/YwKNi6N.png) repeat, url(http://i.imgur.com/YwKNi6N.png) repeat; */
    border: 2px dotted #FFF;
    border-radius: 5px;
}


.date.ytd-video-secondary-info-renderer {
    color: rgba(255, 255, 255, 0.43);
}
#like-button.ytd-comment-action-buttons-renderer {
    color: #01ff44!important;
}

#dislike-button.ytd-comment-action-buttons-renderer {
    color: #ff0000!important;
}

#sentiment.ytd-video-primary-info-renderer {
    margin-top: -4px;
}

div#sections {
    background: #2c2c36;
}
ytd-compact-autoplay-renderer {
    display: block;
    background: #2c2c36;
    padding: 10px 10px 0px 10px;
    border-radius: 5px;
    border: 1px dashed #ceced8;
    margin-bottom: 10px!important;
}

ytd-comment-thread-renderer {
    display: block;
    margin-bottom: 16px;
    border: 1px dashed #7e7e86;
    padding: 10px 10px 0 10px;
    border-radius: 5px;
}

ytd-comment-renderer.ytd-comment-replies-renderer {
    background: rgb(44, 44, 54);
    border: 0px dashed #7e7e86;
    padding: 10px 10px 0 10px;
    border-radius: 5px;
    margin-bottom: 11px!important;
}

/*----Màu nền cover chanel cá nhân----*/
#channel-header.ytd-c4-tabbed-header-renderer {
    background-color: #2c2c31;
    padding: 0!important;
}

/*--------menu dưới cover chanel-----*/
#tabs-inner-container.ytd-c4-tabbed-header-renderer {
    background-color: rgba(0, 0, 0, 0.91);
}

/*----menu chọn playlist----*/
#header.ytd-add-to-playlist-renderer, paper-item.ytd-compact-link-renderer {
    background: rgba(0, 0, 0, 0.83);
}
#playlists.ytd-add-to-playlist-renderer {
    background: rgba(0, 0, 0, 0.79);
}

/*---mở rộng menu top----*/
#endpoint.ytd-compact-link-renderer {
    background: #FFF;
}


/*--sắp xếp hiển thị comment---*/
paper-menu {
    background: #222229;
}


/*--background video xem sau---*/
ytd-playlist-sidebar-renderer, ytd-browse[page-subtype="playlist"] ytd-two-column-browse-results-renderer.ytd-browse, ytd-browse[page-subtype="playlist"] #alerts.ytd-browse {
    background-color: #23232d;
}

/*---menu mở rộng----------------------*/
app-drawer.ytd-app:not([persistent]) #header.ytd-app {
    border-bottom: 0px solid var(--yt-border-color);
    background: #2c2c36;
}
#sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child) {
    border-bottom: 0px solid #000;
}
ytd-app[guide-hovered] #guide-inner-content.ytd-app::-webkit-scrollbar-track
{
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
    background-color: transparent;
}

ytd-app[guide-hovered] #guide-inner-content.ytd-app::-webkit-scrollbar
{
    width: 6px;
    background-color: #212127;
}

ytd-app[guide-hovered] #guide-inner-content.ytd-app::-webkit-scrollbar-thumb
{
    background-color: #000000;
}

/*---Khung profile mở rộng------------------------------------------*/
ytd-active-account-header-renderer {
    background-color: #2b2b2b;
}
#sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer {
    padding: 0px 0;
}
div.style-scope yt-multi-page-menu-section-renderer {
    background: #2b2b2b;
}
ytd-account-settings {
    background-color: #2b2b2b;
}

/*---nút next xem list video ngoài trang chủ----*/
.arrow.yt-horizontal-list-renderer {
    background-color: #2c2c36;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 1), 0 0 4px rgba(0, 0, 0, 0.2);
}


/*---List video ngoài trang chủ---*/
ytd-item-section-renderer.style-scope.ytd-section-list-renderer {
    background: #1a1a21;
    margin: 15px 0 10px 0;
    padding: 20px;
    border: 1px solid #141419!important;
    box-shadow: 1px 1px 7px rgba(0, 0, 0, 0.48);
    border-radius: 5px;
}
.grid-subheader.ytd-shelf-renderer {
    margin: 0px;
}






/*---list menu lịch sử xem---*/
div#secondary {
    margin-left: 15px;
}

/*---thông tin xem sau---*/
ytd-playlist-sidebar-renderer{
    overflow: hidden;
}

#content.ytd-playlist-video-renderer {
    border-bottom: 1px dashed rgba(255, 255, 255, 0.59);
}

[is=yt-endpoint] {
    color: #FFF;
}
ytd-playlist-video-list-renderer, ytd-playlist-video-list-renderer.style-scope.ytd-item-section-renderer{
margin: 8px 0 0 0;
}

/*---khung nhập lời bình luận---*/
#placeholder-area.ytd-comment-simplebox-renderer, .input-content.style-scope.paper-input-container {
    padding: 5px 10px;
    border: 1px dashed #ffffff;
    border-radius: 5px;
}

.underline.style-scope.paper-input-container {
    display: none;
}


/*---nút đăng ký kênh---*/
paper-button[subscribed].ytd-subscribe-button-renderer {
    background-color: hsl(127, 89%, 14%);
}

ytd-subscribe-button-renderer[button-style="COMPACT_GRAY"] paper-button.ytd-subscribe-button-renderer {
    background: #ff0000;
}

/*---bảng thông báo hủy đăng ký kênh---*/
yt-confirm-dialog-renderer[dialog][dialog][dialog] {
    background: #000;
}

/*---nút xem thêm video---*/
paper-button.yt-next-continuation {
    background: #5f5f65;
}

/*---đổi màu logo---*/
#logo-icon.ytd-topbar-logo-renderer #youtube-paths path {
    fill: var(--yt-swatch-logo-override, #FFF);
}

/*---khung tìm kiếm đầu trang---*/
#search-icon-legacy.ytd-searchbox {
    background-color: #2c2c36;
}

/*---list video liked---*/
.header.ytd-playlist-panel-renderer, .playlist-items.ytd-playlist-panel-renderer {
    background-color: #2c2c36;
}





/*----------------UPDATE 10/10/2017-----------------------------------*/



#appbar-guide-menu, .guide-flyout{
    background: #2c2c36;
}
.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    border-right: 0px solid #e8e8e8;
    box-shadow: none;
}

/*----list video lịch sử xem----*/
.yt-ui-ellipsis {
    background-color: transparent;
}
.yt-card {
    background: transparent;
    box-shadow: 0 0px 0px rgba(0,0,0,.1);
}

.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
    color: #ffffff;
}



}
