css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ft.com") {
#body-content {
margin-top:0em !important;
}

.index-detail {
clear:left !important;
display:inline !important;
float:left !important;
margin:0 !important;
padding-bottom:0.3em !important;
width:59em !important;
}

.index-detail h2, .index-detail p.long-lead {
font-family:Georgia !important;
font-size:1.8em !important;
font-weight:normal !important;
line-height:1.13 !important;
}

.from {
font-size:1.2em !important;
margin-bottom:0.3em !important;
white-space:nowrap;
}

.from a {
font-weight:normal !important;
margin:0 0.2em 0 0.3em !important;
text-transform:uppercase;
}

.index-small h2, .index-small p.long-lead {
font-family:Georgia !important;
font-size:1.4em !important;
font-weight:bold !important;
line-height:1.2 !important;
}

.video p a {
color:#000000 !important;
font-family:georgia !important;
font-size:1.5em !important;
line-height:1.2 !important;
}

body #body-content #body-wrapper #rail-content #right-rail .rightRailComponent {
border-top:0.4em solid #A7A59B !important;
clear:both !important;
float:left !important;
margin-bottom:0 !important;
width:34.2em !important;
}
.tools-component {
border-top:0.4em solid #A7A59B !important;
float:left !important;
text-align:left !important;
width:49% !important;
}
}
