/* -=< Darker Engadget by Zigboom Designs >=- */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("engadget.com") {
body, .o-hit .o-hit__link, .th-title, .th-topic, p, .c-gray-1, .c-gray-2, .c-gray-3, .c-black, #atomic .C\(engadgetFontBlack\) {
    color: #aaa !important;
  } 
    

.confab .confab-dialog .confab-button-wrapper button.cancel, .th-base, .bg-gray-11, .no-touchevents .bg-gray-11\:hvr:hover, 
    .bg-gray-11\@tl\+, .no-touchevents .bg-gray-11\@tl\+\:hvr:hover, .bg-white, .o-backdrop.bg-gray-1, .th-reverse, .franchise-bg, #atomic .Bgc\(engadgetGhostWhite\), #atomic .Bdc\(engadgetGutter\), #atomic .C\(engadgetSteelGray\), #atomic .W\(100vw\), #atomic .C\(engadgetBlack\), #atomic .Bdbc\(engadgetGutter\), #atomic .Bgc\(\#fff\) {
    color: #aaa !important;
    background-color: #181818;
    border-color: #333333;
}
    
    .bg-white\@tp\+ {
        background-color: rgba(0,0,0,0.75) !important;
    }
  
.confab .confab-dialog .confab-button-wrapper button.cancel .o-hit:hover .th-underline, .confab .confab-dialog .confab-button-wrapper button.cancel a:hover .th-underline, .th-base .o-hit:hover .th-underline, .th-base a:hover .th-underline {
  border-bottom: 0px !important;
}

.confab .confab-dialog .confab-button-wrapper .th-base button, .confab .confab-dialog .confab-button-wrapper button.cancel .confab-load-more, .confab .confab-dialog .confab-button-wrapper button.cancel .th-btn, .confab .confab-dialog .confab-button-wrapper button.cancel button, .confab .confab-dialog.confab-user-comments-dialog .th-base .confab-load-more, .confab .confab-post-wrapper .th-base button.confab-submit-button, .confab .th-base .confab-load-more, .th-base .confab .confab-dialog .confab-button-wrapper button, .th-base .confab .confab-dialog.confab-user-comments-dialog .confab-load-more, .th-base .confab .confab-load-more, .th-base .confab .confab-post-wrapper button.confab-submit-button, .th-base .th-btn, .confab .confab-dialog .confab-button-wrapper button.cancel .th-btn-alt, .th-base .th-btn-alt {
    color: #aaa;
    background-color: transparent !important;
    border-color: #aaa !important;
    transition: all 0.4s;
}  
  
.confab .confab-dialog .confab-button-wrapper .th-base button:hover, .confab .confab-dialog .confab-button-wrapper button.cancel .confab-load-more:hover, .confab .confab-dialog .confab-button-wrapper button.cancel .th-btn:hover, .confab .confab-dialog .confab-button-wrapper button.cancel button:hover, .confab .confab-post-wrapper .th-base button.confab-submit-button:hover, .confab .th-base .confab-load-more:hover, .th-base .confab .confab-dialog .confab-button-wrapper button:hover, .th-base .confab .confab-load-more:hover, .th-base .confab .confab-post-wrapper button.confab-submit-button:hover, .th-base .th-btn:hover, .confab .confab-dialog .confab-button-wrapper button.cancel .th-btn-alt:hover, .th-base .th-btn-alt:hover {
    color: #333;
    background-color: #aaa !important;
    border-color: #aaa !important;
    transition: all 0.4s;
}

.bg-blue, .bg-gray-1, #module-nav > div > nav > div {
    background-color: #2b2d32 !important;
}

    .article-text a, .thumbnails-b .syndicatedItem .video-title {
    color: #ccc !important;
    transition: all 0.4s;    
    }
    
.article-text a:hover {
     color: #eee !important;
     transition: all 0.4s;
}    
    
    
    
    
  
.oath-logo, .header-logo, .th-base .inline-block.vm {
    filter: invert(0.8) !important;
} 

/* The End */
}
