css:
@-moz-document url-prefix("https://www.youtube.com/watch"), url-prefix("https://youtube.com/watch"), url-prefix("http://youtube.com/watch"), url-prefix("https://www.youtube.com/c"), url-prefix("https://youtube.com/c"), url-prefix("http://youtube.com/c") {
#like-bar.ytd-sentiment-bar-renderer {
    background: hsl(120, 100%, 35%)
}

ytd-sentiment-bar-renderer[activated_] #like-bar.ytd-sentiment-bar-renderer {
    background-color: hsl(120, 100%, 35%)
}
ytd-toggle-button-renderer.style-default-active[is-icon-button] {
    color: hsla(0, 100%, 100%, .74)
}

#container.ytd-sentiment-bar-renderer {
    background-color: hsla(0, 100%, 40%, .8);
}
}
