css:
@-moz-document url-prefix(https://community.giffgaff.com/t5)
{ 
  /* dim the background */
  body 
  { 
    background: silver !important; 
  }
  
  div.lia-decoration-border, div.lia-message-board.lia-panel-message { background: silver !important; }
  

  /* make the paragraph text easier to read */
  
  .lia-message-body-content, .lia-note-body, .mce-content-body
  {
    font-family: 	arial, helvetica, sans-serif !important;
    font-size: 16px !important;
    line-height: 130% !important;    
  }    
  
  /* prevent hyphenation */
  
  .lia-message-body-content { hyphens: none !important; }
  
  /* distinguish the links  */ 
     
  .lia-message-body-content a:link, .lia-note-body a:link, .mce-content-body a:link { color: blue !important; } 
  .lia-message-body-content a:visited, .lia-note-body a:visited, .mce-content-body a:visited { color: brown !important; }
  .lia-message-unread a:link { color: blue !important; }
  .lia-message-read { color: brown !important; } 
  
  /* make the quotes smaller and scrollable */
   
  blockquote     
  { 
    transform: scale(0.9);
    max-height: 130px;
    overflow-y: auto; 
    background: rgba(150, 150, 150, 0.2); 
    opacity: 0.8;
    font-size: 16px !important;
    line-height: 115% !important;
  }
  
  /* improve spacing of list items */ 
  
  li 
  {
    padding-top: 4px;
    padding-bottom: 8px;
  }
  
  /* stop the icons obliterating the notifications */ 
  
  .lia-notification-feed-icon { opacity: 0.3 !important; }
  
  /* limit the initial display size of images */ 
  
  .lia-inline-image-display-wrapper.lia-image-align-inline img,
  .lia-inline-image-display-wrapper.lia-image-align-center img 
  { 
    max-height: 400px !important;
    max-width: 80% !important;
  }
  
  /* display more compact index entries */
  
  .lia-message-board.lia-panel-message { margin-bottom: 12px !important; }
  .lia-button-image-kudos { margin-bottom: 7px !important; }   
  .lia-message-statistics { margin-bottom: 3px !important; }   
  
  .row.topicListWrapper 
  { 
    padding-top: 3px !important; 
    padding-bottom: 3px !important;
    margin-bottom: 3px !important;
  }          
  .row.topicListBack { padding-top: 5px !important; padding-bottom: 5px !important;}
  .fa.icon { height: 28px !important; overflow: hidden !important; font-size: 30px !important; }
  .row.topicListWrapper .bold { white-space: nowrap !important; font-size: 12px !important; }

  /* reduce unsaved drafts to scrollable area */  
                
  #unsavedDraftsTapletV21 div { max-height: 150px !important; overflow-y: auto !important; } /* hide the drafts section */   
  
  
  /* identify gg staff (method 2) */
  
  .rank-handygiffstaffer, .rank-giffgaffEducator, .rank-HeadofCommunity,
  .userRank.rank-handygiffstaffer .UserAvatar,
  .userRank.rank-giffgaffEducator .UserAvatar,
  .userRank.rank-HeadofCommunity .UserAvatar 
  {
    background: green !important;
  }
  
  /* make the current page number more visible */
  
  .lia-paging-page-active .lia-link-navigation,
  .lia-paging-page-first span.lia-link-disabled,
  .lia-paging-page-last span.lia-link-disabled
  { color: white !important; background-color: black !important; }
  
  /* remove unnecessary padding around signatures */
  
  .UserSignature { margin: 5px 0 0 0 !important; padding: 0 0 0 0 !important; overflow: auto !important; }
  
  /* fix the photo album */
  
  .lia-media-album-image-wrapper { overflow: hidden !important; }
  /* .lia-media-saved-photos-album-image-block { cover: scale-down !important; } */
  .lia-media-saved-photos-image 
  { 
    max-width: 100% !important;
    max-height: 100% !important;
    overflow: hidden !important; 
  }
    
    /* batchprocessing on same line */
    .BatchProcessing { float: left !important; margin-right: 3px !important; }
    .row.topicListWrapper h4  {  display: inline-block !important; margin-top: 5px !important;}

}
