css:
@-moz-document url-prefix("https://yggtorrent."), domain("yggtorrent.com") {
/* TOP BAR */

.navbar {
	min-height: 50px;
	position: relative;
	background: #303030;
}
.navbar-nav>li {
	background: #0fd6c5;
}
.navbar-inverse .navbar-nav>li>a {
	background: #333333;
	transition: background-color 1s ease;
	font-size: 13px;
	text-transform: uppercase;
}
.navbar-inverse .navbar-nav>li>a:hover {
	background: #0fd6c5;
	color: black;
}
footer {
	background-color: #303030;
}
.content-box-large {
	padding: 10px;
}
/* LOGO */


.content-box,
.content-box-large {
	color: #BBBBBB;
}
.comment-post {
	color: black;
}
body {
	background: #2b2b2b;
	font-family: 'Segoe UI', Calibri, Tahoma, Geneva, Verdana, sans-serif!important;
}
a:hover,
a:focus {
	color: #C7C7C7;
	text-decoration: underline;
}
.content-box,
.content-box-large {
	background: #3A3A3A;
}
.content-box-large {
	padding: 25px;
}
.page-content>.row {
	margin-left: 3%!important;
	margin-right: 3%!important;
	margin-top: 45px;
}
/* TABLEAU */

table.table.table-striped a {
	color: #BBBBBB!important;
}
table.table.table-striped i {
	color: #BBBBBB;
}
table.table.table-striped td {
	color: #BBBBBB;
}
/* HEAD */
/* BODY */

.table-striped>tbody>tr:nth-child(odd)>td,
.table-striped>tbody>tr:nth-child(odd)>th {
	background-color: inherit;
	color: #BBBBBB;
}
.table-striped>tbody>tr:nth-child(even)>td,
.table-striped>tbody>tr:nth-child(even)>th {
	background-color: inherit;
	color: #BBBBBB;
}
.table-striped>tbody>tr:nth-child(odd),
.table-striped>tbody>tr:nth-child(odd) {
	background-color: #303030;
	color: #BBBBBB;
}
.table-striped>tbody>tr:nth-child(even),
.table-striped>tbody>tr:nth-child(even) {
	background-color: #424242;
	color: #BBBBBB;
}
.table-striped>tbody>tr:hover {
	background-color: #3A4B49;
	color: #000;
}
/* SEARCH BAR */

.ygg-navbar-input {
	background: #464646;
	color: #ffffff;
	transition: background-color 1s ease;
}
.ygg-navbar-input:hover {
	background: #303030;
	color: #ffffff;
}
.ygg-navbar-input:focus {
	background: #2D3D3C;
	color: #ffffff;
}
.ygg-navbar-button {
	background-color: #6B6B6B;
	color: #BBBBBB;
	border: 0;
	border-radius: 0;
	transition: background-color 1s ease;
}
.ygg-navbar-button:hover {
	background-color: #0fd6c5;
	color: #000;
}
/* SIDEBAR */
/* HEAD */

.sidebar .nav>li>a {
	transition: background-color 1s ease;
	background: #303030!important;
	color: #0fd6c5;
}
.sidebar .nav>li>a:hover {
	background: #0fd6c5!important;
	color: #303030;
}
/* SELECT */

select {
	background: #0fd6c5;
	padding: 5px;
	font-size: 16px;
	line-height: 1;
	border: 0;
	border-radius: 0;
	height: 34px;
}
/* LINES */

.sidebar .nav>li>ul>li>a {
	background: #474747;
	border-top: 0px;
	color: #CFCFCF;
	display: block;
	font-size: 13px;
	line-height: 20px;
	padding: 8px;
	text-decoration: none;
	transition: background-color 1s ease;
}
.sidebar .nav>li>ul>li>a:hover {
	background: #5DBBB2;
	color: #444444;
	border-bottom: 0px;
}
/* PAGINATION */

.pagination>.active>a,
.pagination>.active>span,
.pagination>.active>a:hover,
.pagination>.active>span:hover,
.pagination>.active>a:focus,
.pagination>.active>span:focus {
	z-index: 2;
	color: #000;
	cursor: default;
	background-color: #0fd6c5;
	border: 0;
	border-radius: 0;
}
.pagination>li>a {
	border-top-right-radius: 0px;
	border-bottom-right-radius: 0px;
	border-radius: 0px!important;
	border: 0;
}
.pagination>li>a:hover {
	border-top-right-radius: 0px;
	border-bottom-right-radius: 0px;
	background-color: #0fd6c5;
	border-radius: 0px!important;
	border: 0;
}
/* PAGE TORRENT */
/* SUPER HEAD */

.panel-heading>.panel-title,
.content-box-header .panel-title {
	color: #0fd6c5;
	font-weight: bold;
}
.panel-heading>.panel-title>i {
	color: #0fd6c5;
	font-size: 15px;
	font-weight: bold;
	padding: 50px!important;
}
.content-box-header {
	background: #424242;
	border-radius: 0;
	height: 50px;
}
/* HEAD */

.content-box-large {
	padding: 10px;
}
.content-box-large>div>center>a {
	color: #BBBBBB!important;
}
td.tabledata0 {
	background-color: #474747;
	color: #BBBBBB;
	border: 0;
}
td.tabledata1 {
	background-color: #3A3A3A;
	padding: 5px 10px;
	border: 0;
	color: #BBBBBB;
}
.btn-danger {
	color: #000;
	background-color: #0fd6c5;
	border-color: #0fd6c5;
}
/* COMMENTAIRE */

.wysibb .wysibb-toolbar,
.wysibb-text-editor,
.wysibb-text,
.wysibb {
	background: #505050!important;
	color: #BBBBBB!important;
	border: 0;
}
.wysibb-toolbar-btn {
	background: #BBBBBB!important;
	color: black;
}
.wysibb-toolbar-btn:hover {
	background: #0fd6c5!important;
}
.panel {
	background: #505050;
	display: block;
	word-wrap: break-word;
	color: #BBBBBB;
}
.comment-post {
	color: #BBBBBB;
}
a {
	color: #BBBBBB;
	text-decoration: double;
}
element.style {
	background: rgba(218, 181, 181, 0.52);
	text-align: center;
}
element.style {
	color: #BBBBBB!important;
}
.order_panel {
	background: #474747;
	border: 1px solid rgba(48, 15, 15, 0.15);
	border-radius: 4px;
	padding: 15px;
}
div.expand {
	background: #474747!important;
	color: #0fd6c5;
}
div.expand:hover {
	background: #303030!important;
}
cloudflare-app[app="welcome-bar"] {
	background-color: #0fd6c5;
	color: #000;
}
td:nth-child(1) > span {
	display: none
}
td:nth-child(1) > a:nth-child(3) {
	display: none
}
td:nth-child(1) > a:nth-child(4) {
	display: none
}
}
