css:
@-moz-document
domain(www.devrant.io),
domain(devrant.io),
domain(www.devrant.com),
domain(devrant.com) {
/*
	#40415a -> #1A1B1C
	#54556e -> #071114
*/

body, .rant-top-bar, .rantlist li.feed-prev-more {
    background-color: #1A1B1C;
}

.bluegrey-bg {
    background-color: #071114;
}

.top-bar-notif {
    border: 2px solid #1A1B1C;
}

.rantlist-bg:before {
    content: "";
    position: absolute;
    left: 0;
    right: 0;
    top: 44px;
    bottom: 0;
    background: rgba(0, 0, 0, 0.74);
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
}

.rantlist li, .details-page .rantlist li, .notif-list a, .rantlist li.feed-prev-more {
	background-color: #141415;
    border-top: 2px solid #1A1B1C;
}

.notif-list li {
    border-top: 1px solid #1A1B1C;
}

.notif-list a:hover {
    background-color: rgba(20, 20, 21, 0.57);
}

.plusone, .minusone, .modal-x {
    background-color: #1A1B1C;
}

.rantlist li:hover, .rantlist li.post-details:hover, .rantlist li.reply-row:hover, .rantlist li.feed-prev-more:hover {
    background-color: rgba(20, 20, 21, 0.57);
}

.rantlist-title, .notif-body {
    color: #585858;
}

.rantlist-tags a, .username-row .profile-score {
    background-color: #1A1B1C;
    border: 2px solid #585858;
}

.rant-image:before {
    content: "";
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.69);
	-webkit-transition: background-color .25s ease-in-out;
	-ms-transition: background-color .25s ease-in-out;
    transition: background-color .25s ease-in-out;
}

.rant-image:hover:before {
    content: "";
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    background: none;
	-webkit-transition: background-color .25s ease-in-out;
	-ms-transition: background-color .25s ease-in-out;
    transition: background-color .25s ease-in-out;
}

.modal-overlay {
	background-color: rgba(7, 17, 20, 0.92);
}

.signup-login-forms a, .menu-modal-list .icon, .menu-modal .menu-logo {
    color: #585858;
}

.username-details, .rantlist-footer-link, .timestamp {
    color: #717175;
}

.reply-bar {
    background-color: #717175;
}

.post-rant-form textarea, .post-comment-form textarea {
    color: #585858;
    border: 2px solid #141415;
    background-color: #141415;
}

.post-rant-bottom {
    background-color: #1A1B1C;
}

.rantlist-vote-col.vote-state-novote .btn-vote-circle {
	background-color: #717175;
}
}
