@-moz-document domain("ac3.tre.it") {
/*	Style for Tre.it Selfcare
	Author: Polymedia S.p.A. part of the KIT digital family

	Edit By Mx
	------------------------------------------------ */

body {font-family: Arial, Georgia, Helvetica, Verdana; color: #dadada; background-color: #202020 ;}
.clear {clear: both; height: 0px; line-height: 0; overflow: hidden; font-size: 0px; background-color: #202020 ;}
.clearBoth {clear: both;}
.fLeft {float: left;}
.fRight {float: right;}

.box_Last {margin-right: 0;}

.tit_Hidden {display: none;}
.displayNone {display: none;}

.hr {height: 1px; margin-bottom: 1em;}
.hr hr {display: none;}

.txt_Error, .txt_Info {}
.txt_Error {}
.txt_Info {}

/* TXTBOX_CMS
/* restore text attributes for content inserted with cms
---------------------------------------------- */
.txtBox_cms {}
.txtBox_cms h1, .txtBox_cms h2, .txtBox_cms h3, .txtBox_cms h4, .txtBox_cms h5, .txtBox_cms h6 {font-family: Arial; line-height: 1.2em; font-weight: bold; color: #dadada;}
.txtBox_cms h1 {font-size: 19px;}
.txtBox_cms h2 {font-size: 19px;}
.txtBox_cms h3 {font-size: 17px;}
.txtBox_cms h4 {font-size: 15px;}
.txtBox_cms h5 {font-size: 13px;}
.txtBox_cms h6 {font-size: 13px; font-weight: normal;}
.txtBox_cms p {margin-bottom: 1em;}
.txtBox_cms small {font-size: 0.9em;}
.txtBox_cms big {font-size: 1.1em;}
.txtBox_cms strong, .txtBox_cms b {font-weight: bold;}
.txtBox_cms u {text-decoration: underline;}
.txtBox_cms i, .txtBox_cms em {font-style: italic;}
.txtBox_cms dt {font-weight: bold;margin: 0.5em 0;}
.txtBox_cms dd {margin-left: 1em;}
.txtBox_cms ol {list-style: decimal;}
.txtBox_cms ul {list-style: disc;}
.txtBox_cms address {font-style: italic;}
.txtBox_cms blockquote {margin: 0.5em 2em;}
.txtBox_cms h1, .txtBox_cms h2, .txtBox_cms h3, .txtBox_cms h4, .txtBox_cms h5, .txtBox_cms h6, .txtBox_cms address, .txtBox_cms dl, .txtBox_cms ul, .txtBox_cms ol {margin: 0.5em 0;}
.txtBox_cms ol, .txtBox_cms ul {margin-left: 1.4em; margin-top: 0.5em; margin-bottom: 0.5em;}
html>/**/ body .txtBox_cms ol, html>/**/ body .txtBox_cms ul {margin-left: 0; padding-left: 1.1em;}
}

@-moz-document domain("ac3.tre.it") {
/* BOX BODY */
.box_Body {position: static; width: 980px; min-height: 100%; height: auto !important; height: 100%; margin: 0 auto -216px; background: #202020 ; z-index: 100}

/* =TABLE CONSUMI v 3 */

.box_Consumi_v3 {
    background-color: #303030;
}

/* =TABLE in BOX 001 */

.box_001 table tbody th, .box_001 table tbody td {background-color: #303030;}

.box_001 table .tr_Header th, .box_001 table .tr_SubHeader th, .box_001 table th, .box_001 table td {border-right: 4px solid #202020; border-bottom: 4px solid #202020; padding: 6px 8px;}
}

@-moz-document domain("ac3.tre.it") {
/*	Style for Tre.it Selfcare
	Author: Polymedia S.p.A. part of the KIT digital family
	Edit By Mx

	------------------------------------------------ */

@font-face {
	font-family: 'Helvetica-Roman';
	src: url('../fonts/Helvetica-Roman_webfont.eot');
	src: local("?"),
	url('../fonts/Helvetica-Roman_webfont.woff') format('woff'),
	url('../fonts/Helvetica-Roman_webfont.ttf') format('truetype'),
	url('../fonts/Helvetica-Roman_webfont.svg#webfont') format('svg');
}
@font-face {
	font-family: 'Helvetica-Bd';
	src: url('../fonts/Helvetica-Bd_webfont.eot');
	src: local("?"),
	url('../fonts/Helvetica-Bd_webfont.woff') format('woff'),
	url('../fonts/Helvetica-Bd_webfont.ttf') format('truetype'),
	url('../fonts/Helvetica-Bd_webfont.svg#webfont') format('svg');
}
/*
font-family: 'Helvetica-Roman', Arial, sans-serif;
font-family: 'Helvetica-Bd', Arial, sans-serif;
*/

input.btn_Buttons, a.btn_Buttons:link, a.btn_Buttons:visited {font-family: 'Helvetica-Bd', Arial, sans-serif; font-weight: bold; font-size: 1.4em; line-height: 20px; color: #202020 ; text-decoration: none; text-align: center;}
	a.btn_Buttons:hover {}
td input.btn_Buttons, td a.btn_Buttons:link, td a.btn_Buttons:visited {font-size: 1.2em;}
a.link_Expand:link, a.link_Expand:visited {font-size: 1.2em; line-height: 22px; color: #B6B6B6; text-decoration: none;}
	a.link_Expand:hover {color: #dadada;}

.box_FreeHtml {font-size: 1.2em; line-height: 1.3em; color: #dadada;}
.box_FreeHtml a:link, .box_FreeHtml a:visited {color: #dadada; text-decoration: underline;}
	.box_FreeHtml a:hover {text-decoration: none;}

/* BOX HEADER */
.box_Header .tit_Site {font-family: 'Helvetica-Bd', Arial, sans-serif; font-weight: bold; font-size: 46px; line-height: 48px; letter-spacing: -1px; color: #dadada;}
.box_Header .box_Form_Search .box_Input .inp_Text, .box_Header .box_Form_Search .lbl_Label {font-size: 16px; color: #989898;}

.box_Form_Switch_USIM div.selector span, .box_Form_Switch_USIM div.selector select {font-size: 16px; color: #989898;}
.box_Form_Switch_USIM div.selector.disabled span {font-size: 16px;}
.box_Form_Switch_USIM .box_Select {font-size: 16px; line-height: 30px; color: #989898; text-align: left;}

.box_Form_Switch_USIM .lbl_Label {font-size: 16px; line-height: 30px; color: #dadada; white-space: nowrap; text-align: right;}
.box_Form_Switch_USIM .btn_Sumbit {font-family: 'Helvetica-Bd', Arial, sans-serif; font-weight: bold; font-size: 18px; line-height: 30px; color: #202020 ; text-align: center; text-decoration: none;}

.list_Menu_Top li a {font-family: 'Helvetica-Bd', Arial, sans-serif; font-weight: bold; font-size: 17px; line-height: 1.2em; letter-spacing: -1px; color: #dadada; text-align: right; text-decoration: none;}
	.list_Menu_Top li a:hover {color: #999999;}
	.list_Menu_Top li.li_Selected a {color: #202020 ;}

/* =LIST MENU MAIN	*/
.list_Menu_Main li .box_Menu_First a {font-family: 'Helvetica-Bd', Arial, sans-serif; font-weight: bold; font-size: 16px; line-height: 1.2em; color: #989898; text-decoration: none;}
.list_Menu_Main li:hover .box_Menu_First a:link, .list_Menu_Main li:hover .box_Menu_First a:visited, 
.list_Menu_Main li.li_Selected .box_Menu_First a:link, .list_Menu_Main li.li_Selected .box_Menu_First a:visited, 
	.list_Menu_Main li .box_Menu_First a:hover {color: #202020 ;}

/* =LIST SUB MENU	*/
.list_Sub_Menu .tit_Section span {font-size: 24px; line-height: 1.6em; letter-spacing: 0; font-weight: bold; color: #202020 ;}
	.list_Sub_Menu .tit_Section span a:link, .list_Sub_Menu .tit_Section span a:visited {color: #202020 ; text-decoration: none;}
.list_Sub_Menu a.link_Search:link, .list_Sub_Menu a.link_Search:visited, 
.box_Sub_Menu a.link_HowTo:link, .box_Sub_Menu a.link_HowTo:visited, .list_Sub_Menu a.link_HowTo:link, .list_Sub_Menu a.link_HowTo:visited {font-size: 1.2em; line-height: 15px; font-weight: bold; color: #dadada; text-decoration: none; text-align: right;}
	.list_Sub_Menu a.link_Search:hover, 
	.box_Sub_Menu a.link_HowTo:hover, .list_Sub_Menu a.link_HowTo:hover {color: #202020 ;}
	.color_00 .list_Sub_Menu a.link_Search:link, .color_00 .list_Sub_Menu a.link_Search:visited {color: #202020 ;}
		.color_00 .list_Sub_Menu a.link_Search:hover {color: #dadada;}

.list_Sub_Menu .list_Products {font-size: 1.1em; line-height: 1.2em;}
.list_Sub_Menu .list_Products a:link, .list_Sub_Menu .list_Products a:visited {color: #202020 ; text-decoration: none;}
	.list_Sub_Menu .list_Products a:hover {color: #dadada;}

.list_Sub_Menu .list_Plans li .tit_SubSection {font-size: 14px; line-height: 1.2em; letter-spacing: 0; font-weight: bold; color: #202020 ;}
	.list_Sub_Menu .list_Plans li li {font-size: 1.1em; line-height: 1.8em;}
	.list_Sub_Menu .list_Plans li a:link, .list_Sub_Menu .list_Plans li a:visited {color: #202020 ; text-decoration: none;}
		.list_Sub_Menu .list_Plans li a:hover {color: #dadada;}

.list_Sub_Menu .list_Options a:link, .list_Sub_Menu .list_Options a:visited {font-size: 14px; line-height: 30px; font-weight: bold; color: #202020 ; text-decoration: none;}
	.list_Sub_Menu .list_Options a:hover {color: #dadada;}

.list_Sub_Menu .list_Estero a:link, .list_Sub_Menu .list_Estero a:visited {font-size: 14px; line-height: 30px; font-weight: bold; color: #202020 ; text-decoration: none;}
	.list_Sub_Menu .list_Estero a:hover {color: #dadada;}

/* SUB MENU EASY	*/
.li_Sub_Menu_Easy .tit_Section span {font-size: 24px; line-height: 1.6em; letter-spacing: 0; font-weight: bold; color: #202020 ;}
	.li_Sub_Menu_Easy .tit_Section span a {color: #202020 ; text-decoration: none;}
	.li_Sub_Menu_Easy .tit_Section span a:link, .list_Sub_Menu .tit_Section span a:visited {color: #202020 ; text-decoration: none;}
.li_Sub_Menu_Easy .list_Sub_Menu li a:link, .li_Sub_Menu_Easy .list_Sub_Menu li a:visited {font-size: 1.4em; line-height: 30px; font-weight: bold; color: #202020 ; text-decoration: none;}
	.li_Sub_Menu_Easy .list_Sub_Menu li a:hover {color: #dadada;}


/* BOX BODY CONTENT */
/* =BOX INLINE MESSAGE */
.box_Inline_Message {text-align: center; font-size: 1.2em; line-height: 1.2em;}

/* =BOX TARIFFA */
.box_Tariffa .tit_Section {font-family: 'Helvetica-Bd', Arial, sans-serif; font-weight: bold; font-size: 1.8em; line-height: 1.4em; color: #dadada; text-align: center;}
.box_Tariffa .tit_Section span {font-size: 1.6em;}

	.box_Tariffa .tit_Title {font-family: 'Helvetica-Bd', Arial, sans-serif; font-weight: bold; font-size: 1.5em; line-height: 1.1em; color: #dadada;}
	.box_Tariffa .txt_Description {font-size: 1.2em; line-height: 1.1em; color: #dadada;}
	.box_Tariffa .box_Properties {text-align: center;}
		.box_Tariffa .box_Properties .txt_Value {font-family: 'Helvetica-Bd', Arial, sans-serif; font-weight: bold; font-size: 3em; line-height: 1em; color: #202020 ; white-space: nowrap;}
		.box_Tariffa .box_Properties .txt_Info {font-size: 1.2em; line-height: 1em; color: #202020 ;}
		.box_Tariffa .box_Properties .txt_Value_Smaller {font-size: 2.2em; line-height: 1.38em;}
		
.box_Tariffa .txt_Activation_Date {font-size: 1.1em; line-height: 1.2em; color: #dadada;}

/* =BOX TECH DATA */
.box_Tech_Data .tit_Description {font-size: 1.2em; line-height: 1.3em; font-weight: bold;}
.box_Tech_Data .txt_Description {font-size: 1.2em; line-height: 1.3em;}
.box_Tech_Data .txtBox_cms {font-size: 1.1em; line-height: 1.2em;}
.box_Tech_Data .txtBox_cms a:link, .box_Tech_Data .txtBox_cms a:visited {color: #dadada; text-decoration: underline;}
	.box_Tech_Data .txtBox_cms a:hover {}

/* =BOX CONSUMI */
.box_Consumi .tit_Section {font-family: 'Helvetica-Bd', Arial, sans-serif; font-weight: bold; font-size: 1.8em; line-height: 1.4em; color: #dadada; text-align: center;}
