	.tab_Consumi_v1 .box_Counter_Top div, .tab_Consumi_v1 .box_Counter_Bottom div {/*white-space: nowrap;*/}
.tab_Consumi_v1 .tr_Total td {font-family: 'Helvetica-Bd', Arial, sans-serif; font-weight: bold; font-size: 1.5em; line-height: 1.4em; color: #dadada; text-align: center;}

