.tab_Consumi_v3_1 .box_Description .txt_Details_01 {font-size: 1.5em;font-weight: bold;}
.tab_Consumi_v3_1 .box_Percentage {font-size: 1.2em; line-height: 20px; text-align: right;}
	.tab_Consumi_v3_1 .box_Percentage .txt_Percentage {font-weight: bold;}
.tab_Consumi_v3_1 .box_Note {font-size: 1.2em; line-height: 1.2em; font-weight: bold; color: #dadada; text-align: right;}

