css:
@-moz-document domain("forum.roblox.com") {
    /*Made by RedderBetter*/
    html {
        background-color: #fffeef;
    }
    #LogoAndSlogan {
        display: Block;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        background: url("http://i.imgur.com/bVV71UU.png");
        width: 490px;
        height: 261px;
        padding-left: 490px;
    }
    #RollerContainer {
        background: url(https://images.rbxcdn.com/dcbdfaf1c08058e71f65c09f7b98ff04.jpg);
    }
    .container-footer {
        background-color: #fffeef;
    }
    .robloxLogo {
        display: Block;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        background-image: url("https://images.rbxcdn.com/e870a0b9bcd987fbe7f730c8002f8faa.png");
        background-size: 36px, 36px;
        width: 36px;
        height: 36px;
        padding-left: 36px;
    }
    .icon-logo {
        background-image: url(http://i.imgur.com/c8IZByf.png);
    }
    .icon-logo-r {
        background-image: url(https://2ch.hk/fl/src/261923/14864186713970.png);
    }
    #MasterContainer,
    .nav-content-inner,
    .container-main,
    .gutter-ads {
        background: url(http://s.4cdn.org/image/fade.png) top center repeat-x #ffffee;
    }
    #Body {
        background: url(http://s.4cdn.org/image/fade.png) top center repeat-x #ffffee;
    }
    a.linkSmallBold:visited,
    a.linkMenuSink:visited,
    a.linkSmall,
    a.LinkSmallBold,
    a.linkMenuSink,
    a:link,
    a:visited,
    a:active,
    #forum-nav a.menuTextLink {
        color: #800000;
    }
    body .forum-table-header,
    body .forum-table-header th,
    body .forum-table-footer {
        background-color: #b1705c;
    }
    .rbx-left-col {
        background-color: #ffffee;
    }
    a.post-list-subject,
    a.post-list-subject:hover,
    a.post-list-author,
    a.post-list-author:hover {
        color: #343434;
    }
    .table td,
    .table th {
        border-top: 1px solid #D9BFB7;
    }
    .forum-content-background,
    .forum-table-row {
        background-color: #f0e0d6;
    }
    td.flatViewSpacing {
        background-color: #D9BFB7;
    }
    .forum-table-row:hover {
        background-color: #e3d0c3;
    }
    .rbx-header {
        background-color: #b1705c;
    }
    input[type="text"] {
        border-color: #B8B8B8;
    }
    th {
        background-color: #b1705c;
    }
}
