css:
@-moz-document domain("manage.conoha.jp") {
body {
	font-family: "Microsoft YaHei",Tahoma,メイリオ,Meiryo,ヒラギノ角ゴ\20Pro\20W3,"Hiragino Kaku Gothic Pro",ＭＳ\20Ｐゴシック,Arial,Verdana,sans-serif;
   background-color: #222 !important;
   background-image: url(data:image/png;base64,) !important
}

.header__inner, .side__nav__list, .side__nav, .side__nav__menu, .js_switch-button {
	background-color: #222!important;
}

h2, h3, thead, tbody, .refine, .refine__title, #email_header, .header__menu>li>a, .panels__box__item, .items__list, .__tabbtn--help, .news__top__title, .news__contents, .input__radio-2__wrap {
	color: #FFF;
}

.panels__box-hover__list > li > a {
	color: #FFF!important;
}

.input__radio-2+label {
	background: #222;
}

.input__radio-2__wrap {
	border: 1px solid #555;
}

.is-active {
	background-color: #444!important;
}

header {
	border-bottom-width: 0px !important;
}

.side__nav__list {
	border-bottom: 1px solid #444;
}

.progress {
	border: 1px solid #DDD;
}

.panels__box {
	background: rgba(22,22,22,.5);
}

.panels__box-hover {
	background: rgba(22,22,22,1);
}
}
