css:
#gm-status{display: none !important;}

toolbar {-moz-appearance: none !important;border-left:0px !important;border-right: 0px !important;border-bottom:0px !important;border-top:0px !important;}

menubar, menubutton, menulist, menu, menuitem {font-family:helvetica !important;font-style:normal !important;font-weight:normal !important;
  font-size:4mm !important;}

a:visited{font-style: italic !important;color:navy !important;}
a{color:blue !important;}

#toolbar-context-menu, menupopup, popup {border:1px solid !important;-moz-border-top-colors:black !important;-moz-border-right-colors: black !important;-moz-border-bottom-colors:black !important;-moz-border-left-colors:black !important;}

#toolbar-context-menu, menuitem, menupopup, popup, menupopup menu, menupopup menuitem, popup menu, popup menuitem, #bookmarks-menu autorepeatbutton, #personal-bookmarks autorepeatbutton {background-image: url("data:image/png;base64,") !important;color:black !important;font-size:11pt !important;cursor:pointer !important;}

#toolbar-context-menu:hover, menupopup menu:hover, menupopup menuitem:hover, popup menu:hover, popup menuitem:hover, #bookmarks-menu autorepeatbutton:hover, #personal-bookmarks autorepeatbutton:hover {background:gainsboro !important;color:darkgreen !important;}
menuseparator {display: none !important;}

tab{-moz-appearance: none !important;} 
tab[selected="true"] {background-image: url("data:image/png;base64,") !important;color:black !important;}

tab:not([selected="true"]) {background-image: url("data:image/png;base64,") !important;color:black !important;} 

#main-window, #navigator-toolbox, #PersonalToolbar {background-image: url("data:image/png;base64,") !important;background-position:right center !important;background-repeat:repeat !important;}

#urlbar[level] .autocomplete-textbox-container > * {background-color: MistyRose !important;}
#urlbar {background-image: url("data:image/png;base64,") !important;;color:black !important;font-size:9pt !important;font-weight:bold !important;font-family:georgia !important;}

@-moz-document domain("forums.ebay.com"), url-prefix(forums.ebay.com) {
li{color:navy !important;}
img{max-width:600px; max-height:600px;}
body {width:92% !important; margin-left:auto !important; margin-right:auto !important;}

ul, li {background-image: url("data:image/png;base64,") !important;background-position: right center !important;background-repeat: repeat !important;}

table, td, th, div, input, span {background-image: url("data:image/png;base64,") !important;}

body {background-image: url("data:image/png;base64,") !important;}

td{font-size:13pt !important;}
a:visited{font-style: italic !important;}
a[href]:visited {opacity:0.5 !important;color:darkgreen !important;}
a[href]:visited:hover {opacity:1.0 !important;color:darkgreen !important;}
a{border-color:white !important;}
a{font-size:12pt !important;}
a:hover{color:darkgreen !important;}
a:link{color:navy !important;}
li:hover{background:Gainsboro !important;}
h2:hover{background:Gainsboro !important;}
.post{color:darkblue !important;background-image: url("data:image/png;base64,") !important;}
.reply-button{color:blue !important;}
ul{background-image:url("data:image/png;base64,") !important;}

}
