css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("searx.me") {
  
body {
    font-family: 'Roboto', Consolas;
    margin-bottom: 80px;
    background-color: #1d1f21;
}
  
  .alert-danger {
    background-color: #1d1f21;
    border-color: #cc6666;
    color: #cc6666;
}
  
  .alert > p, .alert > ul {
    margin-bottom: 0;
    color: #c5c8c6;
}
  
  .btn-default {
    color: #b0b5b2;
    background-color: #1d1f21;
    border-color: #1d1f21;
}

.btn-default.disabled, .btn-default[disabled], fieldset[disabled] .btn-default, .btn-default.disabled:hover, .btn-default[disabled]:hover, fieldset[disabled] .btn-default:hover, .btn-default.disabled:focus, .btn-default[disabled]:focus, fieldset[disabled] .btn-default:focus, .btn-default.disabled:active, .btn-default[disabled]:active, fieldset[disabled] .btn-default:active, .btn-default.disabled.active, .btn-default.active[disabled], fieldset[disabled] .btn-default.active {
    background-color: transparent;
    border: none;
    color: #b0b5b2;
}

.center-block {
    background-color: transparent;
    margin-left: auto;
    margin-right: auto;
}
  
  .custom-select {
    appearance: none;
    -webkit-appearance: none;
    -moz-appearance: none;
    font-size: 1.2rem;
    font-weight: normal;
    background-color: #1d1f21;
    border: none;
    color: #c5c8c6;
}
  
  .external-link {
    color: #7CC844;
    font-size: 12px;
    line-height: 2;
}
  
  .form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #c5c8c6;
    background-color: #1d1f21;
    background-image: none;
    border: none !important;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
    -webkit-transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
}
  
  .form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    cursor: not-allowed;
    border: none;
    background: none;
    opacity: 1;
}
  
  .img-thumbnail {
    padding: 1.5px;
    line-height: 1.42857143;
    background: none;
    border: none;
    border-radius: 4px;
    -webkit-transition: all .2s ease-in-out;
    -o-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;
    display: inline-block;
    width: 100% \9;
    max-width: 100%;
    height: auto;
}
  
  .infobox .btn {
    background-color: #282a2e;
    border: none;
}
  
  .infobox .btn a {
    font: normal 12.5px Consolas !important;
    color: #ffffff;
    margin: 5px;
}
  
  
  
  .infobox p {
    font: italic 14px Consolas !important;
    color: #bfc0bf;
}

.modal-footer {
    padding: 15px;
    text-align: right;
    border-top: 1px solid rgb(229, 229, 229, 0.5);
}
  
  .modal-header {
    padding: 15px;
    border-bottom: none;
    min-height: 16.42857143px;
}
  
  .modal-title {
    margin: 0;
    line-height: 1.42857143;
    color: #c5c8c6;
}
  
  .modal-wrapper {
    background-clip: padding-box;
    background-color: #1d1f21;
    border: none;
    border-radius: 6px;
    box-shadow: 0 3px 9px rgba(0,0,0,0.5);
    outline: 0 none;
    position: relative;
}
  
  ::-moz-selection {
  background: #424242 ; /* Gecko Browsers */
}
  
  .nav-tabs.nav-justified > li > a {
    border-bottom: none;
    border-radius: 4px 4px 0 0;
}
  
  .panel {
    margin-bottom: 20px;
    background-color: #1d1f21;
    border: none;
    border-top-color: transparent;
    border-right-color: transparent;
    border-bottom-color: transparent;
    border-left-color: transparent;
    border-radius: 4px;
    -webkit-box-shadow: 0 1px 1px rgba(0,0,0,0.05);
    box-shadow: 0 1px 1px rgba(0,0,0,0.05);
}
  
  .panel-default > .panel-heading {
    color: #8b8d8b;
    background-color: #1d1f21;
    border-color: #1d1f21;
}
  
  /*
    float: left !important;*/
  
  #q {
    color: #c5c8c6;
    box-shadow: none;
    border-right: none;
    border-color: #1d1f21;
    background-color: #1d1f21;
}
  
  .result-default:hover, .result-code:hover, .result-torrent:hover, .result-videos:hover, .result-map:hover {
    background-color: #282a2e;
    clear: both;
    padding: 2px 4px;
}
  
  .result_header a, .result_header a:hover {
    color: #81a2be;
    text-decoration: none;
}
  
  .result_header a, .result_header a:visited {
    color: #81a2be;
    text-decoration: none;
}  
  
  /*words find in the results*/
  * {
    border-radius: 0 !important;
    color: #727472;
}
  
  .result-content {
    margin-top: 2px;
    margin-bottom: 0;
    word-wrap: break-word;
    color: #c5c8c6;
    font-size: 13.5px;
}
  
  .result_header a .highlight {
    background-color: transparent;
    color: #81a2be;
}
  
  .search_categories input[type="checkbox"]:checked + label, #categories input[type="checkbox"]:checked + label {
    color: #717885;
    font-weight: bold;
    border-bottom: #717885 5px solid;
}
  
  .search_categories label, #categories label, .search_categories .input-group-addon, #categories .input-group-addon {
    flex-grow: 1;
    flex-basis: auto;
    font-size: 1.2rem;
    font-weight: normal;
    background-color: #1d1f21;
    border: none;
    border-right-width: 1px;
    border-right-style: solid;
    border-right-color: rgb(221, 221, 221);
    border-right: none;
    color: #bdc1bf;
    padding-bottom: .4rem;
    padding-top: .4rem;
    text-align: center;
}
  
  .search_categories label:last-child, #categories label:last-child, .search_categories .input-group-addon:last-child, #categories .input-group-addon:last-child {
    border-right: none;
}
  
  #search_form .input-group-btn .btn {
    border-color: #1d1f21;
    background-color: #1d1f21;
}
  
  .searx-navbar {
    border-bottom: 1px solid #54595f;
    background: #1d1f21;
    height: 2.3rem;
    font-size: 1.3rem;
    line-height: 1.3rem;
    padding: .5rem;
    font-weight: bold;
    margin-bottom: .8rem;
}
  
  .table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th {
    border-top: 1px solid #a0a5ab;
    border-bottom: 1px solid #a0a5ab;
    background-color: #1d1f21;
}
  
  .text-info {
    color: #81a2be;
}
  
  .highlight {
    background: transparent;
    }}
