css:
@-moz-document regexp("(https:\\/\\/|http:\\/\\/)(www\\.)*shopee\\.tw.*") {
#main,
.footer,
.category-overview-with-banner-wrapper {
    background-color: #36393E
}

/*.shopee-top {
    filter: grayscale(0.1); 
}*/
.shopee-searchbar {
    background-color: #514B48;
}
.header-with-search-wrapper .shopee-searchbar-input,
.shopee-searchbar-input input {
    background-color: #514B48;
}

/* 偽元素-placeholder 選擇器 */
.shopee-searchbar-input input::-webkit-input-placeholder {
    /* WebKit, Blink, Edge */
    color: #7A716C;
}
.shopee-searchbar-input input:-moz-placeholder {
    /* Mozilla Firefox 4 to 18 */
    color: #7A716C;
    opacity: 1;
}
.shopee-searchbar-input input::-moz-placeholder {
    /* Mozilla Firefox 19+ */
    color: #7A716C;
    opacity: 1;
}
.shopee-searchbar-input input:-ms-input-placeholder {
    /* Internet Explorer 10-11 */
    color: #7A716C;
}
.shopee-searchbar-input input::-ms-input-placeholder {
    /* Microsoft Edge */
    color: #7A716C;
}
.navbar-wrapper--official-shop + div .ofs-header__right > .shopee-searchbar {
    border: 4.8px solid #514B44;
}
.shopee-searchbar-hints {
    background-color: #514B48;
}
.shopee-searchbar-hints__entry:hover,
.shopee-searchbar-hints__search-shop:hover,
.shopee-searchbar-hints__history-entry:hover {
    background-color: #D2855E;
}
.shopee-searchbar-hints__header {
    color: #fff
}
.shopee-searchbar-hints__entry__typed-product-name {
    color: #FFF;
}
.shopee-searchbar-hints__entry__category-name {
    color: #A4A2A1;
}
.shopee-searchbar-hints__entry:hover * {
    color: #000;
}
.navbar__link--account > .shopee-drawer::after,
.navbar__link--notification > .shopee-drawer::after,
.header-with-search__cart-wrapper > .shopee-drawer::after {
    border-bottom-color: #2F3136;
    /* 更改氣泡框 箭頭顏色 */
}
.ofs-header__cart-wrapper > .shopee-drawer::after {
    border-bottom-color: #36393E;
}
.navbar-account-drawer__content,
.notification-list {
    background-color: #2F3136;
}
.navbar-account-drawer__button:hover,
.notification-card:hover,
.notification-list__link:hover {
    background-color: #42464D;
}
.notification-list__text {
    color: #fff
}
ul[class$="item-list"],
.home-category-list .shopee-image-carousel__item * {
    background-color: #484B51;
}
.home-page .container .full-home-banners {
    background-color: rgba(72, 75, 81, .4);
    padding: 5px;
}
.home-category-list__category-grid-name {
    color: #E3E3E3
}
.shopee-header-section__header {
    background-color: transparent;
}
.home-page .shopee-header-section__content {
    background-color: #484B51;
}
.home-page .container .shopee-header-section__header {
    margin-top: 30px;
    background-color: transparent;
}
.shopee-button-solid svg {
    color: #fff !important;
}
.flash-sale-item-card {
    background-color: transparent;
}
.flash-sale-progress-bar__text {
    color: #6F480C;
}
.flash-sale-progress-bar__text--selling-fast {
    color: #fff;
}
.shopee-item-card {
    background-color: #484B51;
}
.shopee-item-card__text-name {
    color: #EAEAEA
}
.shopee-item-card__btn-like__text,
.shopee-item-card__btn-ratings-count,
.shopee-item-card--full .shopee-item-card__label-sold-count,
.shopee-svg-icon {
    color: #D4D4D4;
}
.shopee-item-card:hover .shopee-item-card__text-name {
    color: #fff;
}
.shopee-image-placeholder {
    background-color: transparent;
}
*[class$="cover-img-background"] {
    border-radius: 7px;
    border: 5px solid #484B51;
}
*[class$="cover-image"] {
    border-radius: 5px;
}
.shopee-carousel__arrow {
    background: #1E2124;
}
.shopee-item-card__badge-wrapper,
.shopee-badge--promotion__label-wrapper,
.shopee-badge--promotion__label-wrapper * {
    background-color: transparent;
}
.ofs-carousel__shop-cover-image {
    background-color: #fff;
    background-size: 95%;
    border: 10px solid #484B51;
    box-sizing: border-box;
    border-radius: 20px;
}
.cart-drawer__view-cart-btn-wrapper,
.navbar-notification-drawer__view-cart-btn-wrapper {
    background-color: #2F3136;
}

/* 右上角購物車 */
.cart-drawer-product__name {
    color: rgba(255, 255, 255, .96);
}
.cart-drawer-product__variation {
    color: rgba(255, 255, 255, .74);
}
.cart-drawer__title,
.navbar-notification-drawer__title {
    color: rgba(255, 255, 255, .46);
}
.cart-drawer-product .cart-drawer-product__delete-btn > .shopee-button-no-outline,
.shopee-cart-item__action {
    color: rgba(255, 255, 255, .46);
}
.cart-drawer-product .cart-drawer-product__delete-btn > .shopee-button-no-outline::before,
.shopee-cart-item__action::before {
    content: "❌";
}
.cart-drawer-product:hover .cart-drawer-product__delete-btn > .shopee-button-no-outline::before,
.shopee-cart-item:hover .shopee-cart-item__action::before {
    color: rgb(245, 0, 0);
}
.cart-drawer-product:hover .cart-drawer-product__delete-btn > .shopee-button-no-outline {
    color: rgba(255, 255, 255, 1.0);
}
.cart-drawer__content {
    background-color: #2F3136;
}
.cart-drawer-product:hover {
    background-color: #42464D;
}
.notification-list__link {
    background-color: #2F3136
}
.footer__category-list-header,
.shopee-footer-section__heading,
.footer__block__content__title,
.daily-discover-theme__title {
    color: #fff;
}
.daily-discover-theme__subtitle {
    color: #FEEBDF;
}
#main .shopee-top div.container-wrapper div.container.header-with-search div.header-with-search__search-section div.hot-words div.hot-words__list a.hot-words__item {
    color: #FFF;
}
body,
p,
*[class$="content"],
*[class$="content__detail"],
*[class*="carousel"] {
    color: #C0C1C2 !important;
}
body {
    background-color: #36393E;
}
a:link,
a:visited {
    color: #EDEDED
}
a:link[class$="--clickable"],
a:visited[class$="--clickable"] {
    color: #DE5A1B;
    text-decoration: underline;
}
a:hover[class$="--clickable"] {
    color: #FFF;
    text-decoration: none;
}
.footer__category-list-item-sub > a {
    opacity: 0.8;
}
.footer__social-links {
    filter: brightness(1.5);
}
.footer__copyright {
    color: rgba(255, 255, 255, .8);
    cursor: default;
}
.footer__copyright::before {
    content: "ィ ";
}
.footer__copyright::after {
    content: "\A ㇿ 好冷酒XD領域™ 所有人 hollen9 設計「黑皮蝦」。";
    white-space: pre;
}

/* ROUTE: /user/notifications/orders */
.notification-card__button .shopee-button-outline {
    background-color: #484B51;
    color: rgba(255, 255, 255, .8);
    border: 1px solid rgba(255, 255, 255, .09);
}
.notification-card__info {
    color: rgba(255, 255, 255, .54);
}
.notification-card--group-secondary .notification-card__title {
    color: rgba(255, 255, 255, .90);
}

/* ROUTE: /user/purchase */
.purchase-empty-order__container,
.purchase-list-page__tabs-container,
.user-page-side-menu,
.notification-page,
.coin-transaction-page__empty-page-wrapper,
.coin-transaction-page__header,
.order-detail-page__order-content-wrapper,
.payment-detail__container,
.order-detail-page__delivery__container-wrapper,
.notification-card--group-secondary,
.order-card__content-wrapper,
.shopee-order-detail__coin-info {
    background-color: #484B51;
}
.order-detail-header {
    background-color: #2B2D31;
}
div[class$="-header__back-button"] {
    color: rgba(255, 255, 255, .54);
}
.purchase-state-bar__item__text {
    color: rgba(255, 255, 255, .8);
}
.purchase-state-bar__item__date {
    color: rgba(255, 255, 255, .26);
}
.purchase-state-bar__item__icon-wrapper {
    background-color: #2B2D31;
}
.order-detail-page__delivery__header__title,
.payment-detail__item__value-text {
    color: rgba(255, 255, 255, .8);
}
.order-detail-page__delivery__header__tracking-info,
.order-detail-page__delivery__shipping-address__detail,
.order-content__item__variation,
.order-content__item__quantity,
.payment-detail__item__description {
    color: rgba(255, 255, 255, .54);
}
.order-detail-page__delivery__logistic-info__item__time--highlighted,
.order-detail-page__delivery__logistic-info__item__description--highlighted,
.order-detail-page__delivery__shipping-address__shipping-name {
    color: rgba(255, 255, 255, .8);
}
.order-detail-page__delivery__logistic-info__item {
    color: rgba(255, 255, 255, .26);
}
.purchase-card-buttons__wrapper,
.purchase-card-buttons__show-button-wrapper,
.order-detail-header__action-container,
.order-detail-header__button-wrapper {
    background-color: #514E48;
    /*深橘色*/
}
.purchase-card-buttons__label-price {
    color: rgba(255, 255, 255, .8);
}
.order-content__header__delivery-status .icon-free-shipping-line {
    stroke: #fff;
}
.purchase-text-info__hover-point {
    color: #00EECE;
}
.order-delivery-status__time {
    background-color: #2F3136;
}
.user-page-side-menu__link:hover,
.user-page-side-menu__link-wrapper--selected > .user-page-side-menu__link {
    background-color: #514D48;
}
.user-page-side-menu__link,
.user-page-side-menu__link:hover {
    border-color: transparent;
}
.notification-page__divider--transparent {
    border-bottom: 1px solid #3E4146;
}
.purchase-list-page__tab {
    color: #72767D;
}
.purchase-list-page__tab:hover:not(.purchase-list-page__tab--selected) {
    background-color: #5C6068;
}
.purchase-list-page__tab--selected {
    color: #FF5722;
    cursor: default;
}
.purchase-list-page__tab-count {
    color: #fff;
}
.purchase-text-info {
    color: #fff;
}
.shopee-button-solid--disabled,
.shopee-button-solid--disabled:hover {
    background-color: rgba(0, 0, 0, .16);
    color: rgba(0, 0, 0, .74);
}
.order-content__container {
    color: rgba(255, 255, 255, .8);
}

/* 請在YYYY-MM-DD前完成貨到付款 */
.purchase-text-info__hover-point {
    padding: 0 2px;
}
.purchase-text-info__tooltip-content {
    background-color: #2F3136;
}
.shopee-arrow-box__arrow-inner {
    border-bottom: .8rem solid #2F3136;
}
.shopee-button-outline {
    background-color: #484B51;
}
.shopee-button-outline span {
    color: #fff;
}
.shopee-button-outline.shopee-button-outline--primary.shopee-button-outline--fill span {
    color: #fff !important;
}
.shopee-button-outline.shopee-button-outline--primary.shopee-button-outline--fill:hover {
    background-color: #3D170A;
}
.shopee-button-outline.shopee-button-outline--primary.shopee-button-outline--fill:hover span {
    color: #ff5722 !important;
}
.shopee-button-outline--normal {
    background-color: #32363B;
    color: #A9AAAC
}
.shopee-button-outline--normal:hover {
    background-color: #000;
    color: #FFF
}
.shopee-border-bottom-dotted-circle__circle {
    background: #0A0A0A;
}
.purchase-empty-order__text {
    color: rgba(255, 255, 255, .8);
}

/* ROUTE: /user/coin/ */
.coin-transaction-page__tabs-container {
    background-color: #484B51;
}
.coin-transaction-page__list__item {
    background-color: #484B51;
}
.coin-transaction-page__tab {
    color: #72767D;
}
.coin-transaction-page__tab:hover,
.coin-transaction-page__tab--selected {
    color: #ff5722;
}

/* ROUTE: /cart */
.cart-page-header-wrapper {
    background-color: #2A2C31;
}
.shopee-cart-page-message {
    background-color: #323233;
    border-color: #35BEFF;
    color: #CCCCCC;
}
.shopee-cart-page-message .shopee-svg-icon {
    color: #35BEFF;
}
.shopee-header-section--simple .shopee-header-section__header__title {
    color: rgba(255, 255, 255, .54);
}
.shopee-cart-product-header {
    background-color: #484B51;
}
.shopee-cart-product-header__product,
.shopee-cart-product-header__unit-price,
.shopee-cart-product-header__quantity,
.shopee-cart-product-header__total-price,
.shopee-cart-product-header__action {
    color: rgba(255, 255, 255, .8);
}
.shopee-cart-page-footer--opc {
    margin-top: 0;
    height: 120px;
    height: 12rem;
}
.shopee-cart-page-footer {
    margin-top: 0;
    height: 203px;
    height: 20.3rem;
}
.shopee-cart-shop-header__redeem-coins,
.shopee-cart-page__main-wrapper {
    color: rgba(255, 255, 255, .8);
}
.shopee-cart-variation-dropdown__content {
    background-color: #2F3136;
}
.shopee-cart-variation-dropdown__label {
    color: rgba(255, 255, 255, .74);
}
.shopee-cart-page-footer__promo-code-button {
    border-color: rgba(255, 255, 255, .34);
    color: #9EA1A5;
}
.shopee-cart-page-footer__promo-code-button:hover {
    border-color: #FF5722;
    color: #FF5722;
}
.shopee-cart-shop-section__separator {
    /*filter: sepia(1) saturate(10000%) hue-rotate(2deg);*/
    filter: sepia(1) brightness(0.5);
}
.shopee-cart-item__unit-price--before {
    color: rgba(255, 255, 255, .44);
}
.shopee-cart-item__unit-price--after {
    color: rgba(255, 255, 255, .8);
}
.shopee-active-cart {
    background-color: #484B51;
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
}
.shopee-cart-page-footer {
    border-top-left-radius: 0;
    border-top-right-radius: 0;
    border-bottom-right-radius: 2px;
    border-bottom-left-radius: 2px;
    background-color: #545860;
    border: 5px solid #484B51;
}
.shopee-cart-page-footer__promo-code-input .shopee-input-with-validator {
    border-color: rgba(255, 255, 255, .34);
    background-color: #484B51;
}
.shopee-cart-page-footer__promo-code-input .shopee-input-with-validator > input {
    background-color: transparent;
}
.shopee-cart-page-footer__promo-code-input .shopee-input-with-validator--focus {
    border: 1px solid #FF5722;
    background-color: #6C7076;
}
.shopee-cart-page-footer__coin-tooltip-content {
    background-color: #2F3136;
}

/* ROUTE: /checkout/ 檢查確認訂單 */
.checkout-header__wrapper {
    background-color: #1E2124;
}
.checkout-product-ordered-list__title,
.checkout-product-ordered-list-item__product-name,
.checkout-payment-method-view__current-title,
.checkout-payment-method-view__current-type {
    color: #CCCCCC;
}
div[class^="checkout-product-ordered-list-item__shipping-selected--"],
.checkout-product-ordered-list-item__shipping-selected-address,
.checkout-product-ordered-list-item__header,
.checkout-product-ordered-list-item__shipping-selected-edt,
.checkout-order-summary__row {
    color: rgba(255, 255, 255, .74) !important;
}
.checkout-order-summary__row--large .checkout-order-summary__row-content {
    color: #FF5728 !important;
}
.checkout-product-ordered-list-item__shipping-price {
    color: #DEDEDE;
}
.checkout-product-ordered-list-item__shop-icon svg {
    fill: #EDEDED;
}
.checkout-product-ordered-list__header-block,
.checkout-product-ordered-list-item {
    background-color: #484B51;
}
.checkout-product-ordered-list-item__review,
.checkout-product-ordered-list-item__actions {
    background-color: #454952;
}
.checkout-product-ordered-list-item__shipping-change,
.checkout-payment-method-view__change-btn,
.page-checkout .stardust-button:not(.checkout-order-summary__place-order-button) {
    border: 1px solid rgba(255, 255, 255, .34);
    border-radius: .2em;
    color: #9EA1A5;
    background-color: #484B51;
    padding: 8px 12px;
    display: block;
}
.checkout-product-ordered-list-item__shipping-change:hover,
.checkout-payment-method-view__change-btn:hover,
.page-checkout .stardust-button:hover:not(.checkout-order-summary__place-order-button) {
    border: 1px solid #C14C26 !important;
    color: #C14C26;
    background-color: #4C5057;
}
.page-checkout .stardust-button svg.stardust-icon {
    fill: #9EA1A5;
}
.page-checkout .stardust-button:hover svg.stardust-icon {
    fill: #C14C26;
}
.checkout-payment-method-view {
    background-color: #484B51;
}
.checkout-order-summary {
    border-top: none;
    background-color: #333230;
}
.checkout-product-ordered-list-item__leave-message .stardust-input input.stardust-input__element {
    border-color: rgba(255, 255, 255, .34);
    background-color: #484B51;
}
.checkout-product-ordered-list-item__leave-message .stardust-input input.stardust-input__element:focus {
    border: 1px solid #FF5722;
    background-color: #6C7076;
}
.logistics-selection-modal,
.logistics-selection-modal__container .shopee-modal__content {
    background: #2F3136;
}
.address-modal__helper_text,
.address-modal__choose_logistic_type_title {
    color: rgba(255, 255, 255, .74);
}
.shopee-input-with-status input {
    background-color: transparent;
}
.shopee-modal__overlay {
    background-color: rgba(0, 0, 0, .6);
}
.logistics-selection-modal__footer {
    background: #333230;
}
.logistics-selection-channels .stardust-radio-button .stardust-radio-button__outer-circle {
    background-color: #1E2124;
}
.logistics-selection-channels .stardust-radio-button .stardust-radio-button__outer-circle > .stardust-radio-button__inner-circle {
    background: #FF5722;
}
.shopee-select-with-status__holder {
    background-image: linear-gradient(180deg, #2F3136, #1E2124);
}
.shopee-select-with-status__dropdown {
    background-image: linear-gradient(0deg, #2F3136, #1E2124);
}
.address-modal__checkboxes .shopee-checkbox {
    color: unset;
}

/* 偽元素-placeholder 選擇器 */
.page-checkout input::-webkit-input-placeholder {
    /* WebKit, Blink, Edge */
    color: #7A716C;
}
.page-checkout input:-moz-placeholder {
    /* Mozilla Firefox 4 to 18 */
    color: #7A716C;
    opacity: 1;
}
.page-checkout input::-moz-placeholder {
    /* Mozilla Firefox 19+ */
    color: #7A716C;
    opacity: 1;
}
.page-checkout input:-ms-input-placeholder {
    /* Internet Explorer 10-11 */
    color: #7A716C;
}
.page-checkout input::-ms-input-placeholder {
    /* Microsoft Edge */
    color: #7A716C;
}
.page-checkout .shopee-select-with-status__placeholder,
.page-checkout .shopee-select-with-status__value {
    color: #7A716C;
}

/* ROUTE: /flash_sale */
.page-flash-sale__banner-wrapper,
.flash-sale-header-with-countdown-timer--landing-page {
    background-color: #2A2C31;
}

/* ROUTE /s/ */
.recommend-item-panel {
    background-color: #484B51;
}
.shopee-button-outline {
    text-align: center;
}
.shopee-button-outline * {
    color: #444445 !important;
}

/* ROUTE: /search/? */
.search-page {
    background-color: initial;
}
.shopee-recommend-search {
    background-color: #2F3136;
}
.shopee-recommend-search__label {
    color: rgba(255, 255, 255, .8);
}
.shopee-recommend-search__word {
    filter: grayscale(.4);
}
.shopee-sort-by-options {
    background-color: #484B51;
}
.shopee-sort-by-options__option:hover {
    background-color: #7E8084;
}
.shopee-sort-by-options .shopee-dropdown__entry {
    background-color: #484B51;
    color: rgba(255, 255, 255, .8);
}
.shopee-sort-by-options .shopee-dropdown__entry:hover {
    background-color: #7E8084;
    color: rgba(255, 255, 255, .8);
}
.shopee-sort-by-options .shopee-dropdown__entry--selected {
    color: rgba(255, 255, 255, .8);
}
.shopee-sort-by-options__option[class$="selected"] {
    color: #FF5722;
}
.shopee-page-controller .shopee-button-no-outline {
    color: #C0C1C2;
}
.shopee-page-controller .shopee-button-no-outline:hover {
    color: #FF5722;
}
.shopee-page-controller .shopee-button-no-outline.shopee-button-no-outline--non-click:hover {
    color: #C0C1C2;
    cursor: not-allowed;
}
.shopee-page-controller .shopee-icon-button svg:hover {
    color: #FF5722;
}
.shopee-price-range-filter__input {
    background-color: #514B48;
    color: #C0C1C2;
}
.shopee-price-range-filter__input:focus {
    border: 2px solid #FF5722;
    box-sizing: border-box;
}
.shopee-facet-filter__main-facet__text,
.shopee-logistics-filter__toggle-btn,
.shopee-logistics-filter .shopee-checkbox__control,
