css:
@-moz-document domain("thepiratebay.org") {
:root {
    --black: #1c2526;
    --black-lighter: #253132;
    --black-darker: #141a1a;
    --light: #fff;
    --light-darker: #666;
    --accent: #54bb6f;
}


/* hide */
div#icons, footer, #howdl, #chb, #navlinks, #subm, div#foot, label, select#category, h2, #TPBlogo, .submitbutton, #sky-banner, #sky-center, .sortby, .viewswitch, table#searchResult img, #main-content iframe {
	display: none;
}


/* body */
body {
    background: var(--black);
    color: var(--light) !important;
	margin: 0;
    padding: 5em;
    font-family: 'Proxima Nova' !important;
}


/* input */
input {
	background: var(--black);
    color: var(--light) !important;
    border: 0;
}


/* links */
a {
	text-decoration: none !important;
    color: inherit;
    border: 0;
}

a:hover, .detDesc a:hover {
	color: var(--accent);
    border: 0;
}

#browseContainer a {
	color: var(--light);
    border: 0;
}

#browseContainer a:hover {
	color: var(--accent);
    border: 0;
}

#browseContainer {
	padding-top: 5em !important;
}


/* text */
b, a, h3 {
	color: var(--light);
}


/* homepage */
#fp h1 a {
	background: url('https://x.eti.tf/pOd8u.jpg') no-repeat center 85%;
    box-shadow: inset 0 0 0 1920px rgba(28, 37, 38, .4);
    background-size: 100%;
    width: 100%;
}

#fp h1 a span:after {
    visibility: visible;
    font-size: 3em;
    color: var(--light);
    top: 2.2em !important;
    left: -3%;
    position: relative;
	content: "WE SAIL FORWARD";
}

div#fp h1 {
	margin: 0;
}

#fp form {
	margin-top: 4em;
}

#fp #inp input {
	padding: 1.5em;
    background: var(--black-lighter);
    border-bottom: .1em solid var(--accent);
}

#fp #inp input:focus {
    outline: 0 !important;
}

#inp span a {
	border: 0;
    color: var(--light);
}

#q .inputbox {
	outline: none;
}


/* pages */
div#header {
	background: url('https://x.eti.tf/pOd8u.jpg') no-repeat center 85%;
    box-shadow: inset 0 0 0 1920px rgba(28, 37, 38, .4);
    background-size: 100%;
    width: 100%;
    padding: 12.5em 0;
}

#q .inputbox, .submitbutton {
	margin: 1.2em 0 0 0;
    padding: 1em;
}

#q .inputbox {
	width: 100%;
}

#q {
	color: var(--white) !important;
    width: 80%;
    margin: 0 auto;
}

#q a {
    top: 15px;
}


/* blog */
#blog {
	background: var(--black-lighter);
    padding: 5em 5%;
    margin-top: 10em;
}

#blog a, .post {
	color: var(--light);
    border: 0;
}

div.meta p a img {
	content: url('https://x.eti.tf/7QCvF.png');
}


/* list */
tbody, table#searchResult th, table#searchResult td, thead#tableHead a {
	background: var(--black);
    color: var(--light) !important;
    border: 0;
}

abbr[title], acronym[title] {
    text-decoration: none;
}

th {
	text-align: right;
}

table#searchResult tr {
	background: inherit;
    color: inherit;
}

#content {
	padding: 5em 0 0 0;
}

.detDesc {
	color: var(--light-darker);
}

table#searchResult td.vertTh {
	display: none !important;
}



/* submission */
#detailsframe #title {
	background: var(--black-lighter);
    border: 0;
    padding: 2em;
}

#detailsframe {
	background: var(--black);
	border: 0;
}

#details {
	background: var(--black);
    padding: 3em 1em;
    margin: 0 auto;
    width: 100%;
}

#details dt, #details dd {
	color: var(--white);
    border: 0;
}

dl {
	margin: 0 !important;
}

.download a {
	color: var(--accent) !important;
    border: 0;
    background: url('https://x.eti.tf/YXPcO.png') 0px no-repeat !important;
}

.nfo {
	background: var(--black-lighter);
    border: 0;
    font-family: 'Roboto Mono', monospace;
    line-height: 1.2em;
}

#details a.comments {
	background: url('https://x.eti.tf/7QCvF.png') 0px 0px no-repeat;
}

#comments .comment {
	background: var(--black);
}

div.comment a {
	color: var(--accent);
}

#details h4, #comments p.byline {
	color: var(--light);
}


}
