css:
@-moz-document domain("forum.paokmania.gr") {
     body{
       color: #bbb;
    }   
    
    #wrap,
    .forumbg,
    .forabg{
    	background-color: #171717 !important;
        background-clip: border-box !important;
        background-origin: padding-box !important;
        background-attachment: scroll !important;
        background-repeat: repeat !important;
        background-size: auto !important;
        background-position: left top !important;
    	background-image: none;
        color: #bbb;
		box-shadow: none;
	}
    
    #cp-main .panel,
    .panel{
        background-color: #333 !important;
    }
    
    .forumbg,
    .forabg{
        box-shadow:0px 0px 20px 3px black;
    }
    
    a:visited, 
    .postprofile a:visited,
    li.row:hover a:visited{
    	color: #666666;
	}
    
    .navbar {
        background-color: #00000035;
        color: #bbb;
    }
    
    .bg1, .bg2, .bg3, .bg4,
    ul.forums{
		background-color: transparent;
    	background-image: none;
		box-shadow: none;
	}
    
    li.row:hover {
		background-color: #00000045;
    }
    
    a:link,
    .postprofile a:link,
    li.row:hover a:link,
    .inputbox:focus,
    dt label:hover{
    	color: #bbe;
    }
    
    h2,
    .content,
    .postbody,
    .postprofile,
    #message-box textarea,
    .inputbox{
        color: #c3c3c3;
    }
    
    h3 {
     	color: #989898;   
    }
    
    .postprofile strong,
    .content h2, 
    .panel h2,
    .panel,
    dd label,
    .post:target .content,
    .post:target h3 a{
        color: #eee;
    }
    
    blockquote {
        background-color: #1b1b1b;
    }
    
    blockquote blockquote{
        background-color: #272727 !important;
    }
    
    hr.divider {
     	display: none   
    }
    
    hr {
		border-top-color: #2CaCaC;
    }
    
    .post {
     	border-bottom-style: solid;
        margin-bottom: 1px;
        padding-bottom: 15px;
        border-width: 1px;
        border-bottom-color: #666666	;
    }

    ::-moz-selection {
  		background-color: #11148f !important;
        background: #34495e !important;
  		color: white !important;
	}

	::selection {
  		background-color: #11148f !important;
        background: #34495e !important;
  		color: white !important;
	}
    
    .inputbox {
     	background-color: #111126;   
    }
    
    span.corners-top, span.corners-top span, span.corners-bottom, span.corners-bottom span {
     	background-image: none !important;
    }
}
