}

.button-link {
    background: rgb(251, 252, 252);
    border-bottom: 1px solid #C7C7C7 !important;
    border: 0;
    color: #7D7D7D;
    font-weight: 100;
    font-size: 11px;
    text-transform: uppercase;
    transition: 0.15s ease-out;
    box-shadow: 4px 4px 6px -1px #e1e1e1;
}

.button-link:hover {
    border-bottom: 1px solid #d95753 !important;
    background: white;
    color: black;
}

.button-link:hover > .icon-sm {
    padding-right: 5px;
    color: black;
}

.button-link .on {
    border-radius: 10px;
    transition: 0.5s cubic-bezier(0, 0, 0, 1);
    background-color: transparent;
}

.button-link .on .icon-sm {
    font-size: 10px;
    transition: 0.5s cubic-bezier(0, 0, 0, 1);
    color: #61bd4f;
}

.button-link:hover .on {
    background-color: transparent;
}

.button-link:hover .on .icon-sm {
    font-size: 14px;
}

.card-label-selectable.selected {
    margin-left: 8px;
}

.card-label-selectable {
    transition: padding 85ms, margin 85ms, box-shadow 85ms;
}

.editable .current {
    cursor: auto;
}

.editable .current a {
    text-decoration: none;
    color: #d95753;
    transition: 0.3s;
}

.editable .current a:hover {
    color: #444;
}

.phenom {
    border: 0;
}

.window-main-col .window-module {
    border-top: 1px solid rgba(0, 0, 0, 0.12) !important;
}

.window-module.add-comment-section {
    border-top: 0 !important;
}

