css:
@-moz-document domain("pawoo.net") {
/*
/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_
_/
_/      Bright Pawoo Theme v2
_/      by Acro(@hinayuzuk) 
_/
/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_/_
*/
/* 背景関連 */

.ui {
    background: /*[[bgcolor]]*/;
}
/* メインカラー（デフォルトの色は#689F38） */

.tabs-bar,
.drawer__header,
.search-results__header,
.drawer__block,
.trend-tags__header,
.suggestion-tags__button,
::-webkit-scrollbar-thumb,
.column-header,
.column-header__button,
.column-header__button.active,
.column-header__button.active:hover,
.react-toggle--checked .react-toggle-track,
.column-header__back-button,
.column-link:hover,
.column-back-button,
.dropdown--active .dropdown__content>ul>li>a:hover,
.media-spoiler,
.video-error-cover,
.privacy-dropdown__option.active,
.privacy-dropdown__option:hover,
.time-limit-dropdown__option.active,
.time-limit-dropdown__option:hover,
.loading-bar,
.upload-area__background,
.status__content__spoiler-link,
.pawoo-subcolumn__header,
.account__section-headline,
.navigation-bar,
.announcements li,
.account__header .account__header__fields dt,
.keyboard-shortcuts kbd,
.pawoo-navigation-column .pawoo-navigation-column__subcolumns .pawoo-navigation-column__subcolumns__button>* {
    background: hsl(88, 50%, 53%) !important;
}
.search__input:focus,
.status-card {
    border: 1px solid hsl(88, 50%, 53%);
}
/*メインカラーのホバー時（マウスを乗せた時）*/

.drawer__header a:hover,
.tabs-bar__link.active,
.account__section-headline a.active {
    background: hsla(0, 0%, 100%, .25);
}
/*メインカラー部分の文字色（デフォルトの色は#FFFFFF）*/

.drawer__tab,
.suggestion-tags__button,
.search-results__header,
.column-header__button,
.column-header__button:hover,
.column-header__back-button,
.column-header,
.column-link:hover,
.column-back-buttonm,
.trend-tags__header,
.character-counter,
.column-back-button,
.upload-area__content,
.status__content__spoiler-link,
.account--action-button .icon-button,
.pawoo-subcolumn__header,
.media-spoiler:active, .media-spoiler:focus, .media-spoiler:hover,
.media-spoiler,
.account__section-headline a,
.account__section-headline a.active,
.announcements li,
.announcements__link,
.announcements__body__dismiss .icon-button,
.account__header .account__header__fields dt,
.column-header>.column-header__back-button,
.keyboard-shortcuts kbd {
    color: #ffffff;
}
/* メインカラー部分のサブ文字色（薄文字） */

.navigation-bar,
.tabs-bar__link.active,
.column-header.active .column-header__icon {
    color: hsla(0, 0%, 100%, .75);
}
/* サブカラー（デフォルトの色は#F5F5F5）*/

.drawer__inner.darker,
.search__input,
.search__input:focus,
.compose-form__buttons,
.drawer__inner,
.trend-tags,
::-webkit-scrollbar-track,
.column-header__collapsible-inner,
.column>.scrollable,
.column-subheading,
.column-link,
.status__wrapper.pinned,
.dropdown--active .dropdown__content>ul,
.dropdown--active .dropdown__content>ul>li>a,
.empty-column-indicator,
.reply-indicator,
.detailed-status,
.detailed-status__action-bar,
.status.status-direct,
.status-card,
.status-card:hover,
.status-card__image,
.getting-started,
.focusable:focus,
.regeneration-indicator,
.status__wrapper.featured,
.account__header .account__header__fields dd,
.status__wrapper[data-featured=true],
.column-inline-form {
    background: #f5f5f5 !important;
    transition: ease .2s;
}
/* サブカラー部分の文字色（黒） */

.search__input,
.search__icon .fa,
.account__display-name strong,
.status__display-name strong,
.column-settings__section,
body,
.column-link,
.account__action-bar__tab strong,
.empty-column-indicator,
.column-header__collapsible,
.icon-button,
.detailed-status__display-name strong,
.simple_form .check_boxes .checkbox label,
.simple_form .input.with_label.boolean .label_input>label,
.regeneration-indicator {
    color: #424242;
}
/* サブカラー部分のサブ文字色（薄文字） */

.suggestion-tags__description,
.search-results__hashtag:active,
.search-results__hashtag:focus,
.search-results__hashtag:hover,
.setting-toggle__label,
.setting-text,
.getting-started p,
.status__prepend,
.icon-button.inverted,
.icon-button:active,
.icon-button:focus,
.icon-button:hover {
    color: hsla(0, 0%, 0%, .7);
}
/* カラー文字（メインカラーと同じ色） */

.suggestion-tags__name,
.account .account__display-name,
.search-results__hashtag,
.status__content a.hashtag,
.status__content a span.ellipsis,
.reply-indicator__content a,
.status__content a,
.dropdown--active .icon-button,
.account__header .account__header__username,
.icon-button.active,
.status__display-name,
.icon-button.inverted.active,
.text-icon-button.active,
.icon-button.inverted:active,
.icon-button.inverted:focus,
.icon-button.inverted:hover,
display-name__account,
.notification__display-name:hover,
.suggestion-tags__name *,
.detailed-status__display-name,
.regeneration-indicator__label strong,
.account__header__fields a {
    color: hsl(88, 50%, 53%);
    transition: ease .4s;
}
/* アクセントカラー */

.button {
    background-color: hsl(100, 40%, 50%);
}
.button:active,
.button:focus,
.button:hover {
    background-color: hsl(100, 50%, 40%);
}
/* パーツの修正（この部分は基本的に弄らなくても大丈夫です） */

.account__header {
    background: none;
    position: relative;
    z-index: 1;
    overflow: hidden;
}
.account__header:after {
    content: " ";
    background: inherit;
    position: absolute;
    top: -4px;
    left: 0;
    width: 105%;
    height: 105%;
    filter: blur(3px);
    z-index: 3;
}
.account__header .account__header__fields dl {
    border-top: none;
    border-bottom: 1px solid #424242;
}
.account__header .account__header__fields dt {
    border-right: 1px solid #424242;
}
.account__header>div {
    background: hsla(0, 0%, 0%, .35) !important;
    position: relative;
    z-index: 5
}
.tabs-bar__link,
.search-results__header,
.tabs-bar__link.active,
.account__header__fields dl {
    border-bottom: none;
}
.detailed-status__action-bar {
    border-top: 1px solid hsla(0, 0%, 0%, .1);
    border-bottom: 1px solid hsla(0, 0%, 0%, .1);
}
.autosuggest-textarea__textarea,
::-webkit-scrollbar-thumb {
    border-radius: 0;
}
.compose-form__buttons {
    box-shadow: none;
}
.compose-form__buttons,
.hashtag-editor,
.pawoo-navigation-column .pawoo-navigation-column__subcolumns .pawoo-navigation-column__subcolumns__button>* {
    border-top: 1px solid hsla(0, 0%, 0%, .1);
}
.suggestion-tags__body li,
.setting-text,
.status,
.account,
.column-subheading,
.column-link {
    border-bottom: 1px solid hsla(0, 0%, 0%, .1);
}
.search__input {
    box-sizing: border-box;
    border: 1px solid hsla(0, 0%, 100%, 0);
    padding: 9px 10px;
}
.search__input:focus,
.react-toggle--checked .react-toggle-thumb{
    border: 1px solid #8BC34A;
}
.column-link {
    padding: 10px;
}
.account--action-button {
    left: 260px
}
.account--follows-info {
    right: 180px
}
.character-counter--over {
    color: #ff5050;
}
.empty-column-indicator {
    box-shadow: 0 0 10px 2px rgba(0, 0, 0, .1) inset
}
.upload-area__content {
    border: 0;
}
.keyboard-shortcuts kbd {
    border-radius: 2px;
    border: none;
    padding: 8px;
}
/* 設定ページ v1 */

body.admin {
    background: #fff
}
.admin-wrapper .content h2,
.simple_form input[type="email"],
.simple_form input[type="number"],
.simple_form input[type="password"],
.simple_form input[type="text"],
.simple_form textarea {
    color: #212121
}
.simple_form input[type="email"],
.simple_form input[type="number"],
.simple_form input[type="password"],
.simple_form input[type="text"],
.simple_form textarea {
    border: 1px solid #eee;
    border-radius: 0;
    border-bottom: 3px solid #8bc34a
}
.text.optional {
    min-height: 90pt
}
.admin-wrapper .content>p,
.admin-wrapper .sidebar ul a,
.simple_form .input.boolean label,
.simple_form .input.check_boxes .label_input>label,
.simple_form .input.file .label_input>label,
.simple_form .input.radio_buttons .label_input>label,
.simple_form .input.radio_buttons .radio label,
.simple_form .input.select .label_input>label,
.simple_form span.hint,
.user_filtered_languages>label,
a.table-action-link {
    color: #424242
}
.simple_form .block-button,
.simple_form .button,
.simple_form button {
    background: #8bc34a
}
.simple_form .block-button:hover,
.simple_form .button:hover,
.simple_form button:hover,
.table td, .table th {
    background: #dcedc8
}
.table td,
.table th {
    border-top: 1px solid #eee
}
.table>thead>tr>th {
    border-bottom: 2px #8bc34a
}
.admin-wrapper .content hr {
    border-bottom: 1px solid #dcedc8
}
.admin-wrapper .content h6 {
    color: #212121
}
.admin-wrapper .content .muted-hint a,
.table a {
    color: #8bc34a
}
.table>tbody>tr:nth-child(odd)>td,
.table>tbody>tr:nth-child(odd)>th {
    background: #dcedc8
}
.flash-message {
    background: #8bc34a;
    color: #fff;
    border-radius: 0;
    box-shadow: none
}
.button:disabled {
    background-color: #424242
}
.simple_form .warning,
.table-form .warning {
    background: 0;
    box-shadow: none;
    text-shadow: none;
    color: #424242;
    border: 1px solid #ff5050
}
.simple_form .warning a,
.simple_form .warning strong,
.table-form .warning a,
.table-form .warning strong {
    color: #ff5050
}
.admin-wrapper .sidebar-wrapper {
    background: #8bc34a
}
.admin-wrapper .sidebar .logo {
    filter: brightness(10)
}
.admin-wrapper .sidebar ul a.selected {
    background: #dcedc8
}
.admin-wrapper .sidebar ul ul a.selected {
    background-color: #4caf50
}
.admin-wrapper .sidebar ul a:hover,
.admin-wrapper .sidebar ul ul a.selected:hover {
    background: #8bc34a
}
.admin-wrapper .sidebar ul ul {
    background: #dcedc8
}
.admin-wrapper .sidebar ul a {
    border-radius: 0
}
.account__section-headline,
.account__action-bar {
  border: none;
}
.account__section-headline a.active:after, .account__section-headline a.active:before {
  border-color: transparent transparent #F5F5F5;
}
.announcements__link {
  border: 1px solid #fff;
}
#logout a,
#web a {
    color: #fff
}
}
