css:
@-moz-document domain('youtube.com') {
  



/* LESS GARBAGE */

#footer{
	display:none;
}


/* VIDEO THUMBNAILS */


/* no verified badges */
ytd-badge-supported-renderer {
    display:none;
}

/* smaller metadata */
#metadata-line.ytd-grid-video-renderer{
	font-size:1rem;
}

/* longer video titles */
ytd-grid-video-renderer.use-ellipsis #video-title.yt-simple-endpoint.ytd-grid-video-renderer{
    max-height:4.8rem;
}

/* 'watched' overlay */
yt-formatted-string.ytd-thumbnail-overlay-playback-status-renderer{
    display:none;
}

/* video length */
ytd-thumbnail-overlay-time-status-renderer{
    margin: 0;
    z-index:1;
    opacity:1;
    letter-spacing:0px;
    padding:1px 2px;
    border-radius:0;
    border-top-left-radius:2px;
}

/* more line space between video rows */
#items.ytd-grid-renderer > ytd-grid-video-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-channel-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-playlist-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-movie-playlist-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-movie-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-show-renderer.ytd-grid-renderer {
    margin-bottom:30px;
}




/* MAKE SIDEBAR SMALLER */



.guide-icon.ytd-guide-entry-renderer {
margin-right:15px;
}
  
yt-icon {
   max-width:20px;
}
  
#logo-icon.ytd-topbar-logo-renderer {
   max-width:none;
}

/* start further left */
#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer{
    	padding: 0 20px;
}

/* do not show number of new videos next to subscriptions */
.guide-entry-count.ytd-guide-entry-renderer {
 	display:none;    
}

/* lessen line height */
ytd-guide-section-renderer{
 	   --paper-item-min-height:35px;
}



  
}
