css:
@-moz-document domain("www.spanishdict.com") {
body {
    background-color: #282828;
    color: #eee;
}

textarea, .search-form-input {
    background-color: #222;
    color: #fff;
}
.search-form-input::-webkit-scrollbar {
    display: none;
}
.search-form-parts {
    background-color: #222;
    border-color: #888;
}
.search-component.search-form {
    background-color: #333;
}
.card {
    background-color: #2b2b2b;
    color: #eee;
    border-color: #888;
}
.dictionary-collins .ph .c {
    color: #eee;
}
.vtable-label, .vtable-title-text {
    color: #fff;
}
.vtable, .vtable-title, .vtable-word {
    border-color: #333;
}
.vtable tr:hover {
	background-color: #252525;
}
.conj-match {
	background-color: #1c1f2c;
}
.vtable-head-row {
    background-color: #252525;
}
.nav-content-item {
    background-color: #333;
    border-color: #444;
}
.nav-content-item.nav-active {
    background-color: #2b2b2b;
    border-left-color: #333;
}
.nav-content-item.nav-active a {
    color: #eee;
}
.nav-content-item a {
    color: #999;
}
.nav-content-item a:active, .nav-content-item a:hover {
    color: #eee;
}
.dictionary-entry a:not(.dictionary-neodict-translation-translation) {
    color: #fff;
}
.wotd-sidebar-word {
	color: #eee;
}
.sd-explore-link {
    color: #eee;
}
.sd-explore-link:hover {
    background-color: #252525;
}
.announcement {
    background-color: #161616;
    color: #faf2d0;
    border-color: #888;
}
.tooltip .tooltip-inner {
    background-color: #444;
    color: #fff;
    border-color: #888;
}
.fluencia-sidebar-title-short {
    color: #ddd;
}

.megaexamples-highlight {
    background-color: #3a3a3a;
}
.sd-phrase-row:hover {
    background-color: #252525;
}
.megaexamples-row {
    border-color: #393939 !important;
}
.megaexamples-row:hover {
    border-color: #555 !important;
}
.megaexamples-page-active {
    background-color: #1b1b1b;
}
.megaexamples-page:hover {
    background-color: #1b1b1b;
}
.dictionary-collins .entry .note, .dictionary-collins .entry .notes {
    background-color: #222;
}
.usage-note {
    background-color: #222;
}
.input-table-table {
    background-color: #333;
    border-color: #888;
}
.input-table-suggestion.selected, .input-table-suggestion:hover {
	background-color: #444;
}
.input-table-table td table td {
    border-color: transparent;
}

.vtable-wrapper::-webkit-scrollbar-track {
    background-color: #555;
}
.vtable-wrapper::-webkit-scrollbar-thumb {
    border-color: #555;
}
.footer {
    color: #ddd;
}
.footer-link a {
	color: #bbb;
}
.megaexamples-pair-part+.megaexamples-pair-part {
    color: #888;
}
.dictionary-entry em {
    color: #888;
}
.rw .answers-block .post-block.accepted {
    background-color: #2a4a2e;
}
.rw .vote-box .vote span {
    background: #2b2b2b;
}
.rw .vote-box .vote {
    background: #444;
}
.rw .comments-box .comments {
    background-color: #333;
    border-color: #444;
}
.rw .nav-tabs>.active>a, .rw .nav-tabs>.active>a:hover {
	background-color: #2b2b2b;
    border-color: #444;
    border-bottom-color: transparent;
    color: #eee;
}
.rw .nav-tabs {
    border-color: #444;
}
.rw blockquote {
    background-color: #333;
    border-color: #444;
}
.rw .post-block .info li, .rw .post-block .info a {
    color: #888;
}
.rw .post-block .bottom {
    border-color: #444;
}
.rw .answers-block .post-block {
    border-color: #444;
}
.rw .comments-box .comments ul li {
    border-color: #444;
}
.rw .page-header {
    border-color: #444;
}
.footer-wrapper {
    border-color: #888;
}
.footer-meta {
    border-color: #888;
}

.lg-title-text {
    color: #fff;
}
.lg-breadcrumb {
    background-color: #333;
    border-color: #888;
}
.lg-breadcrumb-container {
    color: #eee;
}
.lg-article-link {
    line-height: 21px;
}
div.sdmd {
	color: #eee;
}
.sdmd-quick-answer-box {
    background-color: #222;
    border-color: #444;
}
.sdmd-examples tr:nth-child(odd), .sdmd-table tr:nth-child(odd) {
    background-color: #303030;
}
.sdmd-accent-box-info .sdmd-accent-box-text {
    background-color: #222;
    border-color: #444;
}
.sdmd-accent-box-alert .sdmd-accent-box-text {
    background-color: #222;
    border-color: #444;
}
.sdmd-examples-container, .sdmd-table-container {
    border-color: #444;
}
.sdmd-table td {
    border-color: #444;
}
.sdmd-table-header th {
    border-color: #444;
}
.sdmd-text-accent-1 {
    color: #805ebd;
}
.sdmd-text-accent-2 {
    color: #499a41;
}
.lang-tabs>li.active>a {
    background-color: #2b2b2b;
    color: #eee;
}
.lang-tabs>li>a:focus, .lang-tabs>li>a:hover {
    background-color: #333;
    color: #eee;
}
.sdmd-accent-box-extrainfo .sdmd-accent-box-text {
	background-color: #424648;
    border-color: #444;
}
a.translate-quicktip {
    color: #ddd;
}

a.card-title {
    color: #eee;
}

.vtable-word-text {
    color: #eee;
}
.inverted--dyyam {
    background-color: #282828;
}
.bubble--3j0Ro {
    background-color: #1a1a1a;
}
.footerContainer--1VtLp {
    background-color: #222;
}
.footerContainer--1VtLp a {
    color: #eee;
}


body::-webkit-scrollbar {
    width: 0.9em;
}
body::-webkit-scrollbar-track {
    background-color: #111;
}
body::-webkit-scrollbar-thumb {
    background-color: #444;
}
}
