css:
@-moz-document domain("magister.net") {
/* Copyright (c) 2017/2020 Trigstur */
/* https://raw.githubusercontent.com/Trigstur/MagisterMagic/master/LICENSE */
/* ==> Coloring Book <== */
:root {
    --bck1: #17242d;
    /* Main Background */
    --txt1: #fed766;
    /* Text On Background */
    --alttxt1: #041224;
    /* Alternative Text On Background */
    --box1: #212d40;
    /* Box 1 Background */
    --boxtxt1: #fff;
    /* Text on Box 1 Background */
    --innerbox: #212d40;
    /*  Color InnerBox */
    --hovinnerbox: #000b21;
    /* Inner Box Hover */
    --althovinnerbox: #fed766;
    /* Reversed Inner Box Hover */
    --altinnerbox: #fed766;
    /* Reversed Color InnerBox */
    --altinnerbox2: #ddd;
    --innerborder: #17242d;
    /* Main Inner Border */
    --innercontext: #17242d;
    /* Inner information */
    --altinnercontext: #fff;
    /* Reversed Inner information */
    --alert: #fed766;
    /* Alert Color */
    --altalert: #2d3047;
    /* Alert Reversed Color */
    --sidenav: #212d40;
    /* Color Sidenav */
    --primary-background: var(--sidenav);
    --altsidenav: #fed766;
    /* Reversed Color Sidenav */
    --secondary-background: var(--altsidenav);
    --btn1: #212d40;
    /* Background Button 1 */
    --hovbtn1: #212d40;
    /* Background Hover Button 1 */
    --txtbtn1: #fff;
    /* Text Button 1 */
    --hovtxtbtn1: #fff;
    /* Text Hover Button 1 */
}


/* ==> Main Misc <== */
.menu-host .logo span::after {
    content: "Magic";
    font-size: 13px;
    text-decoration: underline;
    font-weight: bold;
}

.master-view,
.view,
.main {
    transition: 1s;
}


/* ==> Background <== */
body,
.sm-grid.k-grid .k-grid-content {
    background: var(--bck1);
}


/* ==> Text <== */
h1 {
    color: var(--txt1);
}

h2 {
    color: var(--alttxt1);
}


/* ==> Better Sidenav <== */
.collapsed-menu .popup-menu::after {
    border-color: var(--sidenav);
}

.collapsed-menu #faux-label,
.collapsed-menu .popup-menu h3 {
    color: var(--sidenav);
    background: var(--altsidenav);
}

.faux.popup-menu {
    background: var(--sidenav);
    border: none;
}

.collapsed-menu .popup-menu ul li a {
    color: var(--altsidenav);
}

.collapsed-menu .popup-menu ul li a:hover {
    color: var(--sidenav);
    background: var(--altsidenav);
}

.main-menu li a,
.menu-host .logo span,
.menu-footer > a > i,
.menu-footer a {
    color: var(--altsidenav);
}

.menu-host .logo svg {
    fill: var(--altsidenav);
}

.main-menu > li.active > a,
.main-menu > li > a:hover {
    background-color: var(--secondary-background);
    border-radius: 6px;
    color: var(--sidenav)
}

.menu-host {
    height: 100%;
    left: -64px;
    transition: height 1s linear 1s, width 1s linear 1s;
}

.menu-footer,
.appbar-host {
    background: var(--sidenav);
}

.appbar > div > a,
a.appbar-button {
    background: var(--altsidenav);
}

.appbar > div > a i,
.appbar-icon {
    color: var(--sidenav);
}

.collapsed-menu {
    min-width: 60px;
    height: 60%;
    transition: height 1s linear 1s, width 1s linear;
}

.collapsed-menu:hover {
    height: 100%;
}

#studiewijzer-detail-container * > div,
#studiewijzer-detail-container * > ul,
#studiewijzer-detail-container .content > div,
#studiewijzer-detail-container .content > ul {
    background-color: var(--innercontext);
    color: var(--altinnercontext);
}

/* ==> Button <== */
.dna-btn-primary,
.dialog .footer .primary-button,
.head-bar div > a.text-button {
    background: var(--btn1);
    color: var(--txtbtn1);
    border: none
}
.dna-btn-primary:hover,
.dialog .footer .primary-button:hover,
.head-bar div > a.text-button {
    background: var(--hovbtn1);
    color: var(--hovtxtbtn1);
    border: none
}


/* ==> Boxes <== */
.block .content,
aside div.content-container {
    background: var(--box1);
}

.asideTrigger.active,
.sources li.selected > a::after {
    border-color: var(--boxtxt1) !important;
}

.block h3,
.tabs li a,
.sources li.selected > a::after {
    color: var(--boxtxt1);
}

.block h3 {
    border: none;
    box-shadow: none;
}
.main {
    position: relative;
    float: left;
    padding: 88px 0 0 24px;
    width: 100%;
    height: 100%;
}
.block,
.endlink {
    background: var(--box1);
    border: none;
}


/* ==> FIX : Dropdown on page <== */
.master-view,
.view {
    width: 80%;
}

/* InnerBox */
.sm-grid.k-grid .k-grid-content tr.k-state-selected,
.sm-grid.k-grid .k-grid-content tr.k-state-selected.k-state-focused,
.sm-grid.k-grid .k-grid-content tr.k-state-selected .k-state-focused,
.sm-grid.k-grid .k-grid-content tr:hover,
.sm-grid.k-grid .k-grid-content tr,
.rest-column,
.widget li,
.k-dropdown-wrap.k-state-default,
.k-dropdown-wrap.k-state-default:hover {
    background: var(--innerbox) !important;
    color: var(--altinnerbox)
}

.sources li li {
    background: var(--box1);
}

.block,
.k-dropdown .k-input,
.k-dropdown .k-state-focused .k-input,
.k-menu .k-popup,
.projects li a,
.studiewijzer-onderdeel div.content ul > li .icon-link,
.studiewijzer-onderdeel div.content ul > li .icon-picture,
.studiewijzer-onderdeel div.content ul > li .icon-sound-on,
.studiewijzer-onderdeel div.content ul > li .icon-document,
.studiewijzer-onderdeel div.content ul > li .icon-movie,
.studiewijzer-onderdeel div.content ul > li .icon-presentation,
.studiewijzer-onderdeel div.content ul > li .icon-unknown {
    color: var(--altinnerbox);
}

.sm-grid.k-grid .k-grid-content .k-grouping-row .group-header-content {
    color: var(--boxtxt1);
}

fieldset label {
    color: var(--altinnerbox2);
}

#afsprakenlijst-container .today {
    text-decoration: underline;
}

#afsprakenlijst-container .today,
.sm-grid.k-grid .k-grid-content td,
.k-content,
.k-editable-area,
.k-panelbar > li.k-item,
.widget .dualcollumn-list li,
.k-panel > li.k-item,
.k-tiles {
    color: var(--altinnercontext);
    border-color: var(--innerborder);
}

.k-calendar td.range-select {
    background-color: var(--innerbox) !important;
    border-top: 1px solid var(--innerborder);
    border-bottom: 1px solid var(--innerborder);
}

form label,
.k-calendar .k-link,
.k-calendar .k-header .k-nav-fast,
.block .content p {
    color: var(--altinnercontext);
}

.block .content form,
.k-content,
.k-editable-area,
.k-panelbar > li.k-item,
.k-panel > li.k-item,
.k-tiles,
.k-calendar .k-header,
form .radio input[type="radio"] ~ label,
fieldset .radio input[type="radio"] ~ label,
form .radio input[type="radio"]:checked ~ label,
fieldset .radio input[type="radio"]:checked ~ label {
    background-color: var(--innercontext);
}

.widget li,
#vandaag-container .grade-widget ul,
.widget .agenda-list li a,
.sm-grid.k-grid .k-grid-content .k-grouping-row td .k-reset,
.column-container h3,
.comment.ng-binding.ng-scope,
.first-column {
    background: var(--innerbox);
}

#bronnen-container .bronnen-quota-label {
    background: var(--innercontext);
    color: var(--altinnercontext);
}

.sources li a:hover::after {
    color: var(--boxtxt1);
}

.column-container li.selected,
.column-container li.checked,
.column-container li:hover,
.widget .list li:hover,
.widget .list li.active,
.sources li.selected > a {
    background: var(--innercontext);
}

.alert a:hover {
    background: var(--hovinnerbox) !important;
}

.widget .list li a:hover {
    color: var(--althovinnerbox);
}

dl.list-dl {
    background-color: var(--innercontext);
    color: var(--altinnercontext);
}

.widget .list li a,
.endlink a,
.widget .dualcollumn-list li strong,
dl.list-dl dt,
.sources li a::after {
    color: var(--altinnerbox);
}

.sm-grid.k-grid .k-grid-content td,
dl.list-dl dd,
form .radio input[type="radio"]:checked ~ label::before,
fieldset .radio input[type="radio"]:checked ~ label::before,
.sm-grid.k-grid .k-grid-content td,
.block .content form,
.k-content,
.k-editable-area,
.k-panelbar > li.k-item,
.k-panel > li.k-item,
.k-tiles,
.k-calendar .k-header,
form .radio input[type="radio"] ~ label,
fieldset .radio input[type="radio"] ~ label,
form .radio input[type="radio"]:checked ~ label,
fieldset .radio input[type="radio"]:checked ~ label,
.block .content form,
.k-content,
.k-editable-area,
.k-panelbar > li.k-item,
.k-panel > li.k-item,
.k-tiles,
.k-calendar .k-header,
form .radio input[type="radio"] ~ label,
fieldset .radio input[type="radio"] ~ label,
form .radio input[type="radio"]:checked ~ label,
fieldset .radio input[type="radio"]:checked ~ label {
    color: var(--altinnercontext);
}


/* ==> Alerts <== */
.alert {
    border-left: 4px solid var(--alert);
}

.alert span.nrblock {
    background-color: var(--alert);
    color: var(--altalert);
}

.widget .agenda-list li.alert span.time::after {
    color: var(--alert) !important;
}

.widget .agenda-list li.alert span.time::before {
    color: var(--altalert);
    background: var(--alert);
    padding: 2px;
    border-radius: 5px;
}


/* ==> Misc <== */
.head-bar div > a > i {
    color: var(--box1);
    background: var(--boxtxt1);
}


/* ==> Center Login/Account <== */
.splash-container,
.top img,
.bottom,
.endlink {
    display: none;
}

.bottom,
.podium_container {
    width: 100%;
}
}
