css:
@-moz-document regexp("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/edit.html.*") {
/* ------------ Body ------------- */
body {
  background: #101010 !important;
  color: #CCC !important;
}

#header {
  background: #202020 !important;
  border-right: 1px solid #303030 !important;
}

#sections > div:not(:first-of-type) {
  border-color: #202020 !important;
}
/* --------- Help Popups --------- */

#help-popup {
  background: #303030 !important;
}

.title {
  background: #404040 !important;
}
/* --------- Input Bars ---------- */

#name,
input,
select {
  background: #303030 !important;
  border: 1px solid #404040 !important;
  color: #CCC !important;
}

.style-contributor {
  background: #202020 !important;
  border-color: #303030 !important;
}
/* ----------- Buttons ----------- */
  /* --------- Header ---------- */

#save-button,
#beautify,
#cancel-button,
#from-mozilla,
#to-mozilla {
  background: #303030 !important;
  border: 1px solid #404040 !important;
  color: #CCC !important;
}

#save-button:hover,
#beautify:hover,
#cancel-button:hover,
#from-mozilla:hover,
#to-mozilla:hover {
  background: #404040 !important;
  border-color: #505050 !important;
  color: #CCC !important;
}

#save-button:disabled {
  color: #999 !important;
}

#save-button:disabled:hover {
  background: #303030 !important;
  border-color: #404040 !important;
}
  /* ---------- Body ----------- */

.add-section,
.remove-section,
.beautify-section,
.remove-applies-to,
.add-applies-to {
  background: #202020 !important;
  border: 1px solid #303030 !important;
  color: #CCC !important;
}

.add-section:hover,
.remove-section:hover,
.beautify-section:hover,
.remove-applies-to:hover,
.add-applies-to:hover {
  background: #303030 !important;
  border-color: #404040 !important;
  color: #CCC !important;
}

}

@-moz-document regexp("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/manage.html*") {
/* ------------ Body ------------- */
body {
  background: #101010 !important;
  color: #CCC !important;
}

#header {
  background: #202020 !important;
  border-right: 1px solid #303030 !important;
}

a {
  color: #CCC !important;
}

a:hover {
  color: #888888 !important;
}

#installed::after {
  background: transparent !important;
}

[style-id] {
  border-color: #202020 !important;
}
/* --------- Input Bars ---------- */

#search {
  background: #303030 !important;
  border: 1px solid #404040 !important;
}
/* ----------- Buttons ----------- */
  /* --------- Header ---------- */

#check-all-updates,
#add-style-label,
#file-all-styles,
#unfile-all-styles, #apply-all-updates{
  background: #303030 !important;
  border: 1px solid #404040 !important;
  color: #CCC !important;
}

#check-all-updates:hover,
#add-style-label:hover,
#file-all-styles:hover,
#unfile-all-styles:hover, #apply-all-updates:hover {
  background: #404040 !important;
  border-color:  #505050 !important;
  color: #CCC !important;
}

/* ---------- Body ----------- */

.style-edit-link button,
.enable,
.disable,
.delete,
.check-update, .update {
  background: #202020 !important;
  border: 1px solid #303030 !important;
  color: #CCC !important;
}

.style-edit-link button:hover,
.enable:hover,
.disable:hover,
.delete:hover,
.check-update:hover, .update:hover {
  background: #303030 !important;
  border-color: #404040 !important;
  color: #CCC !important;
}
}

@-moz-document regexp("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/popup.html.*") {
body {
  background: #202020 !important;
}

.style-name,
#disableAll-label,
#write-style-for {
  color: #CCC !important;
}

a {
  color: #93a08e !important;
}

a:hover {
  color: #737b6c !important;
}

.entry,
#installed {
  border-color: #303030 !important;
  color: #999 !important;
}
}
