css:
@-moz-document domain("tid.ies21.edu.ar") {
*{ text-shadow: 0 0px 0;}
object{
    background:#000;
}
.content-primary,.content,.ui-content,.ui-page{
     background-color: #171717!important; 
     background: #171717!important;
     color: #eaeaea;
    /*background-color: #eaeaea;
    background: #eaeaea;
    color: #171717;*/
    font-family: 'Open Sans', sans-serif;
    font-size: 14pt;
    text-shadow: 0 0px 0;
	}
.paginatid .ui-content{
    width: 100%;
    display: inline-flex;
    background-color: #171717!important;
    background: #171717!important;
    padding: 1em 2em 0em 0em!important;
	}
.content-secondary{
    display: none;
    width: 0px;
    background-color: #171717!important;
    background: #171717!important;
}
.content-primary{
    width: 80%;
	/* padding: 0.5vw 3em 3em 1em!important;*/
    padding-left: 20%!important;
    padding-right: 3%!important;
    padding-top: 1%!important;
}
.bold{color:#fff;}
.titulohe, .ti, .b{color: #1abc9c;}
.titulo, .ti{color: #0099BC;}
.subtitulo, .st{color: #9b59b6;}

.li1{background: #171717!important;
	border-color: #00B7C3!important;}
.li1:hover{transition: background 0.2s;
           background: #204B4D!important;}
.li1 a{color:#00B7C3!important;}
.li1 a:hover{-webkit-transition:color .2s ease-in;color: #fff!important;}

#Stage,#Stage_fnd{
    width: 100%!important;
    /*height: 100%!important;*/
}

.ui-btn-up-b{background:#00B7C3}
.ui-btn-hover-b a{color:#00B7C3!important}
.ui-btn-hover-b{background:#ecf0f1}

.li2{background: #171717!important;}
.li2:hover{transition: background 0.2s;
           background: #204B4D!important;}
.li2 *{color: #ecf0f1!important;}
.ui-content{
    padding-left: 20%;
    padding-right: 20%;

}
.s{
    background-color: #E3008C;
    color: #eaeaea!important;
    text-shadow: 0px 0px 5px #000;
}
iframe{
    border: 0px;
    border-bottom: solid 1px #1abc9c;
}
.codigo{
    background-color: #333;
    color: white;
}
}
