css:
@-moz-document domain("technews.tw") {
/* 廣告區 */
#secondary .AD_wrapper[style="width: 300px; height: 600px; background-color: rgb(255, 255, 255); border: 2px solid rgb(211, 211, 211); text-align: center; margin: 40px 0px 25px;"],
#secondary .AD_wrapper[style="width: 300px; height: 600px; background-color: rgb(255, 255, 255); border: 2px solid rgb(211, 211, 211); text-align: center; margin: 40px 0px 25px; display: block;"]{
    display: none !important;
} /* Ad blocker 提示訊息 隱藏 */


hgroup > .logobar #ADTOP,
#secondary.widget-area > aside[id^="text-"]:nth-of-type(1),
#secondary.widget-area > aside[id^="text-"]:nth-of-type(3),
#secondary.widget-area .fb_iframe_widget,
.inside_AD iframe {
    filter: grayscale(1) brightness(0.3);
    transition: 0.2s;
}
hgroup > .logobar:hover #ADTOP,
#secondary.widget-area:hover > aside[id^="text-"]:nth-of-type(1),
#secondary.widget-area:hover > aside[id^="text-"]:nth-of-type(3),
#secondary.widget-area:hover .fb_iframe_widget,
.inside_AD iframe:hover {
    filter: grayscale(0) brightness(1);
}

/* 類廣告: 活動專區 */
.widget .tabs {
    background-color: transparent !important;
}
.widget .tabs li {
    color: #ccc !important;
}
.widget #tab-1.tab-content .tab_flexslider .c_block li {
    border-top: 0 !important;
    margin: 0 !important;
}
.widget #tab-1.tab-content .tab_flexslider .c_block li:first-child a {
    color: rgba(255, 255, 255, .7) !important;
}
.widget #tab-1.tab-content .tab_flexslider .c_block li a {
    color: rgba(255, 255, 255, .6) !important;
}
.widget #tab-1.tab-content .tab_flexslider .c_block li:first-child a:hover,
.widget #tab-1.tab-content .tab_flexslider .c_block li a:hover {
    color: rgba(255, 255, 255, 1) !important;
}
.widget .tab-content {
    border-right: 0 !important;
    border-left: 0 !important;
    border-bottom: 0 !important;
}

.widget-area > div[style="height: 404px; overflow:hidden;"],
.widget .tab_slider,
.widget .tab_slider .tab_flexslider,
.widget .tab_slider .flex-viewport {
    height: 414px !important;
}

.widget .tab_slider .tab_flexslider {
    background-color: #1E2124 !important;
}
.widget #tab-2.tab-content h3,
.widget #tab-3.tab-content h3 {
    color: rgba(255, 255, 255, .8)
}
.widget #tab-2.tab-content ul li.active_location {
    white-space: nowrap;
}
.widget #tab-2.tab-content a.active_btn {
    min-height: unset;
    padding: unset;
}

div#main.wrapper {
    background-color: transparent;
}

/* 上方漂浮選單 */
.main-navigation li ul li a {
    border-bottom: 0;
    background-color: rgba(0, 0, 0, .97);
    opacity: 1;
}
.main-navigation li ul li a:hover {
    background-color: rgba(169, 21, 21, .99);
    color: #fff;
}

/* 文章兩側漂浮箭頭按鈕 */
.divContainerDown {
    border: none;
    background-color: transparent;
    background-image: url('https://technews.tw/wp-content/plugins/jj-wp-easy-navigation/img/grid-dark.png');
    border-radius: 2px;
    box-shadow: 3px 3px 5px 3px #1E2124;
}

.jj-thumb-holder {
    border: 0;
    background: none;
}

.jj-title-holder {
    background: none;
}

#next-post-anchor > img,
#prev-post-anchor > img {
    filter: invert(1);
}

#next-post-anchor:active > img,
#prev-post-anchor:active > img {
    filter: none;
}

.jj-title-holder .jj-title-contents {
    font-size: 18px;
    width: 100%;
}

.jj-title-holder {
    width: 290px;
}

.divContainerDown {
    width: 400px;
}

.divContainerDown:hover {
    filter: brightness(1.05);
}

.divContainerDown:active {
    filter: brightness(0.85);
}

#jj-next-post .divContainerDown {
    left: -100px;
    top: 6px;
}

#jj-prev-post .divContainerDown {
    left: 20px;
    top: 6px;
}

/* */
.pagination a {
    border: 0;
    color: rgba(255, 255, 255, .7);
    border-radius: 2px;
}
.pagination a:hover {
    color: #fff
}
.pagination span.current {
    border: 0;
    border-radius: 2px;
    box-shadow: 0 0 0px 2px #EB8206;
    color: #EB8206;
}
div.topblack ul.screen li div.img div.sum_title,
div.topblack ul.screen li div.img div.sum_title h3 {
    color: rgba(255, 255, 255, .9);
}
div.topblack ul.screen li div.img:hover div.sum_title {
    color: #fff;
    border-bottom-color: #474A4F !important;
}
div.topblack ul.screen li.spotlist {
    border-bottom-color: #474A4F !important;
}
div.topblack a {
    color: rgba(255, 255, 255, 0.8);
    display: block;
}
div.topblack ul.screen li div.img div.sum_title {
    display: block;
    border-bottom: 1px solid #474A4F !important;
}
div.topblack a:hover,
div.topblack ul.screen li div.img div.sum_title:hover {
    color: #fff;
    text-decoration-color: #fff !important;
    text-decoration: underline;
}
div.content div.entry-content > div.indent > p,
.rightifo {
    color: rgba(255, 255, 255, 0.701961);
}
.single-post .entry-content .indent table * {
    font-size: 100% !important;
}
/* 內文表格 */
.single-post .entry-content .indent table {
    border-color: #000 !important;
    filter: invert(1) hue-rotate(180deg) contrast(0.7);
}
.single-post .entry-content .indent td {
    border-color: #000 !important;
}
.entry-content h3 {
    color: #34363C;
}
div.content div.entry-content {
    background-color: rgba(46, 48, 54, 0.298039);
    border: 1px solid rgba(46, 48, 54, 0.6);
    border-radius: 3px;
}
.entry-header span.head {
    /*display: none;*/
}
.entry-header span:not(.inside-feedly) a:not([rel="author"]) {
    display: inline-block;
    border-radius: 2px;
    background-color: #973A60;
    padding: 5px;
    color: #fff;
    visibility: visible;
    font-size: 10px !important;
}
.entry-header .indexsocial + span.head + span.body {
    visibility: hidden;
    font-size: 4px !important;
}
/* 標籤逗號間隔 */
div.content table tr td h1.entry-title a {
    color: #fff;
}
.moreinf a:hover {
    text-decoration: underline;
}

/* 背景底色 */
body.custom-background {
    background-color: #36393E;
}
.widget-area .widget {
    background-color: transparent;
}
div#primary {
    background-color: #36393e;
}
div #secondary {
    background-color: #2F3136;
}

/**/
body {
    color: #BABABA;
}
a {
    color: #0096cf;
}
a:hover {
    color: #fff;
}
body.single .entry-header {
    /*background-color: #2F3136;*/
}
body.single #primary > #content > article > .content > header + div {
    height: 10px;
}

body.single #primary > #content > article > .content > header + div > img {
    display: none;
}
body.home .site-content {
    margin: 4px 0 0;
}
body.single .site-content {
    margin: -2px 0 0;
    background-color: transparent !important;
}
body.single .site-content header.entry-header table {
    background-color: #2F3136;
    border-bottom: 10px solid #2F3136;
}
/* 文章頁::標題底 */
div.pop-inside ul li:hover,
div.pop-inside ul li:active,
div.pop-inside ul li:focus {
    background-color: transparent;
}
.widget-area .widget ul {}
.widget-area .widget li {
    vertical-align: middle;
    display: table;
}
.widget-area .widget li a {
    display: table-cell;
    min-height: 65px;
    color: rgba(255, 255, 255, .4);
}
.widget-area .widget li a:hover {
    background-color: #36393F;
    text-decoration: none;
    color: rgba(255, 255, 255, .8);
}
.widget-area .widget #pop-widget-viewed-2 a:hover {
    box-shadow: 0 0 0px 10px #36393F;
}
.widget-area .widget li {
    margin-bottom: 0.3rem;
}
.widget-area #lastest-posts a {
    padding: 5px 7px;
    display: table;
}
.widget-area #lastest-posts a > span:first-child {
    display: table-cell;
    width: 40px;
    ;
}
.widget-area #lastest-posts a > span:last-child {
    display: table-cell;
}
span.tech_spec_column {
    color: #AF75FF;
}
/* [專欄] */
.widget-area .widget a > img {
    filter: grayscale(.6);
}
.widget-area .widget a:hover > img {
    filter: grayscale(.0);
}
img.wp-post-image {
    padding-bottom: 0;
}
.rps_wrapper {
    background-color: #2F3136;
}
.rps_item a {
    border-radius: 5px;
    color: rgba(255, 255, 255, .7) !important;
    height: 90%;
    padding: 10px;
}
.rps_item a:hover {
    color: rgba(255, 255, 255, .9) !important;
    background-color: #36393F;
}
.rps_item a:active {
    color: #fff !important;
    background-color: #42464D;
}
.rps_item a img.rps_thumb {
    border: none;
    filter: brightness(0.8);
    transition: 0.1s;
}
.rps_item a:hover img.rps_thumb {
    filter: brightness(1);
}
.rps_item a:active img.rps_thumb {
    filter: brightness(1) saturate(4);
}
.widget-area .widget ul.disc {
    list-style-type: none;
    margin-left: 0px;
}
.site-content article {
    border-top: 1px solid rgba(255, 255, 255, 0.0392157);
    margin: 20px 0;
}
.site-content article .maintitle {
    margin-top: 10px;
    display: block;
}
div.content table tr td h1.entry-title a:hover {
    color: #fff;
    text-decoration: underline;
}
div.topblack ul.screen li.block2014 {
    background-color: #2F3136;
    box-shadow: 0 0 0px 10px #2F3136; /**/
    max-width: 224px;
}

div.topblack ul.screen {
    padding-left: 10px; /**/
}

body.home .section-head {
    padding-top: 30px;
}
.widget-area {
    border-left: none;
    /*outline: 10px solid #2F3136;*/
    box-shadow: 0 0 0px 10px #2F3136;
    position: relative;
    left: 20px;
}
body.page-template-default .widget-area,
body.archive .widget-area,
body.home .widget-area {
    padding: 0 10px;
}
#secondary div.textwidget > div[style] {
    visibility: hidden;
}
.widget-area .widget h3 {
    color: #ccc !important;
}

/*
ul.pop-widget-tabs li a::after {
    font-size: 50%;
    content: "(點此另開新頁)";
}*/
ul.pop-widget-tabs li a:hover,
ul.pop-widget-tabs li a.active,
ul.pop-widget-tabs li a:active,
ul.pop-widget-tabs li a:focus {
    /*color: #0096CF;*/
    color: #ccc;
    /* note: 本週熱門 這連結根本就沒意義。*/
}
ul.pop-widget-tabs,
.widget-area .widget h3 {
    border-bottom: 4px solid #ccc;
}
div.tipbar {
    /*background-image: none;*/
    filter: invert(1) hue-rotate(180deg) saturate(2.5);
}
.tipbar .search > input {
    background: url("https://img.technews.tw/wp-content/themes/twentytwelve/images/icon-search.png") no-repeat scroll 7px 8px #999999 !important;
}
.site-header {
    background-color: #1E2124;
}
.logobar > a > img {
    filter: blur(2px) brightness(2);
    position: relative;
    left: -10px;
    width: auto;
    height: auto;
}
hgroup > .logobar {
    background-image: url('https://technews.tw/wp-content/themes/twentytwelve/images/logo.gif');
    background-repeat: no-repeat;
}
footer[role="contentinfo"] {
    background-color: #1E2124;
}
footer[role="contentinfo"] > div:nth-child(2) {
    background-color: #1E2124 !important;
}
footer #site-generator > p > span:nth-of-type(3)::before {
    content: "暗色主題《黑科技新報》由「好冷酒XD領域™」所有人 hollen9 提供。 |　"
}

/* Facebook 留言區 */
div#fbSEOComments {
    background-image: none;
}
div#fbSEOComments::before {
    font-weight: bolder;
    content: "讀者留言";
    font-size: 18px;
    background-color: #4966B6;
    padding: 5px 20px;
    color: #fff;
    position: relative;
    bottom: 4px;
}
div#fbSEOComments > div.fb_iframe_widget > span {
    border: 4px solid #4966B6;
    border-radius: 0 2px 2px 2px;
    background-color: #F8F8F8;
    box-sizing: border-box;
}

/* ROUTE: /enterprise-portal */
.enterprise_block {
    border: 0;
    background-color: #2F3136;
}
.enterprise_block .company_name {
    color: #ccc;
}
.enterprise_block .company_intro {
    color: #999;
}

/* ROUTE: /authors */
.author-info {
    background-color: rgba(46, 48, 54, .3);
    border: 1px solid rgba(46, 48, 54, .6);
    ;
    border-radius: 2px;
}
}

@-moz-document url("https://www.facebook.com/plugins/comments.php?api_key=439518036097315&amp;channel_url=https%3A%2F%2Fstaticxx.facebook.com%2Fconnect%2Fxd_arbiter%2Fr%2F0sTQzbapM8j.js%3Fversion%3D42%23cb%3Df1966e6d7e80bfa%26domain%3Dtechnews.tw%26origin%3Dhttps%253A%252F%252Ftechnews.tw%252Ffc3e551287ad66%26relation%3Dparent.parent&amp;colorscheme=light&amp;href=https%3A%2F%2Ftechnews.tw%2F2017%2F09%2F12%2Fiphone-x-out-of-stock%2F&amp;locale=zh_TW&amp;numposts=10&amp;sdk=joey&amp;skin=light&amp;version=v2.0&amp;width=100%25"), regexp(".*facebook\\.com\\/plugins\\/comments\\.php\\?api_key=439518036097315.*") {
body {
    color: yellow !important;
}
}
