css:
@-moz-document domain("fi.nobelium.xyz") {
.navbar-light.bg-faded {
	background: #ffffff;
    border: none;
    box-shadow: 0 1px 2px rgba(0,0,0,0.3);
    color: #666666!important;
    font-family: system-ui;
}

.navbar.navbar-inverse {
	background: #292525;
    border: none;
    box-shadow: 0 1px 2px rgba(0,0,0,0.3);
    color: white!important;
    font-family: system-ui;
}

.footer.container {
	display: none;
}

.container.home-cards {
	display: none;
}

.btn.btn-success {
	background: #4caf50;
}

.btn {
	text-transform: uppercase;
    border: none;
    box-shadow: 1px 1px 4px rgba(0,0,0,0.4);
    transition: all 0.4s;
    position: relative;
}

.btn:after {
    content: '';
	background-image: radial-gradient(circle, #000 10%, transparent 10.01%);
    background-repeat: no-repeat;
    background-size: 1000% 1000%;
    background-position: 50%;
    transition: background .5s, opacity 1s;
}

.btn:hover {
	box-shadow: 1px 1px 4px rgba(0,0,0,0.4);
}

.nav.navbar-nav.my-2.my-lg-0 li {
	text-transform: uppercase;
    border: none;
    box-shadow: 1px 1px 4px rgba(0,0,0,0.4);
    transition: all 0.4s;
    position: relative;
    background: #4caf50;
    border-radius: 3px;
    margin-right: 6px!important;
    color: white!important;
}

.my-2.my-lg-0 a.nav-link.dropdown-toggle {
	color: white!important
}

input.form-control {
	box-shadow: inset 0 -1px 0 #ddd;
    border-radius: 0px;
    border-left: none;
    border-right: none;
    border-top: none;
    border-bottom: 1px;
    padding-left: 0;
    font-size: 16px;
    font-weight: 400;
    color: #666666;
    transition: ease-in 0.04s;
    background: transparent;
}

input.form-control:focus {
	border-bottom: 2px solid rgb(33, 150, 243);
    background: transparent;
}

textarea.form-control:focus {
	border-bottom: 2px solid rgb(33, 150, 243);
    background: transparent;
}

textarea.form-control {
	box-shadow: inset 0 -1px 0 #ddd;
    background: transparent;
    border-radius: 0px;
    border-left: none;
    border-right: none;
    border-top: none;
    border-bottom: 1px;
    padding-left: 0;
    font-size: 16px;
    font-weight: 400;
    color: #666666;
    transition: ease-in 0.04s;
}

.btn.btn-outline-primary {
	background-color: white!important;
}

.close {
	display: none!important;
}

.alert {
	border: none;
    width: auto;
    height: 45px;
    background-color: black;
}

.nav.navbar-nav.my-2.my-lg-0 ul {
}

.nav.navbar-nav.my-2.my-lg-0 li:hover {
	box-shadow: 1px 1px 4px rgba(0,0,0,0.4);
    background-color: rgb(67, 154, 70);
}

body {
	background: #f5f5f5;
}

.card-header {
	background-color: #2196f3;
    font-family: system-ui;
    color: white;
    text-align: center;
}

.btn.btn-success:hover {
	background-color: rgb(67, 154, 70);
}

.card {
	box-shadow: 0 1px 4px rgba(0,0,0,0.3);
    border: none;
    border-radius: 2px;
}

.jumbotron-home img {
	display: none;
}

.jumbotron-home p {
	display: none;
}

.nav-link {
	color: #666666!important;
}

.nav-link:hover {
	color: rgb(33, 33, 33)!important;
}

.navbar-light.bg-faded.container {
	padding-left: 15px!important;
    padding-right: 15px!important;
    margin: 0;
    width: 100%;
    height: 100%;
    color: white;
}

.navbar-brand {
	font-size: 0px;
    word-spacing: 0px;
}

.navbar-brand img {
	content: url("https://web.archive.org/web/20161112160912im_/https://xdiscuss.net/html/img/logo.png");
    width: 150px;
    height: 23px;
    vertical-align: middle;
}

@keyframes janky {
    0% {
		background: url(https://web.archive.org/web/20161112160912im_/https://xdiscuss.net/html/img/frontback.jpg);
    	width: 100%;
    	background-repeat: no-repeat;
   		background-position: center center;
   		background-size: cover;
    } 50% {
        background: url(https://web.archive.org/web/20161112160912im_/https://xdiscuss.net/html/img/frontback.jpg);
    	width: 100%;
    	background-repeat: no-repeat;
   		background-position: center center;
   		background-size: cover;
    } 100% {
      	background: url(https://web.archive.org/web/20161112160912im_/https://xdiscuss.net/html/img/frontback.jpg);
    	width: 100%;
    	background-repeat: no-repeat;
   		background-position: center center;
   		background-size: cover;
    }
}

.container.main-content {
	display: none;
}

.footer {
	display: none;
}

.alert {
	display: none;
}
}
