css:
@-moz-document domain("boards.4chan.org") {
body .reply, body .preview, body .reply:target, body.reply.highlight {
    background-color: #111;
    border: 1px solid black;
    box-shadow: 2px 2px 6px black;
    border-radius: 4px;
    padding: 4px!important;
}
body a, body a:visited { color: #436d7d; }
body a:hover { color: #7ba9bb!important; }
body .reply:target, body .reply.highlight {
    background: #2a2a2a!important;
    border: 1px solid #222!important;
}

body #boardNavDesktop, body #boardNavDesktopFoot { color: #2c7b5f; }

body #boardNavDesktop a, body #boardNavDesktop a:visited, body #boardNavDesktopFoot a, body #boardNavDesktopFoot a:visited { color: #6fa78d; }

body #boardNavDesktop a:hover, body #boardNavDesktopFoot a:hover { color: #9bfdd9!important; }

body .boardBanner { color: #65a2a0; }

body hr { border-top: 1px solid #1b1b1f; }

body .preview { border: 1px solid black!important; }

body div.post div.postInfo span.postNum a { color: #666; }

body s:hover, body s:focus, body s:hover a { color: #bbb!important; }

body .board a.postMenuBtn { color: #436d7d; }
a.replylink:not(:hover), div#absbot a:not(:hover) { color: #436d7d!important; }
body a.replylink:not(:hover), body div#absbot, body div.backlink a.quotelink, body div.backlink a.quoteLink { color: #436d7d!important; }

body div.post div.postInfo span.postNum a.replylink:hover, body div.backlink a.quotelink:hover, body div.backlink a.quoteLink:hover { color: #7ba9bb!important; }

body div.post div.postInfo span.postNum a:hover, body .posteruid .hand:hover { color: #d0d0d0!important; }

body blockquote>span.quote { color: #6a9e55; }

body div.post div.postInfo span.nameBlock span.name, body div.sideArrows { color: #406150; }

body .quoteLink, body .quotelink { color: #396b71!important; }

body input[type=text], body input[type=password], body table.postForm>tbody textarea, body #recaptcha_response_field {
    background: #222;
    color: #aaa;
    border: 1px solid black;
    font-family: roboto,arial,helvetica,sans-serif;
}

body div.post div.postInfo span.subject, div.post div.postInfoM span.subject { color: #928438; }

body .globalMessage { color: #775d98; }

body .dd-menu ul {
    background-color: #111;
    border: 1px solid black;
    box-shadow: 2px 2px 6px black;
}

body .dd-menu li { border-bottom: 1px solid black; }

body .dd-menu li:hover { background-color: #1a1a1a; }

body #quote-preview.reveal-spoilers s { background-color: black!important; }

body .deadlink { color: #a25656!important; }

body span.ts-replies { color: #bb8b57; }

body span.ts-images { color: #599a76; }

body span.ts-ips { color: #4d76b5; }

body span.ts-page { color: #af6daf; }

body .prettyprint { background-color: black; }

body .prettyprint .pln { color: #bbb; }

body div.pagelist {
    background: #111;
    border-right: 1px solid black;
    border-bottom: 1px solid black;
    color: #303a48;
}

body div.pagelist div.cataloglink { border-left: 1px solid black; }

body div.next input, body div.prev input {
    border: 1px solid black;
    border-radius: 3px;
    padding: 3px 8px;
    background: #222;
    color: #aaa;
}

body .dateTime, body .fileText { color: #888; }

body input[type="checkbox"] {
    background-color: #292929!important;
    -webkit-appearance: none;
    border: 1px solid black;
    box-shadow: 1px 1px 6px black;
    border-radius: 2px;
    padding: 5px;
    color: #ddd;
    position: relative;
    outline: none;
}

body input[type="checkbox"]:checked {
    background-color: black!important;
    border-color: black!important;
}

body input[type="checkbox"]:checked:after {
    content: '';
    width: 8px;
    border-radius: 2px;
    height: 8px;
    box-shadow: 0 0 6px 1px #00ff00;
    background: #95ff95;
    position: absolute;
    top: 1px;
    left: 1px;
}

body a.button, body span.button {
    border: 1px solid black;
    border-radius: 3px;
    background: #222;
    color: #aaa!important;
}

.panelHeader {
    border-bottom: 1px solid #1b1b1f!important;
}

body .tCollapsed .extButton.expbtn, body .post-hidden .extButton.threadHideButton { content: url(https://i.imgur.com/e2p7T6h.png); }
body .extButton.threadHideButton, body .tExpanded .extButton.expbtn { content: url(https://i.imgur.com/eCJTCF5.png); }

body a.button:hover, body span.button:hover {
    border: 1px solid #111;
    background: #333;
    color: #aaa!important;
}

::-webkit-scrollbar {
    background-color: #080808!important;
    width: 10px;
}
::-webkit-scrollbar-thumb:hover { background-color: #222; }
::-webkit-scrollbar-corner { background: #080808; }
::-webkit-scrollbar-thumb { background-color: #161616; }

.navLinks.desktop {
    color: #224450;
}

table.postForm>tbody>tr>td:first-child {
    color: #aaa;
    background-color: #182729;
    border-color: black!important;
}

body #settingsMenu label {
    color: #83cea9;
}

body .settings-tip {
    color: #4d735b;
}

input[type=text]:focus, input[type=password]:focus, input:not([type]):focus, textarea:focus {
    border: 1px solid #6baf8f!important;
}

#bannerCnt {
    border: 1px solid black!important;
    box-shadow: 1px 1px 6px 2px black!important;
}

@media only screen and (max-width: 480px) {
    body div.replyContainer { background-color: transparent; }
    body div.opContainer {
		background-color: #111;
    	border: 1px solid black!important;
    }
    body div.post div.postInfoM {
        border-bottom: 1px solid black;
    	background-color: #222;
    }
    body div.post div.postInfoM span.nameBlock span.name, body div.postInfo span.nameBlock span.name { color: #406150; }
    body div.post div.postInfoM span.dateTime, body div.post div.postInfoM span.dateTime a { color: #666!important; }
    body .backlink.mobile, body .postLink.mobile {
    	background-color: #1a1a1a;
    	border-top: 1px solid black;
    }
    body div.board>hr { border-top: 1px solid #1b1b1f; }
    body div.postLink span.info { color: #b38350; }
    body a, body .useremail:not(:hover) .name:not(.capcode), body .useremail:not(:hover) .postertrip:not(.capcode) {
    	color: #436d7d!important;
	}
    body a:hover, body .useremail:hover .name:not(.capcode), body .useremail:hover .postertrip:not(.capcode) {
    	color: #436d7d!important;
	}
}
}

@-moz-document domain("www.4chan.org") {
.top-box, #announce {
    background: #111;
    color: inherit;
    border-color: black;
    box-shadow: 2px 2px 6px black;
}

.boxbar {
    background: #222;
    color: #6bb36b;
}

#announce .boxbar {
    background: #222;
    color: #62a5b1;
}
body a, body a:visited { color: #436d7d!important; }
body a:hover { color: #7ba9bb!important; }
body #fp-menu li:hover { background: #333; }
body #fp-menu {
    background: #222;
    border: 1px solid black;
    box-shadow: 2px 2px 6px black;
}
body #ft ul { border-top: 1px solid black; }
body #ft li.current, body #ft li.fill {
    background: transparent;
    color: black;
}
#ft li.first { border-left: 1px solid black; }
#ft li {
    background: #111;
    border-color: black;
}
body .c-thread img { border: 1px solid black; }
.boxcontent { color: #bbb; }

.left-box {
    background: #111311;
    color: #326732;
}

.left-box .boxbar {
    background: #326732;
    color: #9c6;
}

.right-box {
    background: #111113;
    color: #282852;
}

.right-box .boxbar { background: #282852; }

hr { color: #282852; background: #282852; }

.left-box a, .left-box a:visited { color: #487748!important; }
.left-box a:hover { color: #5e9a5e!important; }

.boxcontent dd { border-color:#282852; }
dt, dt.first { color: #7da6e6; }
}

