css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://learn2.acseducation.edu.au/course"), url-prefix("https://learn2.acseducation.edu.au/mod/"), url-prefix("https://learn2.acseducation.edu.au/mod/forum/"), url-prefix("https://learn2.acseducation.edu.au/message/"), url-prefix("https://learn2.acseducation.edu.au/mod/book/"), url-prefix("https://learn2.acseducation.edu.au/mod/assign/"), url-prefix("https://learn2.acseducation.edu.au/user/"), url-prefix("https://learn2.acseducation.edu.au/report/"), url-prefix("https://learn2.acseducation.edu.au/blog/"), url-prefix("https://learn2.acseducation.edu.au/notes/"), url-prefix("https://learn2.acseducation.edu.au/grade/") {
.col-md-3 {
    width: 20%;
}
.col-md-9 {
    width: 95% !important;
}
.col-md-9 .right.side {
    position: absolute;
    right: 200px;
}
#page-header {
    height: 0px;
    overflow: hidden;
}
#page-content-wrapper {
    width: 100%;
    padding: 0;
    margin-top: 0%;
    padding-left: 10px;
}
.col-md-9 {
    width: 85%;
}
/* main content of home page */
.course-content {
    margin-top: 25px !important;
}
/*The main sidebar containing teacher's pic etc */

/* set all blocks within sidebar up with transparent backgrounds first */
#block-region-side-pre .block {
    background-color: transparent;
}
#block-region-side-pre {
    position: fixed;
    top: 22px;
    left: 4px;
    height: 21px !important;
    width: 20px;
    color: white;
    overflow: hidden !important;
    background: #17227d;
    /* Old browsers */
    background: linear-gradient(0deg, #17227d 0%, #0154a2 100%);
    z-index: 10;
    margin: 0px !important;
    padding: 0px !important;
    box-shadow: -3px 3px 10px 0px rgba(0, 0, 0, 0.3);
    border-radius: 5px;
    border: 2px rgb(1, 84, 162) solid!important;
}
#block-region-side-pre:hover {
    height: auto !important;
    width: 330px;
    overflow: visible;
    box-shadow: -3px 3px 10px 0px rgba(0, 0, 0, 0.3);
    border: thin rgb(1, 84, 162) solid;
    padding-top: 44px;
    background: linear-gradient(135deg, #17227d 0%, #0154a2 100%);
}
.block_tree.list p,
.block_tree.list {
    margin-top: 1px;
    padding-top: 0px;
}
/* "Course Administration" on home page.  Becomes different things on other pages e.g. Forum Admin on Forum page*/
#settingsnav {
    position: fixed;
    top: 22px;
    left: 24px;
    height: 21px;
    line-height: 16px;
    width: 180px;
    white-space: nowrap;
    text-align: right;
    overflow: hidden;
    color: white;
    border: thin gray solid;
    background: #17227d;
    /* Old browsers */
    background: linear-gradient(0deg, #17227d 0%, #0154a2 100%);
    margin: 0px !important;
    padding: 0px !important;
    padding-right: 20px !important;
    box-shadow: -3px 3px 10px 0px rgba(0, 0, 0, 0.3);
    border-radius: 5px;
    z-index: 12;
}
#settingsnav:hover {
    height: auto !important;
    overflow: scroll !important;
    box-shadow: -3px 3px 10px 0px rgba(0, 0, 0, 0.3);
    width: 250px;
    background: linear-gradient(135deg, #17227d 0%, #0154a2 100%);
    z-index: 24;
}
#settingsnav .tree_item.branch:hover {
    text-decoration: none;
    background: #AB0D29;
    /* Old browsers */
    background: linear-gradient(135deg, #AB0D29 0%, #D62833 100%);
}
/* Home - my course, the site, the current course (participants, badges, general, weeks) */

/* Also, it's parent, the Navigation block (so that can see other courses etc)   */
.block_navigation.block {
    height: 0px;
}
/* .block_navigation.block ul li ul li:nth-child(3) ul,  */
.block_navigation.block ul:nth-child(1) {
    position: fixed;
    left: 88px;
    top: 43px;
    height: 22px !important;
    line-height: 19px !important;
    width: 116px;
    overflow: hidden !important;
    background: #17227d;
    /* Old browsers */
    background: linear-gradient(0deg, #17227d 0%, #0154a2 100%);
    margin: 0px !important;
    padding: 0px !important;
    padding-left: 6px!important;
    box-shadow: -3px 3px 10px 0px rgba(0, 0, 0, 0.3);
    border-radius: 5px;
    border: thin gray solid;
    z-index: 13!important;
}
/*.block_navigation.block ul li ul li:nth-child(3) ul:hover,*/
.block_navigation.block ul:nth-child(1):hover {
    height: auto !important;
    max-height: 600px !important;
    min-width: 400px !important;
    max-width: 800px !important;
    overflow: scroll !important;
    box-shadow: -3px 3px 10px 0px rgba(0, 0, 0, 0.3);
    background: linear-gradient(135deg, #17227d 0%, #0154a2 100%);
    border: none;
    width: 250px;
    z-index: 12!important;
}
/* messages waiting from students */
#block-region-side-pre .block_messages.block {
    position: fixed;
    top: 43px;
    left: 4px;
    height: auto;
    width: 200px;
    color: white;
    background: linear-gradient(0deg, #17227d 0%, #0154a2 100%);
    margin: 0px !important;
    padding: 0px !important;
    box-shadow: -3px 3px 10px 0px rgba(0, 0, 0, 0.3);
    border: thin gray solid;
    border-radius: 5px;
    z-index: 12;
}
#block-region-side-pre .block_messages.block:hover {
    background: linear-gradient(135deg, #17227d 0%, #0154a2 100%);
}
#block-region-side-pre .block_messages.block .info {
    margin-left: -28px;
    visibility: hidden;
}
/*change the footer into the heaader - the linkedword "Messages" */
.block_messages.block {
    width: 100%;
}
.block_messages.block > div {
    padding-left: 6px!important;
    padding-right: 4px!important;
}
.block_messages.block .footer {
    position: absolute;
    top: 0px;
    padding-left: 1px;
    width: 100%;
}
.block_messages.block .footer:hover {
    background-color: rgb(214, 40, 51)!important;
}
/*rules for when notifications are present */
.block_messages.block .content .listentry {
    position: relative;
    top: 4px;
}
.block_messages.block div.content li.listentry div.user {
    position: relative;
    left: 0px;
    top: 4px;
    width: 156px;
}
/* THis addresses the first student (when there is more than 1 message waiting) and I think is necessary because I placed the foooter ("Meessages") at the top of the box aas a header. It's the student's name */
.block_messages.block > div:nth-child(2) > ul:nth-child(1) > li:nth-child(1) > div:nth-child(1) {
    top: 20px;
}
.block_messages.block div.content ul.list {
    margin: 0px!important;
}
.block_messages.block .content .listentry .user a img {
    display: none;
}
.block_messages.block .content .listentry div.message a {
    display: inline-block;
    position: relative;
    top: -16px;
    width: 100%;
    text-decoration: none;
    background: linear-gradient(135deg, #D62833 0%, #CA0D29 100%) !important;
    border-radius: 4px;
    border: thin white solid;
    text-align: center;
    font-weight: bold;
    padding-right: 2px;
    height: 19px;
    line-height: 14px;
}
/* THis addresses the first student (when there is more than 1 message waiting) and I think is necessary because I placed the foooter ("Meessages") at the top of the box aas a header. It's the red pic saing 1 or more messages waiting */
.block_messages.block > div:nth-child(2) > ul:nth-child(1) > li:nth-child(1) > div:nth-child(2) a {
    top: -2px!important;
}
/*for when there is no message from a student */
.block_messages.block .content .info {
    position: relative;
    left: 14px;
    top: 16px!important;
}
/* the overlay at th bottom right corner saying hteir is a count of messages waiting */
#newmessageoverlay {
    border: 2px rgb(214, 40, 51) solid;
    box-shadow: -3px -3px 10px 0px rgba(0, 0, 0, 0.3);
    width: auto;
    right: 25px;
    bottom: 25px;
}
/* I think these are unnecessary. They create unused space in the sidebar */
.block_activity_results.block {
    display: none!important;
}
/*The profile pic, and teacher details */
.myprofileitem {
    text-align: center!important;
    font-size: 13px!important;
    margin: 0px!important;
    padding: 0px!important;
}
.myprofileitem.picture {
    margin-left: auto!important;
    margin-right: auto!important;
    margin-top: 4px!important;
    margin-bottom: 4px!important;
}
.myprofileitem.picture img {
    border: 2px solid rgb(1, 84, 162)!important;
    -webkit-transition: -webkit-transform 10s ease-in-out;
    -ms-transition: -ms-transform 0s ease-in-out;
    transition: transform 10s ease-in-out;
}
.myprofileitem.picture img:hover {
    border: 2px solid rgb(214, 40, 51)!important;
    transform: rotate(7200deg);
    -ms-transform: rotate(7200deg);
    -webkit-transform: rotate(7200deg);
}
.myprofileitem.country,
.myprofileitem.city {
    display: none;
}
.myprofileitem.email {
    padding-bottom: 4px!important;
}
/*The My Dashboard and Calendar links in the teacher block */
.block_controlpanel {
    border-bottom: 10px rgba(256, 256, 256, 0) solid!important;
}
.block_controlpanel img {
    display: block;
    position: relative;
    left: -270px;
    top: 1px;
    height: 18px!important;
    width: 18px!important;
    text-align: right;
}
.block_controlpanel.block.no-header a {
    width: 100%!important;
    margin: 0px!important;
    padding: 0px!important;
}
.block_controlpanel.block.no-header a:hover {
    background: rgb(214, 40, 51)!important;
}
.panelinner h3 {
    display: inline-block;
    width: 100%;
    height: 0!important;
}
.block_controlpanel.block.no-header .panelinner {
    margin: 0px!important;
    padding: 0px!important;
    padding-left: 62px!important;
    background-color: transparent!important;
    width: 100%!important;
}
/*trying to get rid of the faint grey shadow from edge of each entry e.g. calendar, but only partially succeeding */
a.col-md-3 * {
    border: none!important;
    box-shadow: none!important;
    margin: 0px!important;
    padding: 0px!important;
    background: none!important;
}
.block_controlpanel.block.no-header .panelinner h3 {
    font-size: 14px!important;
    font-weight: normal!important;
    text-transform: none!important;
    margin: 0px!important;
    padding: 0px!important;
    width: 100%!important;
}
.block_controlpanel.block.no-header {
    margin: 0px!important;
    padding: 0px!important;
    background-color: transparent!important;
}
/* The Participants, Mahara, Assignment/Chats/etc links in the teacher's box*/

/*line under group of headings in teacahers' block */
.col-md-3 .block_activity_modules.block.list_block {
    border-bottom: thin rgba(256, 256, 256, .6) solid!important;
}
.col-md-3 .block_activity_modules.block.list_block .content {
    padding-left: 0px;
    padding-right: 0px;
    margin-left: 0px;
    width: 100%;
}
.col-md-3 .block_participants.block.list_block ul,
.col-md-3 .block_mnet_hosts.block.list_block ul {
    margin-top: 0px!important;
    margin-bottom: 0px!important;
}
.col-md-3 .block_activity_modules.block.list_block ul {
    width: 100%;
    padding-left: 0px;
    margin-left: 0px;
}
.col-md-3 .block_activity_modules.block.list_block li {
    display: inline-block;
    width: 100%;
}
.col-md-3 .block_activity_modules.block.list_block a {
    padding-left: 36px;
}
.col-md-3 .block_activity_modules.block.list_block div {
    width: 100%;
}
/* The Mahara link */

/*hide the blcok in the teachers' blcok*/
div[data-block="mnet_hosts"] ul {
    height: 0px;
}
/*move it up to the top of the page */
a[title="ACS Mahara"]:before {
    content: "Mah";
}
a[title="ACS Mahara"] {
    display: block;
    width: 60px;
    height: 22px;
    position: fixed;
    top: 43px;
    left: 143px;
    overflow: hidden;
    color: white;
    text-decoration: none;
    background: linear-gradient(0deg, #17227d 0%, #0154a2 100%);
    margin: 0px !important;
    padding: 0px !important;
    padding-left: 8px !important;
    box-shadow: -3px 3px 10px 0px rgba(0, 0, 0, 0.3);
    border: thin gray solid;
    border-radius: 5px;
    z-index: 16;
}
a[title="ACS Mahara"] img {
    margin: 0px;
    padding: 0px!important;
    padding-left: 4px!important;
    position: relative;
    top: -2px;
    height: 14px;
    width: 18px;
}
a[title="ACS Mahara"]:hover {
    text-decoration: none;
    background: linear-gradient(135deg, #D62833 0%, #CA0D29 100%)!important;
    text-align: center;
    width: 200px
}
a[title="ACS Mahara"]:hover::before {
    content: "Jump out to ACS Mahara";
}
/* The "Add a block " section that pops up in the teacher's block when editing is turned on, so taht the teacher's block can be edited */
div[data-block="adminblock"] {
    display: block;
}
/*resize the containers for each online user in prep for the  mouse over background fills horiontal space of box */
.block_online_users.block ul {
    padding-left: 0px;
    margin-left: 0px;
}
.block_online_users.block li {
    width: 100%;
    height: 16px;
    line-height: 16px;
    padding-left: 24px;
}
.col-md-3 .block_participants.block.list_block:hover,
.col-md-3 .block_mnet_hosts.block.list_block:hover,
.col-md-3 .block_activity_modules.block.list_block li div:hover,
.block_tree.list ul li:hover,
p.tree_item.branch:hover,
.block_online_users.block li:hover {
    background: rgb(214, 40, 51)!important;
}
.col-md-3 .block_mnet_hosts.block.list_block {
    margin-left: -4px;
    ;
}
h2 {
    font-size: 20px;
    margin-bottom: 0px;
}
h3 {
    font-size: 18px;
    margin-bottom: 0px;
}
h3 {
    font-size: 16px;
    margin-bottom: 0px;
}
.block_controlpanel.block.no-header .row {
    background: transparent!important;
}
.block_controlpanel.block.no-header .unlist {
    background: none!important;
    background-color: transparent!important;
}
.block_controlpanel.block.no-header .r0 {
    background: none!important;
    background-color: transparent!important;
}
#block-region-side-pre .content {
    padding-top: 0px !important;
    padding-bottom: 0px !important;
    background: none!important;
    background-color: transparent!important;
}
#block-region-side-pre .content a:hover {
    text-decoration: none;
}
#block-region-side-pre .my-profile-picture {
    padding: 0px !important;
    margin: 0px !important;
    float: left;
    width: 10px;
}
#block-region-side-pre div.header {
    display: none !important;
}
/*Breadcrumbs showing curent navigation depth along top of page */
div.breadcrumb {
    position: fixed;
    top: 1px;
    left: 2px;
    margin: 0px !important;
    padding: 0px !important;
    z-index: 50!important;
    height: 20px;
    overflow: hidden;
    width: auto!important;
}
ol.breadcrumb {
    margin: 0px !important;
    padding: 0px!important;
    display: block;
}
.breadcrumb a {
    text-decoration: none;
    color: white;
}
.breadcrumb a:hover {
    background: #AB0D29;
    /* Old browsers */
    background: linear-gradient(135deg, #AB0D29 0%, #D62833 100%);
}
ol.breadcrumb > li {
    display: block;
    float: left;
    width: auto;
    max-width: 140px;
    white-space: nowrap;
    overflow: hidden;
    background: #17227d;
    /* Old browsers */
    background: linear-gradient(0deg, #17227d 0%, #0154a2 100%);
    box-shadow: -3px 3px 10px 0px rgba(0, 0, 0, 0.3);
    border-radius: 5px;
    text-align: center!important;
    padding: 0px 8px 0px 8px;
    margin: 0px 0px 0px 0px;
    line-height: 20px;
}
/*professional year program crumb and 
    professional year environments course crumb*/
ol.breadcrumb > li:nth-child(2),
ol.breadcrumb > li:nth-child(3) {
    max-width: 120px;
}
ol.breadcrumb > li:hover {
    max-width: none;
}
/* dumping the breadcrumb slashes and replacing with paadding and border radius */
ol.breadcrumb > li a,
ol.breadcrumb > li span {
    width: 100%;
    display: inline-block;
    text-align: center!important;
}
ol.breadcrumb > li span {
    color: rgb(200, 200, 255);
}
ol.breadcrumb > li::before {
    display: none;
}
/* Week numbers and Jump to current week page number jumps */
aside div:nth-child(6) {
    position: fixed;
    top: 22px;
    left: 205px;
    width: 202px;
    background: transparent !important;
    z-index: 10;
    margin: 0px !important;
    padding: 0 !important;
}
aside div:nth-child(6) div {
    margin: 0px !important;
    padding: 0px !important;
    padding-left: 1px !important;
    padding-right: 1px !important;
}
aside div:nth-child(6) ol {
    margin: 0px !important;
    padding: 0px !important;
}
aside div:nth-child(6) ol li {
    background: #17227d;
    /* Old browsers */
    background: linear-gradient(0deg, #17227d 0%, #0154a2 100%);
    display: block;
    float: left;
    text-align: center;
    color: white;
    width: 20px;
    border: thin gray solid;
    text-decoration: none;
    border-radius: 5px;
}
/* drop the page 11 crumb to the next line */
.inline-list > li:nth-child(11) {
    clear: both;
}
aside div:nth-child(6) ol li a {
    text-decoration: none;
    display: inline-block;
    width: 100%
}
aside div:nth-child(6) ol li:hover {
    background: #AB0D29;
    /* Old browsers */
    background: linear-gradient(135deg, #AB0D29 0%, #D62833 100%);
}
aside div:nth-child(6) .header {
    height: 0px;
}
/*Jump to current week */
aside div:nth-child(6) .content > a:before {
    content: "Current ";
}
aside div:nth-child(6) .content > a {
    position: relative;
    left: 0px;
    top: 0px;
    border: thin gray solid;
    background: #17227d;
    /* Old browsers */
    background: linear-gradient(0deg, #17227d 0%, #0154a2 100%);
    display: inline-block;
    width: 60px;
    height: 22px;
    line-height: 18px;
    text-decoration: none;
    border-radius: 5px;
    text-align: left!important;
    ;
    margin: 0px!important;
    padding: 0px!important;
    padding-left: 4px!important;
    overflow: hidden;
}
aside div:nth-child(6) .content > a:hover:before {
    content: none;
}
aside div:nth-child(6) .content > a:hover {
    background: #AB0D29;
    /* Old browsers */
    background: linear-gradient(135deg, #AB0D29 0%, #D62833 100%);
    width: 162px!important;
    z-index: 53!important;
    top: -21px;
    left: 0px;
}
aside div:nth-child(6) .content {
    background: transparent !important;
}
aside div:nth-child(6) .header div {
    display: none;
}
/* login button and edit button to the to pright corner */
.pull-right button {
    position: fixed !important;
    top: 2px;
    right: 14px !important;
    width: 62px !important;
    z-index: 10;
    border: 1px solid rgb(23, 34, 155)!important;
    ;
    margin: 0px !important;
    padding: 0px !important;
    height: 20px;
    line-height: 16px;
}
.pull-right button:hover {
    background-color: rgb(23, 34, 125);
    color: white;
}
/* turn editing on/off button */
div.navbutton input {
    position: fixed;
    top: 2px;
    right: 70px;
    z-index: 10;
    background: rgba(230, 230, 255, 1)!important;
    ;
    padding: 0px 2px!important;
    border: 2px solid rgb(23, 34, 125)!important;
    color: rgb(23, 34, 125) !important;
    height: 20px;
    line-height: 16px;
    width: 110px;
}
div.navbutton input:hover {
    background-color: rgb(23, 34, 125)!important;
    color: white!important;
}
/* The little progress word withthe help button */
#completionprogressid {
    position: absolute;
    right: -20px;
    bottom: -34px;
    z-index: 0;
    color: white;
}
/* Heading for each week */
li.section.main {
    border-top: 10px solid rgba(0, 0, 150, .0);
}
.sectionname {
    border-top: 3px solid rgba(0, 100, 0, .3)!important;
    border-radius: 3px;
}
.section.main.clearfix > div:nth-child(4) > div:nth-child(2) > div:nth-child(1) > p:nth-child(1) *,
.summary > div > p > span > span,
.summary > div > .summary > p > span > span,
.summary > div > .summary > p > span,
.summary > p > span > span,
.summary > div > .summary > p > span > span,
#yui_3_17_2_1_1483427782317_557 > p:nth-child(1) > span:nth-child(1) > span:nth-child(1) {
    color: green!important;
    font-family: Trebuchet MS, Verdana, Arial, Helvetica, sans-serif!important;
    font-size: 18px!important;
    line-height: 18px;
    width: 100%!important;
    font-weight: bold;
}
.summary h2 {
    font-weight: bold;
    line-height: 24px;
}
/*headings within each week particularly "What to do this week */
.summary div p strong,
.summary div div p strong,
.summary p b,
.summary div p b {
    color: black!important;
    font-size: 18px!important;
}
/* make imags smaller */
.summary img {
    height: 70px;
    width: 100px;
    margin: 0px!important;
}
.summary > div p {
    text-align: left !important;
}
.sectionname {
    padding: 0px !important;
    margin: 0px !important;
    font-weight: bold;
}
.summary > div > p > span {
    font-size: 18px !important;
    font-weight: bold;
}
.summary > div > span {
    font-size: 18px !important;
    font-weight: bold;
}
/* bunch up student resources and sub-headings */
.section.img-text .contentwithoutlink h4,
.activityinstance,
.activity.resource div,
.section li.activity,
.activity.label {
    margin-bottom: 0px!important;
    padding-bottom: 0px!important;
    line-height: 14px;
    min-height: auto;
}
.activity.journal,
.activity.resource,
.activity.forum,
.activity.book,
.activity.chat,
.activity.url,
.activity.quiz .activityinstance,
.activity.assign .activityinstance {
    height: 20px;
    line-height: 20px;
    min-height: auto;
}
/* HAve to make exception to previous rules for academic misconduct */
#module-74616 > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) > div:nth-child(1) {
    height: 50px;
