css:
@-moz-document domain("www.liaoxuefeng.com") {
/*
* Please use it in Chrome extension Stylish.
* It applies to the site: www.liaoxuefeng.com.
*/


html,
body {
    background-color: #f3eacb;
}

/* 导航栏取消固定，太占位置 */
#header {
    position: relative;
}

/* 目录栏 */
div.x-sidebar-left {
    position: absolute;
    left: -170px;
    border: 1px solid #0593d3;
    border-radius: 0 15px 15px 0;
    width: 200px;
    background-color: #f3eacb;
}

div.x-sidebar-left:hover {
    left: 0px;
    border: 1px solid #fdf6e3;
    box-shadow: 0 9px 30px -6px rgba(0, 0, 0, .2), 0 18px 20px -10px rgba(0, 0, 0, .04), 0 18px 20px -10px rgba(0, 0, 0, .04), 0 10px 20px -10px rgba(0, 0, 0, .04);
    background-color: #fdf6e3;
}

.uk-nav-side>li>a {
    margin: 0;
    padding: 0 5px;
    font-size: 14px;
}

/* 文章主体：居中便于浏览 */
.x-center {
    margin: 0 auto;
    box-shadow: 0 9px 30px -6px rgba(0, 0, 0, .2), 0 18px 20px -10px rgba(0, 0, 0, .04), 0 18px 20px -10px rgba(0, 0, 0, .04), 0 10px 20px -10px rgba(0, 0, 0, .04);
    padding: 10px;
    width: 900px;
}

/* 润色，护眼，防亮瞎 */
#main {
    color: #704214;
}

pre,
code {
    background-color: #fdf6e3;
}

textarea {
    background: #fdf6d3!important;
}

/* 隐藏广告、评论、推广等 */
#x-comment-list,
#footer,
.x-display-if-not-signin,
.x-anchor,
.x-sidebar-left-bottom,
div.x-wiki-content + h3,
div.x-wiki-content + h3 + p + h3,
div.x-anchor + h3,
ul#x-comment-list + h3,
a[href="/webpage/donate"],
a[onclick="shareToWeibo()"],
div#x-sponsor-a,
div#x-sponsor-b {
    display: none;
}

/* 前后篇文章 */
#x-wiki-prev-next {
    position: fixed;
    right: 30px;
    bottom: 20px;
    width: 200px;
}

/* 文章标题 */
div.x-content > h4 {
    font-size: 25px;
}

/* 返回顶部 */
div.x-goto-top {
    background-color: rgba(255, 255, 255, 0);
}

div.x-goto-top:hover {
    background-color: #fdf6e3;
}
}
